                    >
                      <item.icon className="h-5 w-5 flex-shrink-0" />
                      <span className="ml-0 flex-1">{item.label}</span>
                      <ChevronDown className={`h-4 w-4 transition-transform ${arcadeExpanded ? "rotate-180" : ""}`} />
                    </Button>
                    {arcadeExpanded && (
                      <div className="ml-8 mt-1 space-y-1">
                        {item.submenuItems?.map((subItem) => (
                          <Button
                            key={subItem.id}
                            variant="ghost"
                            className={`flex items-center gap-3 px-3 py-2 rounded-md transition-colors font-mono text-sm w-full text-left justify-start ${activePage === subItem.id
                              ? "text-[#00daa2] bg-gray-800"
                              : "text-gray-400 hover:text-[#00daa2] hover:bg-gray-800"
                              }`}
                            onClick={() => {
                              setActivePage(subItem.id);
                              toggleMobileMenu();
                            }}
                          >
                            <span>{subItem.label}</span>
                          </Button>
                        ))}
                      </div>
                    )}
                  </div>
                ) : (
                  <Button
                    key={item.label}
                    variant="ghost"
                    className={`flex items-center gap-3 px-3 py-3 rounded-md transition-colors font-mono font-semibold w-full text-left justify-start ${activePage === item.id
                      ? "text-[#00daa2] bg-gray-800"
                      : "text-[#00daa2] hover:bg-gray-800"
                      }`}
                    onClick={() => {
                      if (item.externalUrl) {
                        handleExternalLink(item.externalUrl);
                      } else {
                        setActivePage(item.id);
                      }
                      toggleMobileMenu();
                    }}
                  >
                    <item.icon className="h-5 w-5 flex-shrink-0" />
                    <span className="ml-0">{item.label}</span>
                  </Button>
                ))}

              {/* Separator */}
              <div className="border-t border-gray-700 my-2"></div>

              {/* Social Navigation Items (White) */}
              {navigationItems
                .filter((item) => !item.isGreen)
                .map((item) => (
                  <Button
                    key={item.label}
                    variant="ghost"
                    className="flex items-center gap-3 px-3 py-3 rounded-md transition-colors font-mono font-semibold w-full text-left justify-start text-gray-300 hover:text-[#00daa2] hover:bg-gray-800"
                    onClick={() => {
                      if (item.externalUrl) {
                        handleExternalLink(item.externalUrl);
                      } else {
                        setActivePage(item.id);
                      }
                      toggleMobileMenu();
                    }}
                  >
                    <item.icon className="h-5 w-5 flex-shrink-0" />
                    <span className="ml-0">{item.label}</span>
                  </Button>
                ))}
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col min-w-0 max-w-full w-full">
          {/* Header */}
          <header className="flex items-center justify-between p-5 border-b border-[#8351a1] bg-gray-950 w-full max-w-full">
            <div className="flex items-center gap-4">
              {/* Mobile Logo */}
              <div className="md:hidden">
                <Image
                  src="/Cars/DRVNWHITE.png"
                  alt="DRVN VHCLS"
                  width={60}
                  height={40}
                  className="transition-all duration-300 w-auto h-auto"
                />
              </div>
            </div>

            <div className="flex bg-none items-center gap-2 md:gap-4 px-2 py-1 rounded-md">
              <div className="hidden md:flex text-[#8351a1] text-xs md:text-sm font-mono">
                XP / coming soon
              </div>

              {/* Mobile Menu Button */}
              <Button
                variant="ghost"
                size="icon"
                className="text-white border border-[#8351a1] md:hidden"
                onClick={toggleMobileMenu}
              >
                <Menu className="h-5 w-7" />
              </Button>
            </div>
          </header>

          {/* Main Content */}
          <main className="p-4 md:p-6 space-y-6 md:space-y-8 flex-1 overflow-y-auto overflow-x-hidden mobile-scroll w-full max-w-full min-w-0">
            {renderPageContent()}
          </main>
        </div>
      </div>

      {/* Modals */}
      {/* Auto Signup Modal - Shows when wallet connects but user doesn't exist */}
      <AutoSignupModal
        isOpen={shouldShowSignup && showAuthModal}
        onClose={() => setShowAuthModal(false)}
        onSuccess={handleSignupSuccess}
      />

      {/* Manual Auth Choice Modal - Shows when user manually clicks auth */}
      <AuthChoiceModal
        isOpen={showAuthModal && !shouldShowSignup}
        onClose={() => setShowAuthModal(false)}
        onSuccess={handleSigninSuccess}
      />

      {/* Marketplace Detail Modal - Buyer-focused with specs */}
      <MarketplaceDetailModal
        isOpen={showMarketplaceModal}
        onClose={() => {
          setShowMarketplaceModal(false);
          setSelectedMarketplaceItem(null);
        }}
        item={selectedMarketplaceItem}
      />

      {/* Global Floating Post Button - Hidden on Settings page */}
      {activePage !== "settings" && (
        <CreatePostButton onClick={() => setShowCreatePostModal(true)} />
      )}

      {/* Global Create Post Modal */}
      <CreatePostModal
        isOpen={showCreatePostModal}
        onClose={() => setShowCreatePostModal(false)}
        userId={currentUser?._id || "anonymous"}
        registeredVehicles={mockRegisteredVehicles}
        currentUser={{
          name: currentUser ? `${currentUser.firstName} ${currentUser.lastName}` : "DRVN User",
          username: currentUser?.username || "drvn_user",
          avatar: currentUser?.profileImage || "https://github.com/shadcn.png",
        }}
        sponsors={availableSponsors}
        onPostCreated={handlePostCreated}
        connectedPlatforms={platformConnections.filter(p => p.connected).map(p => p.platform)}
        initialCrossPostSettings={crossPostSettings}
      />
    </div>
  );
}

