"use client";

import { Avatar, AvatarFallback, AvatarImage } from "@/app/components/ui/avatar";
import { Button } from "@/app/components/ui/button";
import { Card, CardContent } from "@/app/components/ui/card";
import {
    FaTwitter,
    FaInstagram,
    FaFacebook,
    FaYoutube,
    FaTiktok,
    FaLinkedin
} from "react-icons/fa";
import { SiFarcaster } from "react-icons/si";
import { Edit, UserPlus, UserMinus, ExternalLink } from "lucide-react";
import { useState } from "react";

interface SocialLinks {
    farcaster?: string;
    base?: string;
    x?: string;
    instagram?: string;
    facebook?: string;
    youtube?: string;
    tiktok?: string;
    linkedin?: string;
}

interface VehicleToken {
    ticker: string;
    address?: string;
    vehicleName?: string;
}

interface UserProfileProps {
    user: {
        _id: string;
        username: string;
        displayName?: string;
        bio?: string;
        profileImage?: string;
        followerCount?: number;
        followingCount?: number;
        socialLinks?: SocialLinks;
        walletAddress: string;
        farcasterHandle?: string;
        baseHandle?: string;
    };
    isOwner: boolean;
    isFollowing?: boolean;
    vehicleTokens?: VehicleToken[];
    onFollow?: () => void;
    onUnfollow?: () => void;
    onEditProfile?: () => void;
    onTokenClick?: (token: VehicleToken) => void;
}

function parseLinksInBio(bio: string): React.ReactNode[] {
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const parts = bio.split(urlRegex);

    return parts.map((part, index) => {
        if (urlRegex.test(part)) {
            urlRegex.lastIndex = 0;
            const displayUrl = part.replace(/^https?:\/\//, '').replace(/\/$/, '');
            return (
                <a
                    key={index}
                    href={part}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-[#00daa2] hover:text-[#00b894] hover:underline inline-flex items-center gap-0.5"
                >
                    {displayUrl.length > 30 ? displayUrl.substring(0, 30) + '...' : displayUrl}
                    <ExternalLink className="w-3 h-3" />
                </a>
            );
        }
        return <span key={index}>{part}</span>;
    });
}

