Frontend/hooks/useMiniKitNavigation.ts.content:
"use client";

import {
  useOpenUrl,
  useComposeCast,
  useViewProfile,
} from "@coinbase/onchainkit/minikit";

export function useMiniKitNavigation() {
  const openUrl = useOpenUrl();
  const composeCastHook = useComposeCast();
  const viewProfile = useViewProfile();

  const handleExternalLink = (url: string) => {
    if (openUrl) {
      openUrl(url);
    } else {
      // Fallback for non-MiniKit environments
      window.open(url, "_blank");
    }
  };

  const handleShare = (text: string, url?: string) => {
    try {
      // Check if composeCastHook is available and has a mutate function
      if (
        composeCastHook &&
        "mutate" in composeCastHook &&
        typeof composeCastHook.mutate === "function"
      ) {
        composeCastHook.mutate({
          text,
          embeds: url ? [url] : [window.location.href],
        });
      } else {
        // Fallback for non-MiniKit environments
        if (navigator.share) {
          navigator.share({
            title: "DRVN/VHCLS",
            text,
            url: url || window.location.href,
          });
        } else {
          // Copy to clipboard fallback
          navigator.clipboard.writeText(url || window.location.href);
        }
      }
    } catch (error) {
      console.error("Error sharing:", error);
      // Fallback to clipboard
      navigator.clipboard.writeText(url || window.location.href);
    }
  };

  const handleViewProfile = (fid: number) => {
    if (viewProfile) {
      viewProfile(fid);
    } else {
      // Fallback for non-MiniKit environments
      window.open(`https://warpcast.com/${fid}`, "_blank");
    }
  };

  return {
    handleExternalLink,
    handleShare,
    handleViewProfile,
    openUrl,
    composeCast: composeCastHook,
    viewProfile,
  };
}

Frontend/app/components/vehicle/Swipeabl.content:
"use client";

import { useState } from "react";
import Image from "next/image";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { Button } from "@/app/components/ui/button";
import { cn } from "@/lib/utils";

interface SwipeableGalleryProps {
    images: { url: string; isNftImage: boolean }[];
}

export function SwipeableGallery({ images }: SwipeableGalleryProps) {
    const [currentIndex, setCurrentIndex] = useState(0);
    const [touchStartX, setTouchStartX] = useState<number | null>(null);
    const [touchEndX, setTouchEndX] = useState<number | null>(null);

    const nextSlide = () => {
        setCurrentIndex((prev) => (prev + 1) % images.length);
    };

    const prevSlide = () => {
        setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
    };

    const handleTouchStart = (e: React.TouchEvent) => {
        setTouchStartX(e.touches[0].clientX);
        setTouchEndX(null);
    };

    const handleTouchMove = (e: React.TouchEvent) => {
        setTouchEndX(e.touches[0].clientX);
    };

    const handleTouchEnd = () => {
        if (touchStartX === null || touchEndX === null) return;
        const distance = touchStartX - touchEndX;
        if (distance > 50) {
            nextSlide();
        } else if (distance < -50) {
            prevSlide();
        }
        setTouchStartX(null);
        setTouchEndX(null);
    };

    if (!images || images.length === 0) {
        return (
            <div className="w-full aspect-[2/1] bg-zinc-900 rounded-lg flex items-center justify-center text-zinc-600">
                No Images Available
            </div>
        );
    }

    return (
        <div 
            className="relative w-full aspect-[2/1] group rounded-xl overflow-hidden border border-white/10 bg-black"
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
        >
            {/* Main Image */}
            <div className="relative w-full h-full">
                <Image
                    src={images[currentIndex].url}
                    alt={`Vehicle Image ${currentIndex + 1}`}
                    fill
                    className="object-cover"
                    priority={currentIndex === 0}
                />

                {/* NFT Badge */}
                {images[currentIndex].isNftImage && (
                    <div className="absolute top-4 right-4 bg-primary/90 text-black text-xs font-bold px-2 py-1 rounded shadow-lg">
                        NFT Image
                    </div>
                )}
            </div>

            {/* Navigation Buttons */}
            {images.length > 1 && (
                <>
                    <Button
                        variant="ghost"
                        size="icon"
                        className="absolute left-2 top-1/2 -translate-y-1/2 bg-black/50 hover:bg-black/70 text-white rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
                        onClick={prevSlide}
                    >
                        <ChevronLeft className="w-6 h-6" />
                    </Button>
                    <Button
                        variant="ghost"
                        size="icon"
                        className="absolute right-2 top-1/2 -translate-y-1/2 bg-black/50 hover:bg-black/70 text-white rounded-full opacity-0 group-hover:opacity-100 transition-opacity"
                        onClick={nextSlide}
                    >
                        <ChevronRight className="w-6 h-6" />
                    </Button>

                    {/* Dots */}
                    <div className="absolute bottom-4 left-1/2 -translate-x-1/2 flex gap-2">
                        {images.map((_, idx) => (
                            <button
                                key={idx}
                                className={cn(
                                    "w-2 h-2 rounded-full transition-all",
                                    idx === currentIndex ? "bg-white w-4" : "bg-white/50 hover:bg-white/80"
                                )}
                                onClick={() => setCurrentIndex(idx)}
                            />
                        ))}
                    </div>
                </>
            )}
        </div>
    );
}

Hardhat/contracts/VehicleRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

interface IERC6551Registry {
    function createAccount(
        address implementation,
        bytes32 salt,
        uint256 chainId,
        address tokenContract,
        uint256 tokenId
    ) external returns (address account);

    function account(
        address implementation,
        bytes32 salt,
        uint256 chainId,
        address tokenContract,
        uint256 tokenId
    ) external view returns (address account);
}

contract VehicleRegistry is ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    IERC20 public usdcToken;
    address public treasury;
    uint256 public registrationFee = 50 * 10**6; // 50 USDC (assuming 6 decimals)

    // ERC6551 Config
    address public registry6551;
    address public implementation6551;

    mapping(bytes32 => bool) public vinHashes; // Prevent duplicate VINs
    mapping(uint256 => address) public vehicleTBA; // Token ID -> TBA Address

    event VehicleRegistered(uint256 indexed tokenId, address indexed owner, string vinHash, address tba);
    event RegistrationFeeUpdated(uint256 newFee);
    event TreasuryUpdated(address newTreasury);

    constructor(
        address _usdcToken,
        address _treasury,
        address _registry6551,
        address _implementation6551
    ) ERC721("DRVN Registered Vehicle", "DRVN-VHCL") {
        usdcToken = IERC20(_usdcToken);
        treasury = _treasury;
        registry6551 = _registry6551;
        implementation6551 = _implementation6551;
    }

    function registerVehicle(
        address to,
        string memory tokenURI,
        string memory vin
    ) external returns (uint256) {
        bytes32 vinHash = keccak256(abi.encodePacked(vin));
        require(!vinHashes[vinHash], "Vehicle already registered");

        // Collect Registration Fee
        require(usdcToken.transferFrom(msg.sender, treasury, registrationFee), "Fee transfer failed");

        _tokenIds.increment();
        uint256 newItemId = _tokenIds.current();

        _mint(to, newItemId);
        _setTokenURI(newItemId, tokenURI);

        vinHashes[vinHash] = true;

        // Create Token Bound Account (TBA)
        address tba = IERC6551Registry(registry6551).createAccount(
            implementation6551,
            bytes32(0), // salt
            block.chainid,
            address(this),
            newItemId
        );

        vehicleTBA[newItemId] = tba;

        emit VehicleRegistered(newItemId, to, vin, tba);

        return newItemId;
    }

    function setRegistrationFee(uint256 _fee) external onlyOwner {
        registrationFee = _fee;
        emit RegistrationFeeUpdated(_fee);
    }

    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "Invalid address");
        treasury = _treasury;
        emit TreasuryUpdated(_treasury);
    }

    function getVehicleTBA(uint256 tokenId) external view returns (address) {
        return vehicleTBA[tokenId];
    }
}

