import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

// Minimal interface for the BSTR vault used by this mint contract
interface IBSTRVault {
    function token() external view returns (address);
    function isMinter(address who) external view returns (bool);
    function distribute(address to, uint256 amount) external;
}

/**
 * @title DRVNTitanium (TOKEN_ID = 2)
 * @notice ERC1155 single-ID collection with USDC paid mint, optional BSTR rewards (vault / wallet / contract-held),
 *         royalties (ERC2981), pause, airdrop, metadata freeze, and frontend helpers.
 */
contract DRVNTitanium is ERC1155, ERC1155Supply, ERC1155Burnable, ERC2981, Ownable, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // â€”â€”â€” Token ID (fixed) â€”â€”â€”
    uint256 public constant TOKEN_ID = 2;

    // â€”â€”â€” Collection metadata (UX helpers; ERC1155 has no standard name/symbol) â€”â€”â€”
    string public name;
    string public symbol;

    // â€”â€”â€” Mint config â€”â€”â€”
    uint256 public immutable MAX_SUPPLY; // cap for ID 2
    uint256 public mintPrice; // USDC price per unit (USDC typically 6 decimals)
    bool public saleActive; // public sale switch

    address public fundsReceiver; // USDC proceeds receiver
    IERC20 public immutable USDC; // payment token

    // â€”â€”â€” BSTR reward config â€”â€”â€”
    IERC20 public BSTR; // reward token (expects 9 decimals)
    address public rewardVault; // 0 => contract-push; non-0 => vault/wallet address
    bool public rewardVaultIsContract; // true => call IBSTRVault.distribute
    uint256 public rewardPerUnit; // BSTR per NFT unit (smallest units)
    bool public rewardEnabled; // master switch
    bool public rewardOnAirdrop; // also reward airdrops/ownerMint
    bool public rewardRevertIfInsufficient; // if true, revert when insufficient BSTR

    // â€”â€”â€” URIs â€”â€”â€”
    string private _baseURI; // e.g. ipfs://CID/
    string private _contractURI; // collection-level metadata (OpenSea)
    bool public metadataFrozen; // freeze flag for URIs

    // â€”â€”â€” Events â€”â€”â€”
    event SaleStateSet(bool active);
    event MintPriceSet(uint256 price);
    event FundsReceiverSet(address indexed receiver);
    event BaseURISet(string uri);
    event ContractURISet(string uri);
    event MetadataFrozen();

    event BSTRSet(address indexed token);
    event RewardPerUnitSet(uint256 amount);
    event RewardVaultSet(address indexed vault, bool isContract);
    event RewardFlagsSet(bool enabled, bool onAirdrop, bool strict);
    event RewardSent(address indexed to, uint256 amount);
    event RewardSkipped(address indexed to, uint256 required, uint256 available);

    constructor(
        string memory _name,
        string memory _symbol,
        string memory baseURI_,
        string memory contractURI_,
        address initialOwner,
        address usdc_,
        address _fundsReceiver,
        uint96 defaultRoyaltyBps, // e.g. 500 = 5.00%
        uint256 maxSupply_,
        uint256 mintPrice_
    ) ERC1155("") {
        require(initialOwner != address(0), "Owner=0");
        require(usdc_ != address(0), "USDC=0");
        require(_fundsReceiver != address(0), "Receiver=0");
        require(maxSupply_ > 0, "maxSupply=0");

        name = _name;
        symbol = _symbol;
        _baseURI = baseURI_;
        _contractURI = contractURI_;
        USDC = IERC20(usdc_);
        fundsReceiver = _fundsReceiver;
        MAX_SUPPLY = maxSupply_;
        mintPrice = mintPrice_;

        _transferOwnership(initialOwner);
        _setDefaultRoyalty(_fundsReceiver, defaultRoyaltyBps);
    }

    // â€”â€”â€” Modifiers â€”â€”â€”
    modifier onlyEOA() {
        require(msg.sender == tx.origin, "No contracts");
        _;
    }

    // â€”â€”â€” Admin â€”â€”â€”
    function setSaleActive(bool active) external onlyOwner nonReentrant {
        saleActive = active;
        emit SaleStateSet(active);
    }
    function pause() external onlyOwner nonReentrant {
        _pause();
    }
    function unpause() external onlyOwner nonReentrant {
        _unpause();
    }

    function setMintPrice(uint256 price) external onlyOwner nonReentrant {
        mintPrice = price;
        emit MintPriceSet(price);
    }
    function setFundsReceiver(address receiver) external onlyOwner nonReentrant {
        require(receiver != address(0), "Receiver=0");
        fundsReceiver = receiver;
        emit FundsReceiverSet(receiver);
    }

    function setDefaultRoyalty(address receiver, uint96 feeNumerator) external onlyOwner nonReentrant {
        _setDefaultRoyalty(receiver, feeNumerator);
    }
    function deleteDefaultRoyalty() external onlyOwner nonReentrant {
        _deleteDefaultRoyalty();
    }

    function setBaseURI(string calldata newBase) external onlyOwner nonReentrant {
        require(!metadataFrozen, "Frozen");
        _baseURI = newBase;
        emit BaseURISet(newBase);
    }
    function setContractURI(string calldata newContractURI) external onlyOwner nonReentrant {
        require(!metadataFrozen, "Frozen");
        _contractURI = newContractURI;
        emit ContractURISet(newContractURI);
    }
    function freezeMetadata() external onlyOwner nonReentrant {
        metadataFrozen = true;
        emit MetadataFrozen();
    }

    // â€”â€”â€” BSTR setup â€”â€”â€”
    function setBSTR(address token) external onlyOwner nonReentrant {
        require(token != address(0), "BSTR=0");
        // Validate 9 decimals if the token supports IERC20Metadata
        try IERC20Metadata(token).decimals() returns (uint8 dec) {
            require(dec == 9, "BSTR decimals!=9");
        } catch {}
        BSTR = IERC20(token);
        emit BSTRSet(token);
    }

    function setRewardPerUnit(uint256 amount) external onlyOwner nonReentrant {
        require(amount % 1e9 == 0, "not multiple of 1e9"); // enforce whole BSTR units
        rewardPerUnit = amount;
        emit RewardPerUnitSet(amount);
    }

    function setRewardVault(address vault, bool isContractVault) external onlyOwner nonReentrant {
        rewardVault = vault;
        rewardVaultIsContract = isContractVault;
        if (isContractVault && vault != address(0)) {
            // Sanity checks so the wrong vault can't be linked
            require(IBSTRVault(vault).token() == address(BSTR), "vault token mismatch");
            require(IBSTRVault(vault).isMinter(address(this)), "vault not authorizing this minter");
        }
        emit RewardVaultSet(vault, isContractVault);
    }

    function setRewardFlags(bool enabled, bool onAirdrop, bool strict) external onlyOwner nonReentrant {
        rewardEnabled = enabled;
        rewardOnAirdrop = onAirdrop;
        rewardRevertIfInsufficient = strict;
        emit RewardFlagsSet(enabled, onAirdrop, strict);
    }

    // â€”â€”â€” Public mint (USDC) â€”â€”â€”
    function mint(uint256 amount) external nonReentrant whenNotPaused onlyEOA {
        require(saleActive, "Sale off");
        require(amount > 0, "Amount=0");
        require(totalSupply(TOKEN_ID) + amount <= MAX_SUPPLY, "Exceeds cap");

        uint256 cost = mintPrice * amount;
        USDC.safeTransferFrom(msg.sender, fundsReceiver, cost); // user must approve beforehand

        _mint(msg.sender, TOKEN_ID, amount, "");

        if (rewardEnabled && rewardPerUnit > 0) {
            _sendReward(msg.sender, rewardPerUnit * amount);
        }
    }

    // â€”â€”â€” Owner distributions â€”â€”â€”
    function airdrop(address[] calldata recipients, uint256[] calldata amounts) external onlyOwner nonReentrant {
        require(recipients.length == amounts.length, "Len mismatch");
        uint256 total;
        for (uint256 i = 0; i < amounts.length; i++) total += amounts[i];
        require(total > 0, "No tokens");
        require(totalSupply(TOKEN_ID) + total <= MAX_SUPPLY, "Exceeds cap");

        for (uint256 i = 0; i < recipients.length; i++) {
            _mint(recipients[i], TOKEN_ID, amounts[i], "");
            if (rewardEnabled && rewardOnAirdrop && rewardPerUnit > 0) {
                _sendReward(recipients[i], rewardPerUnit * amounts[i]);
            }
        }
    }

    function ownerMint(address to, uint256 amount) external onlyOwner nonReentrant {
        require(amount > 0, "Amount=0");
        require(totalSupply(TOKEN_ID) + amount <= MAX_SUPPLY, "Exceeds cap");
        _mint(to, TOKEN_ID, amount, "");
        if (rewardEnabled && rewardOnAirdrop && rewardPerUnit > 0) {
            _sendReward(to, rewardPerUnit * amount);
        }
    }

    // â€”â€”â€” Reward helpers â€”â€”â€”
    function _availableBSTR() internal view returns (uint256) {
        if (address(BSTR) == address(0)) return 0;
        if (rewardVault == address(0)) {
            // Contract-push mode
            return BSTR.balanceOf(address(this));
        } else if (rewardVaultIsContract) {
            // Vault contract (no allowance needed)
            return BSTR.balanceOf(rewardVault);
        } else {
            // Wallet + allowance mode
            uint256 bal = BSTR.balanceOf(rewardVault);
            uint256 alw = BSTR.allowance(rewardVault, address(this));
            return bal < alw ? bal : alw;
        }
    }

    function _sendReward(address to, uint256 amount) internal {
        require(address(BSTR) != address(0), "BSTR unset");
        uint256 available = _availableBSTR();
        if (available < amount) {
            if (rewardRevertIfInsufficient) revert("Insufficient BSTR");
            emit RewardSkipped(to, amount, available);
            return; // best-effort: skip
        }
        if (rewardVault == address(0)) {
            BSTR.safeTransfer(to, amount);
        } else if (rewardVaultIsContract) {
            IBSTRVault(rewardVault).distribute(to, amount);
        } else {
            BSTR.safeTransferFrom(rewardVault, to, amount);
        }
        emit RewardSent(to, amount);
    }

    // â€”â€”â€” Rescue â€”â€”â€”
    function rescueETH() external onlyOwner nonReentrant {
        uint256 bal = address(this).balance;
        if (bal > 0) payable(fundsReceiver).transfer(bal);
    }

    function rescueERC20(address token, uint256 amount) external onlyOwner nonReentrant {
        IERC20(token).safeTransfer(fundsReceiver, amount);
    }

    // â€”â€”â€” Views / Frontend helpers â€”â€”â€”
    function uri(uint256 id) public view override returns (string memory) {
        require(id == TOKEN_ID, "Bad tokenId");
        return string(abi.encodePacked(_baseURI, Strings.toString(TOKEN_ID), ".json"));
    }

    function contractURI() external view returns (string memory) {
        return _contractURI;
    }
    function totalMinted() external view returns (uint256) {
        return totalSupply(TOKEN_ID);
    }
    function maxSupply() external view returns (uint256) {
        return MAX_SUPPLY;
    }

    function rewardConfig()
        external
        view
        returns (
            address bstr,
            address vault,
            bool isContract,
            uint256 perUnit,
            bool enabled,
            bool onAirdrop,
            bool strict
        )
    {
        return (
            address(BSTR),
            rewardVault,
            rewardVaultIsContract,
            rewardPerUnit,
            rewardEnabled,
            rewardOnAirdrop,
            rewardRevertIfInsufficient
        );
    }

    function airdropFlag() external view returns (bool) {
        return rewardEnabled && rewardPerUnit > 0;
    }

    function rewardCapacity() public view returns (uint256 units) {
        if (!rewardEnabled || rewardPerUnit == 0 || address(BSTR) == address(0)) return 0;
        return _availableBSTR() / rewardPerUnit; // floors to full units only
    }

    function rewardStatusForMint(
        uint256 units
    )
        external
        view
        returns (
            bool active,
            uint256 perUnit,
            uint256 totalRewards,
            bool viaVault,
            address vault,
            uint256 availableUnits
        )
    {
        active = rewardEnabled && rewardPerUnit > 0 && address(BSTR) != address(0);
        perUnit = rewardPerUnit;
        totalRewards = rewardPerUnit * units;
        viaVault = rewardVault != address(0);
        vault = rewardVault;
        availableUnits = rewardPerUnit == 0 ? 0 : (_availableBSTR() / rewardPerUnit);
    }

    function balanceOfKey(address account) external view returns (uint256) {
        return balanceOf(account, TOKEN_ID);
    }

    // â€”â€”â€” Required overrides â€”â€”â€”
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override(ERC1155, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC1155, ERC2981) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

Frontend/app/components/profile/VHCLRegi.content:
"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/app/components/ui/card";
import { Button } from "@/app/components/ui/button";
import { Plus, ShieldCheck, Zap, Trophy } from "lucide-react";
import Image from "next/image";
import { vehicles, formatCurrency, type Vehicle } from "@/app/data/vehicleData";

interface VHCLRegistryProps {
    isOwner: boolean;
    onRegisterClick: () => void;
    onVehicleClick?: (vehicle: Vehicle) => void;
}

function getStatusBadge(status?: Vehicle['status']) {
    switch (status) {
        case 'coming_soon':
            return (
                <div className="absolute top-2 left-1/2 -translate-x-1/2 bg-yellow-500/90 backdrop-blur-sm text-black text-[10px] font-bold px-3 py-1 rounded-full flex items-center gap-1.5 shadow-lg">
                    <span className="w-1.5 h-1.5 bg-black rounded-full animate-pulse" />
                    Coming Soon
                </div>
            );
        case 'demo':
            return (
                <div className="absolute top-2 right-2 bg-primary/90 backdrop-blur-sm text-black text-[10px] font-bold px-2.5 py-1 rounded-full flex items-center gap-1.5 shadow-lg">
                    <TrendingIcon />
                    Demo
                </div>
            );
        case 'live':
            return (
                <div className="absolute top-2 right-2 bg-green-500/90 backdrop-blur-sm text-white text-[10px] font-bold px-2.5 py-1 rounded-full flex items-center gap-1.5 shadow-lg">
                    <span className="w-1.5 h-1.5 bg-white rounded-full animate-pulse" />
                    Live
                </div>
            );
        default:
            return null;
    }
}

function TrendingIcon() {
    return (
        <svg className="w-3 h-3" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="23 6 13.5 15.5 8.5 10.5 1 18" />
            <polyline points="17 6 23 6 23 12" />
        </svg>
    );
}

export function VHCLRegistry({ isOwner, onRegisterClick, onVehicleClick }: VHCLRegistryProps) {
    return (
        <Card className="w-full bg-black/40 border-white/10 backdrop-blur-md mb-6">
            <CardHeader className="flex flex-row items-center justify-between pb-2">
                <div className="space-y-1">
                    <CardTitle className="text-xl font-bold text-white flex items-center gap-2">
                        <ShieldCheck className="w-5 h-5 text-primary" />
                        VHCL Registry
                    </CardTitle>
                    <p className="text-xs text-zinc-400">
                        Official on-chain registered vehicles
                    </p>
                </div>
                {isOwner && (
                    <Button onClick={onRegisterClick} size="sm" className="bg-primary hover:bg-primary/90 text-black font-bold">
                        <Plus className="w-4 h-4 mr-1" />
                        Register VHCL
                    </Button>
                )}
            </CardHeader>
            <CardContent>
                {vehicles.length > 0 ? (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {vehicles.map((vehicle) => (
                            <button
                                key={vehicle._id}
                                onClick={() => onVehicleClick?.(vehicle)}
                                className="text-left w-full"
                            >
                                <div className="group relative rounded-xl overflow-hidden border border-white/10 bg-zinc-900 transition-all hover:border-primary/50 cursor-pointer">
                                    {/* Image Container */}
                                    <div className="relative aspect-video">
                                        {vehicle.images && vehicle.images[0] ? (
                                            <Image
                                                src={vehicle.images[0].url}
                                                alt={`${vehicle.year} ${vehicle.make} ${vehicle.model}`}
                                                fill
                                                className="object-cover transition-transform group-hover:scale-105"
                                            />
                                        ) : (
                                            <div className="w-full h-full flex items-center justify-center bg-zinc-800 text-zinc-600">
                                                No Image
                                            </div>
                                        )}

                                        {/* Status Badge */}
                                        {getStatusBadge(vehicle.status)}
                                    </div>

                                    {/* Info Section */}
                                    <div className="p-4 bg-zinc-900/95 border-t border-white/5">
                                        {/* Year, Make & Token */}
                                        <div className="flex justify-between items-start mb-1">
                                            <p className="text-xs text-zinc-400 font-mono uppercase tracking-wide">
                                                {vehicle.year} {vehicle.make}
                                            </p>
                                            {vehicle.isUpgraded && vehicle.carToken && (
                                                <span className="text-xs font-bold text-primary bg-primary/10 px-2 py-0.5 rounded border border-primary/20">
                                                    ${vehicle.carToken.ticker}
                                                </span>
                                            )}
                                        </div>

                                        {/* Model Name */}
                                        <h3 className="font-bold text-white text-xl font-mono leading-tight mb-0.5">
                                            {vehicle.model}
                                        </h3>

                                        {/* Collection/Nickname */}
                                        {vehicle.collection && (
                                            <p className="text-xs text-zinc-500 mb-3">
                                                {vehicle.collection}
                                            </p>
                                        )}

                                        {/* Valuation Section */}
                                        <div className="space-y-2 pt-2 border-t border-white/5">
                                            {/* Market Value */}
                                            <div className="flex justify-between items-center">
                                                <span className="text-xs text-primary font-medium">MV</span>
                                                <span className="text-sm font-bold text-white font-mono">
                                                    {formatCurrency(vehicle.valuation.marketValue)}
                                                </span>
                                            </div>

                                            {/* Appraised Value */}
                                            <div className="flex justify-between items-center">
                                                <span className="text-xs text-zinc-500 font-medium">AV</span>
                                                <span className="text-sm text-zinc-400 font-mono">
                                                    {formatCurrency(vehicle.valuation.appraisedValue)}
                                                </span>
                                            </div>

                                            {/* Spread */}
                                            {vehicle.valuation.spread !== 0 && (
                                                <div className="flex justify-between items-center pt-1 border-t border-white/5">
                                                    <span className="text-xs text-zinc-500 font-medium">Spread</span>
                                                    <span className={`text-xs font-bold font-mono ${
                                                        vehicle.valuation.spread < 0 
                                                            ? 'text-green-400' 
                                                            : 'text-red-400'
                                                    }`}>
                                                        {vehicle.valuation.spread >= 0 ? '+' : ''}
                                                        {formatCurrency(vehicle.valuation.spread)} ({vehicle.valuation.spread >= 0 ? '+' : ''}{vehicle.valuation.spreadPercent.toFixed(1)}%)
                                                    </span>
                                                </div>
                                            )}
                                        </div>

                                        {/* Sponsor Slots Preview */}
                                        {vehicle.sponsorshipCollection && (
                                            <div className="pt-3 mt-3 border-t border-white/5">
                                                <div className="flex items-center justify-between mb-2">
                                                    <div className="flex items-center gap-1.5">
                                                        <Trophy className="w-3.5 h-3.5 text-yellow-500" />
                                                        <span className="text-xs text-zinc-400 font-medium">Sponsors</span>
                                                    </div>
                                                    <span className="text-xs text-zinc-500">
                                                        {vehicle.sponsors.length}/{vehicle.sponsorshipCollection.maxSupply} claimed
                                                    </span>
                                                </div>
                                                <div className="flex items-center gap-1">
                                                    {/* Show claimed sponsor logos (up to 5) */}
                                                    {vehicle.sponsors.slice(0, 5).map((sponsor, idx) => (
                                                        <div 
                                                            key={sponsor.tokenId} 
                                                            className="w-7 h-7 rounded-lg border border-white/20 bg-zinc-800 overflow-hidden relative shadow-sm"
                                                            style={{ marginLeft: idx > 0 ? '-4px' : 0, zIndex: 5 - idx }}
                                                        >
                                                            {sponsor.logo ? (
                                                                <Image
                                                                    src={sponsor.logo}
                                                                    alt={sponsor.name || "Sponsor"}
                                                                    fill
                                                                    className="object-cover"
                                                                />
                                                            ) : (
                                                                <div className="w-full h-full flex items-center justify-center bg-gradient-to-br from-zinc-700 to-zinc-800">
                                                                    <span className="text-[8px] font-bold text-zinc-400">
                                                                        {sponsor.name?.substring(0, 2).toUpperCase() || "SP"}
                                                                    </span>
                                                                </div>
                                                            )}
                                                        </div>
                                                    ))}
                                                    {/* Show remaining count if more than 5 */}
                                                    {vehicle.sponsors.length > 5 && (
                                                        <div 
                                                            className="w-7 h-7 rounded-lg border border-white/10 bg-zinc-800 flex items-center justify-center"
                                                            style={{ marginLeft: '-4px' }}
                                                        >
                                                            <span className="text-[9px] font-bold text-zinc-400">
                                                                +{vehicle.sponsors.length - 5}
                                                            </span>
                                                        </div>
                                                    )}
                                                    {/* Show available slots indicator */}
                                                    {vehicle.sponsorshipCollection.maxSupply - vehicle.sponsors.length > 0 && (
                                                        <div className="flex items-center gap-1 ml-2">
                                                            <div className="w-5 h-5 rounded border border-dashed border-yellow-500/40 bg-yellow-500/5 flex items-center justify-center">
                                                                <Plus className="w-2.5 h-2.5 text-yellow-500/60" />
                                                            </div>
                                                            <span className="text-[10px] text-yellow-500/70 font-medium">
                                                                {vehicle.sponsorshipCollection.maxSupply - vehicle.sponsors.length} available
                                                            </span>
                                                        </div>
                                                    )}
                                                </div>
                                            </div>
                                        )}

                                        {/* No Sponsorship Collection - Show Opportunity */}
                                        {!vehicle.sponsorshipCollection && vehicle.isUpgraded && (
                                            <div className="pt-3 mt-3 border-t border-white/5">
                                                <div className="flex items-center justify-between">
                                                    <div className="flex items-center gap-1.5">
                                                        <Trophy className="w-3.5 h-3.5 text-zinc-500" />
                                                        <span className="text-xs text-zinc-500">No sponsors yet</span>
                                                    </div>
                                                    <span className="text-[10px] text-yellow-500/70 bg-yellow-500/10 px-2 py-0.5 rounded-full border border-yellow-500/20">
                                                        Create Collection
                                                    </span>
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </button>
                        ))}
                    </div>
                ) : (
                    <div className="text-center py-8 px-4 border border-dashed border-white/10 rounded-lg bg-white/5">
                        <div className="flex justify-center mb-4">
                            <div className="p-3 bg-zinc-900 rounded-full">
                                <ShieldCheck className="w-8 h-8 text-zinc-500" />
                            </div>
                        </div>
                        <h3 className="text-lg font-medium text-white mb-2">No Vehicles Registered</h3>
                        <p className="text-zinc-400 text-sm max-w-md mx-auto mb-6">
                            Register your vehicle on-chain to unlock monetization, sponsorship opportunities, and digital twin creation.
                        </p>

                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 max-w-2xl mx-auto mb-6">
                            <div className="p-3 bg-black/40 rounded border border-white/5 text-left">
                                <ShieldCheck className="w-5 h-5 text-primary mb-2" />
                                <h4 className="text-sm font-bold text-white">Proof of Ownership</h4>
                                <p className="text-xs text-zinc-500">Immutable on-chain record linked to your VIN.</p>
                            </div>
                            <div className="p-3 bg-black/40 rounded border border-white/5 text-left">
                                <Zap className="w-5 h-5 text-yellow-500 mb-2" />
                                <h4 className="text-sm font-bold text-white">Monetization</h4>
                                <p className="text-xs text-zinc-500">Launch a token and earn from your car&apos;s reputation.</p>
                            </div>
                            <div className="p-3 bg-black/40 rounded border border-white/5 text-left">
                                <Trophy className="w-5 h-5 text-purple-500 mb-2" />
                                <h4 className="text-sm font-bold text-white">Sponsorships</h4>
                                <p className="text-xs text-zinc-500">Sell sponsorship slots as NFTs to brands and fans.</p>
                            </div>
                        </div>

                        {isOwner && (
                            <Button onClick={onRegisterClick} className="bg-primary hover:bg-primary/90 text-black font-bold">
                                Start Registration ($50)
                            </Button>
                        )}
                    </div>
                )}
            </CardContent>
