Frontend/app/components/modals/WalletMan.content:
"use client";

import { useState } from "react";
import { useAccount, useConnect, useDisconnect, type Connector } from "wagmi";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/app/components/ui/dialog";
import { Button } from "@/app/components/ui/button";
import { Wallet, CheckCircle } from "lucide-react";

interface WalletManagementModalProps {
    isOpen: boolean;
    onClose: () => void;
}

export function WalletManagementModal({ isOpen, onClose }: WalletManagementModalProps) {
    const { address, isConnected, connector } = useAccount();
    const { connect, connectors } = useConnect();
    const { disconnect } = useDisconnect();
    const [isLoading, setIsLoading] = useState(false);

    const handleConnect = async (connector: Connector) => {
        setIsLoading(true);
        try {
            await connect({ connector });
        } catch (error) {
            console.error("Failed to connect wallet:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleDisconnect = async () => {
        setIsLoading(true);
        try {
            await disconnect();
        } catch (error) {
            console.error("Failed to disconnect wallet:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const formatAddress = (addr: string) => {
        return `${addr.slice(0, 6)}...${addr.slice(-4)}`;
    };

    return (
        <Dialog open={isOpen} onOpenChange={onClose}>
            <DialogContent className="bg-gray-900 border-gray-700 text-white max-w-md">
                <DialogHeader>
                    <DialogTitle className="text-xl font-bold text-white font-mono flex items-center gap-2">
                        <Wallet className="w-5 h-5 text-[#00daa2]" />
                        Manage Wallets
                    </DialogTitle>
                </DialogHeader>

                <div className="space-y-4 py-4">
                    {/* Connected Wallet Section */}
                    {isConnected && address ? (
                        <div className="space-y-3">
                            <h3 className="text-sm font-medium text-gray-400 font-mono">
                                Connected Wallet
                            </h3>
                            <div className="bg-gray-800 rounded-lg p-4 border border-gray-700">
                                <div className="flex items-center justify-between mb-3">
                                    <div className="flex items-center gap-3">
                                        <div className="w-10 h-10 bg-[#00daa2]/20 rounded-full flex items-center justify-center">
                                            <Wallet className="w-5 h-5 text-[#00daa2]" />
                                        </div>
                                        <div>
                                            <p className="text-white font-mono font-medium">
                                                {connector?.name || "Wallet"}
                                            </p>
                                            <p className="text-gray-400 text-sm font-mono">
                                                {formatAddress(address)}
                                            </p>
                                        </div>
                                    </div>
                                    <CheckCircle className="w-5 h-5 text-[#00daa2]" />
                                </div>
                                <Button
                                    onClick={handleDisconnect}
                                    disabled={isLoading}
                                    variant="outline"
                                    className="w-full border-red-500 text-red-500 hover:bg-red-500/10 font-mono"
                                >
                                    Disconnect
                                </Button>
                            </div>
                        </div>
                    ) : (
                        <div className="space-y-3">
                            <h3 className="text-sm font-medium text-gray-400 font-mono">
                                Available Wallets
                            </h3>
                            {connectors.map((connector) => (
                                <button
                                    key={connector.id}
                                    onClick={() => handleConnect(connector)}
                                    disabled={isLoading}
                                    className="w-full bg-gray-800 hover:bg-gray-700 rounded-lg p-4 border border-gray-700 transition-colors flex items-center justify-between"
                                >
                                    <div className="flex items-center gap-3">
                                        <div className="w-10 h-10 bg-gray-700 rounded-full flex items-center justify-center">
                                            <Wallet className="w-5 h-5 text-white" />
                                        </div>
                                        <span className="text-white font-mono">
                                            {connector.name}
                                        </span>
                                    </div>
                                    <div className="text-[#00daa2]">
                                        <svg
                                            className="w-5 h-5"
                                            fill="none"
                                            stroke="currentColor"
                                            viewBox="0 0 24 24"
                                        >
                                            <path
                                                strokeLinecap="round"
                                                strokeLinejoin="round"
                                                strokeWidth={2}
                                                d="M9 5l7 7-7 7"
                                            />
                                        </svg>
                                    </div>
                                </button>
                            ))}
                        </div>
                    )}

                    {/* Info Section */}
                    <div className="bg-blue-900/20 border border-blue-500/30 rounded-lg p-3">
                        <p className="text-blue-300 text-xs font-mono">
                            ðŸ’¡ Your wallet is your identity on DRVN. Connect to access all features.
                        </p>
                    </div>
                </div>

                <div className="flex justify-end pt-4 border-t border-gray-700">
                    <Button
                        onClick={onClose}
                        variant="outline"
                        className="border-gray-600 text-white hover:bg-gray-800 font-mono"
                    >
                        Close
                    </Button>
                </div>
            </DialogContent>
        </Dialog>
    );
}

Frontend/app/api/vehicles/register/route.content:
import { NextRequest, NextResponse } from "next/server";
import dbConnect from "@/lib/mongodb";
import RegisteredVehicle from "@/lib/models/RegisteredVehicle";
import User from "@/lib/models/User";

/**
 * POST /api/vehicles/register
 * 
 * Registers a new vehicle on-chain and in the database.
 * 
 * Expected body:
 * {
 *   userId: string,
 *   vin: string,
 *   factorySpecs: { make, model, year, trim, engine, transmission, color },
 *   images: string[], // IPFS URLs
 *   nftImageIndex: number,
 *   nickname?: string,
 *   txHash: string, // Transaction hash from VehicleRegistry.registerVehicle
 *   tokenId: number,
 *   tbaAddress: string
 * }
 */
export async function POST(req: NextRequest) {
    try {
        await dbConnect();

        const body = await req.json();
        const {
            userId,
            vin,
            factorySpecs,
            images,
            nftImageIndex,
            nickname,
            txHash,
            tokenId,
            tbaAddress,
        } = body;

        // Validation
        if (!userId || !vin || !factorySpecs || !images || !txHash || !tokenId || !tbaAddress) {
            return NextResponse.json(
                { error: "Missing required fields" },
                { status: 400 }
            );
        }

        // Check if VIN already registered
        const existing = await RegisteredVehicle.findOne({ vin });
        if (existing) {
            return NextResponse.json(
                { error: "Vehicle already registered" },
                { status: 409 }
            );
        }

        // Create vehicle record
        const vehicle = await RegisteredVehicle.create({
            owner: userId,
            vin,
            factorySpecs,
            images,
            nftImageIndex: nftImageIndex || 0,
            nickname,
            registrationTx: txHash,
            tokenId,
            tbaAddress,
            isUpgraded: false,
        });

        // Update user's registered vehicles count (optional denormalization)
        await User.findByIdAndUpdate(userId, {
            $inc: { registeredVehiclesCount: 1 },
        });

        return NextResponse.json(
            {
                success: true,
                vehicleId: vehicle._id,
                tokenId: vehicle.tokenId,
                tbaAddress: vehicle.tbaAddress,
            },
            { status: 201 }
        );
    } catch (error) {
        console.error("Error registering vehicle:", error);
        return NextResponse.json(
            { error: "Internal server error" },
            { status: 500 }
        );
    }
}

Frontend/app/globals.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: #ffffff;
  --foreground: #111111;
}

@media (prefers-color-scheme: dark) {
  :root {
    --background: #111111;
    --foreground: #ffffff;
  }
}

html, body {
  overflow-x: hidden;
  max-width: 100vw;
}

body {
  color: var(--foreground);
  background: var(--background);
  font-family: var(--font-space-grotesk), "Space Grotesk", sans-serif;
  font-optical-sizing: auto;
  font-weight: 400;
  font-style: normal;
  font-size: 80%;
}

/* Custom scrollbar styles */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* Sleek thin scrollbar for all elements */
* {
  scrollbar-width: thin;
  scrollbar-color: rgba(255, 255, 255, 0.15) transparent;
}

*::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

*::-webkit-scrollbar-track {
  background: transparent;
  border-radius: 10px;
}

*::-webkit-scrollbar-thumb {
  background: rgba(255, 255, 255, 0.15);
  border-radius: 10px;
  border: 2px solid transparent;
}

*::-webkit-scrollbar-thumb:hover {
  background: rgba(255, 255, 255, 0.25);
}

*::-webkit-scrollbar-corner {
  background: transparent;
}

/* Ultra sleek scrollbar variant for modals */
.sleek-scrollbar {
  scrollbar-width: thin;
  scrollbar-color: rgba(255, 255, 255, 0.1) transparent;
}

.sleek-scrollbar::-webkit-scrollbar {
  width: 4px;
}

.sleek-scrollbar::-webkit-scrollbar-track {
  background: transparent;
}

.sleek-scrollbar::-webkit-scrollbar-thumb {
  background: linear-gradient(180deg, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.1) 100%);
  border-radius: 10px;
}

.sleek-scrollbar::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(180deg, rgba(255, 255, 255, 0.3) 0%, rgba(255, 255, 255, 0.2) 100%);
}

/* Mobile optimizations */
@media (max-width: 768px) {
  .scrollbar-hide {
    scrollbar-width: none;
    -ms-overflow-style: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  /* Improve mobile scrolling */
  body {
    -webkit-overflow-scrolling: touch;
    overscroll-behavior: contain;
  }

  /* Ensure smooth scrolling on mobile */
  html {
    -webkit-overflow-scrolling: touch;
    overscroll-behavior: contain;
  }

  /* Mobile scroll optimization for main content */
  .mobile-scroll {
    -webkit-overflow-scrolling: touch;
    overscroll-behavior-y: auto;
    scroll-behavior: smooth;
  }

  /* Fix for mobile layout to prevent blank space */
  @media (max-width: 768px) {
    .mobile-scroll {
      height: auto;
      min-height: calc(100vh - 80px);
      /* Ensure no extra space at bottom */
      padding-bottom: 0;
    }
