}

/* DRVN Car Animation Enhancements */
@keyframes car-glow {
  0%,
  100% {
    filter: drop-shadow(0 0 8px rgba(9, 9, 9, 0.6));
  }
  50% {
    filter: drop-shadow(0 0 15px rgba(8, 8, 8, 0.9));
