}

/* @keyframes road-dust {
  0% {
    opacity: 0;
    transform: scale(0.8);
  }
  50% {
    opacity: 0.6;
    transform: scale(1.2);
  }
  100% {
    opacity: 0;
    transform: scale(1.5);
  }
} */

/* Enhanced Car Dashboard Effects */
@keyframes dashboard-scan {
  0% {
    background-position: -100% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

@keyframes fuel-gauge-pulse {
  0%,
  100% {
    box-shadow: 0 0 5px rgba(0, 218, 162, 0.3);
  }
  50% {
    box-shadow: 0 0 15px rgba(0, 218, 162, 0.6);
