  }
}

@keyframes transmission-glow {
  0%,
  100% {
    border-color: rgba(0, 218, 162, 0.2);
  }
  50% {
    border-color: rgba(0, 218, 162, 0.5);
  }
}

/* Car Dashboard Modal Enhancements */
.dashboard-modal {
  background: linear-gradient(
    135deg,
    rgba(17, 17, 17, 0.95) 0%,
    rgba(0, 0, 0, 0.9) 50%,
    rgba(34, 34, 34, 0.95) 100%
  );
