  );
}

.fuel-gauge-container {
  background: linear-gradient(
    135deg,
    rgba(34, 34, 34, 0.4) 0%,
    rgba(0, 218, 162, 0.1) 50%,
    rgba(34, 34, 34, 0.4) 100%
  );
