}

.engine-status {
  animation: engine-idle 2s ease-in-out infinite;
}

.transmission-status {
  animation: transmission-glow 3s ease-in-out infinite;
}

/* Enhanced Button Hover Effects */
.car-button-hover {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.car-button-hover:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px rgba(0, 218, 162, 0.3);
}

/* Car Background Overlay */
.car-bg-overlay {
  background-image: url("/Cars/SupraHero2.jpg");
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  filter: blur(3px) brightness(0.2) contrast(1.2);
  animation: car-glow 4s ease-in-out infinite;
}

/* Car Slide Animations */
@keyframes slide-in-from-right {
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes slide-in-from-left {
  0% {
    transform: translateX(-100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes slide-out-to-left {
  0% {
    transform: translateX(0);
    opacity: 1;
  }
  100% {
    transform: translateX(-100%);
    opacity: 0;
  }
}

@keyframes slide-out-to-right {
  0% {
    transform: translateX(0);
    opacity: 1;
  }
  100% {
    transform: translateX(100%);
    opacity: 0;
  }
}

.animate-slide-in-right {
  animation: slide-in-from-right 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
}

.animate-slide-in-left {
  animation: slide-in-from-left 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
}

.animate-slide-out-left {
  animation: slide-out-to-left 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
}

.animate-slide-out-right {
  animation: slide-out-to-right 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
}

/* Car overlay slide animations for GarageHero - Two-phase animation */
/* Exit animations - car slides out */
@keyframes slideOutLeft {
  0% {
    transform: translateX(0);
    opacity: 1;
  }
  100% {
    transform: translateX(-100%);
    opacity: 0;
  }
}

@keyframes slideOutRight {
  0% {
    transform: translateX(0);
    opacity: 1;
  }
  100% {
    transform: translateX(100%);
    opacity: 0;
  }
}

/* Enter animations - new car slides in */
@keyframes slideFromRight {
  0% {
    transform: translateX(100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes slideFromLeft {
  0% {
    transform: translateX(-100%);
    opacity: 0;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

