    purchaseTxHash: String,
    purchasedAt: {
        type: Date,
        default: Date.now,
    },

    createdAt: {
        type: Date,
        default: Date.now,
    },
    updatedAt: {
        type: Date,
        default: Date.now,
    },
});

// Update timestamp on save
SponsorshipSchema.pre("save", function (next) {
    this.updatedAt = new Date();
    next();
});

export default mongoose.models.Sponsorship || mongoose.model("Sponsorship", SponsorshipSchema);

