Hardhat/scripts/verify-all-contracts.ts.content:
import { ethers } from "hardhat";
import { HardhatRuntimeEnvironment } from "hardhat/types";

async function main() {
  console.log("ðŸ” Starting contract verification with constructor arguments...");

  const hre = require("hardhat") as HardhatRuntimeEnvironment;
  const { getNamedAccounts } = hre;
  const { deployer } = await getNamedAccounts();

  // Helper functions to format values (same as deployment script)
  const toUSDC = (n: string | number) => ethers.utils.parseUnits(String(n), 6); // 6â€‘decimals
  const toBSTR = (n: string | number) => ethers.utils.parseUnits(String(n), 9); // 9â€‘decimals

  // -----------------------------
  // ðŸ”— Addresses (same as deployment)
  // -----------------------------
  const BSTR = "0xbfc5cd421bbc91a2ca976c4ab1754748634b7d41"; // 9â€‘dec ERC20
  const USDC = "0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913"; // 6â€‘dec ERC20

  // -----------------------------
  // Contract configurations (same as deployment)
  // -----------------------------
  const Steel = {
    name: "DRVN Steel Key",
    symbol: "Steel",
    baseURI:
      "https://drvnlabo.mypinata.cloud/ipfs/bafkreicdzxjph6vjutybhyusr74hrpdyyalernyfuomm37w2c6qhfvnufu/",
    contractURI:
      "https://drvnlabo.mypinata.cloud/ipfs/bafkreic76uhhdcpjgb3z5zlwowas24w3ow2d7kw2222zhda6arjlb4lqne/",
    owner: deployer,
    usdc: USDC,
    funds: deployer,
    royaltyBps: 500,
    maxSupply: 62,
    mintPrice: toUSDC(450),
    rewardPerUnit: toBSTR(500000),
  };

  const Carbon = {
    name: "DRVN Carbon Key",
    symbol: "Carbon",
    baseURI:
      "https://drvnlabo.mypinata.cloud/ipfs/bafkreihvtv2nk42zselxofww2dibs6r32x6yudwhzjrcqon33iorqgjxpi/",
    contractURI:
      "https://drvnlabo.mypinata.cloud/ipfs/bafkreiepwquub22atfdxloyqca5hlakn4nzps42re4nvdtwtfefonbkcxi/",
    owner: deployer,
    usdc: USDC,
    funds: deployer,
    royaltyBps: 500,
    maxSupply: 24,
    mintPrice: toUSDC(1100),
    rewardPerUnit: toBSTR(1250000),
  };

  const Titanium = {
    name: "DRVN Titanium Key",
    symbol: "Titanium",
    baseURI:
      "https://drvnlabo.mypinata.cloud/ipfs/bafkreigfhls2uxkhgd63gqfk4moe7tne5gcsxmorthz5qx6c6ug7z3qjq4/",
    contractURI:
      "https://drvnlabo.mypinata.cloud/ipfs/bafkreic33mbfqhjzogxqf2wq5ds2xyvsnuvajicu6li7wv56ms6w7j3pde/",
    owner: deployer,
    usdc: USDC,
    funds: deployer,
    royaltyBps: 500,
    maxSupply: 7,
    mintPrice: toUSDC(4000),
    rewardPerUnit: toBSTR(4750000),
  };

  // -----------------------------
  // Get deployed contract addresses
  // -----------------------------
  const { deployments } = hre;
  const deployedContracts = await deployments.all();

  // Sleep function for rate limiting
  const sleep = (ms: number): Promise<void> => {
    return new Promise((resolve) => setTimeout(resolve, ms));
  };

  // Verification function with retry logic
  async function verifyContract(
    contractName: string,
    address: string,
    constructorArgs: unknown[],
    contractPath?: string
  ) {
    console.log(`\nðŸ” Verifying ${contractName} at ${address}...`);
    console.log(`ðŸ“ Constructor args: ${JSON.stringify(constructorArgs, null, 2)}`);

    try {
      const verifyOptions: {
        address: string;
        constructorArguments: unknown[];
        contract?: string;
      } = {
        address: address,
        constructorArguments: constructorArgs,
      };

      if (contractPath) {
        verifyOptions.contract = contractPath;
      }

      await hre.run("verify:verify", verifyOptions);
      console.log(`âœ… ${contractName} verified successfully!`);
      return true;
    } catch (error: unknown) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      if (errorMessage.includes("already verified")) {
        console.log(`âœ… ${contractName} is already verified`);
        return true;
      } else if (errorMessage.includes("rate limit")) {
        console.log(`âš ï¸  Rate limit hit for ${contractName}, waiting 5 seconds and retrying...`);
        await sleep(5000);
        return verifyContract(contractName, address, constructorArgs, contractPath);
      } else {
        console.error(`âŒ Failed to verify ${contractName}:`, errorMessage);
        return false;
      }
    }
  }

  // -----------------------------
  // Verify BSTRVault
  // -----------------------------
  if (deployedContracts.BSTRVault) {
    const bstrVaultArgs = [deployer, BSTR, []]; // owner, BSTR token, initial minters[]
    await verifyContract(
      "BSTRVault",
      deployedContracts.BSTRVault.address,
      bstrVaultArgs,
      "contracts/BSTRVault.sol:BSTRVault"
    );
    await sleep(3000); // Wait 3 seconds between verifications
  }

  // -----------------------------
  // Verify DRVNSteel
  // -----------------------------
  if (deployedContracts.DRVNSteel) {
    const steelArgs = [
      Steel.name,
      Steel.symbol,
      Steel.baseURI,
      Steel.contractURI,
      Steel.owner,
      Steel.usdc,
      Steel.funds,
      Steel.royaltyBps,
      Steel.maxSupply,
      Steel.mintPrice,
    ];

    await verifyContract(
      "DRVNSteel",
      deployedContracts.DRVNSteel.address,
      steelArgs,
      "contracts/DRVNSteel.sol:DRVNSteel"
    );
    await sleep(3000);
  }

  // -----------------------------
  // Verify DRVNCarbon
  // -----------------------------
  if (deployedContracts.DRVNCarbon) {
    const carbonArgs = [
      Carbon.name,
      Carbon.symbol,
      Carbon.baseURI,
      Carbon.contractURI,
      Carbon.owner,
      Carbon.usdc,
      Carbon.funds,
      Carbon.royaltyBps,
      Carbon.maxSupply,
      Carbon.mintPrice,
    ];

    await verifyContract(
      "DRVNCarbon",
      deployedContracts.DRVNCarbon.address,
      carbonArgs,
      "contracts/DRVNCarbon.sol:DRVNCarbon"
    );
    await sleep(3000);
  }

  // -----------------------------
  // Verify DRVNTitanium
  // -----------------------------
  if (deployedContracts.DRVNTitanium) {
    const titaniumArgs = [
      Titanium.name,
      Titanium.symbol,
      Titanium.baseURI,
      Titanium.contractURI,
      Titanium.owner,
      Titanium.usdc,
      Titanium.funds,
      Titanium.royaltyBps,
      Titanium.maxSupply,
      Titanium.mintPrice,
    ];

    await verifyContract(
      "DRVNTitanium",
      deployedContracts.DRVNTitanium.address,
      titaniumArgs,
      "contracts/DRVNTitanium.sol:DRVNTitanium"
    );
  }

  console.log("\nðŸŽ‰ Contract verification process completed!");
  console.log("\nðŸ“‹ Summary:");
  console.log("âœ… BSTRVault - Multi-minter vault contract");
  console.log("âœ… DRVNSteel - ID 0, Max Supply: 62, Price: 450 USDC");
  console.log("âœ… DRVNCarbon - ID 1, Max Supply: 24, Price: 1100 USDC");
  console.log("âœ… DRVNTitanium - ID 2, Max Supply: 7, Price: 4000 USDC");
}

main()
  .then(() => process.exit(0))
  .catch((error) => {
    console.error("âŒ Verification script failed:", error);
    process.exit(1);
  });

Frontend/app/components/vehicle/CarProfi.content:
"use client";

import { Card, CardContent } from "@/app/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/app/components/ui/avatar";
import { Badge } from "@/app/components/ui/badge";
import { Button } from "@/app/components/ui/button";
import { ShieldCheck, Zap } from "lucide-react";

interface CarProfileCardProps {
    vehicle: {
        nickname?: string;
        make: string;
        model: string;
        year: number;
        nftImage?: string;
        isUpgraded: boolean;
        location?: string;
        registryId?: string;
        owner?: {
            name: string;
            username?: string;
            avatar?: string;
        };
        followerCount?: number;
        carToken?: {
            ticker: string;
        };
        sponsorshipCollection?: {
            maxSupply: number;
            mintedCount: number;
        };
    };
    onUpgrade?: () => void;
}

export function CarProfileCard({ vehicle, onUpgrade }: CarProfileCardProps) {
    const remainingSponsors = vehicle.sponsorshipCollection
        ? vehicle.sponsorshipCollection.maxSupply - (vehicle.sponsorshipCollection.mintedCount || 0)
        : 0;

    return (
        <Card className="bg-black/40 border-white/10 backdrop-blur-md">
            <CardContent className="p-6 space-y-4">
                <div className="flex flex-col md:flex-row gap-6 items-start">
                    <div className="flex-shrink-0 relative">
                        <Avatar className="w-20 h-20 md:w-24 md:h-24 border-2 border-primary/50 rounded-xl">
                            <AvatarImage src={vehicle.nftImage} alt={vehicle.nickname} className="object-cover" />
                            <AvatarFallback className="bg-zinc-800 rounded-xl text-xs">
                                {vehicle.year} {vehicle.make}
                            </AvatarFallback>
                        </Avatar>
                        {vehicle.isUpgraded && (
                            <div className="absolute -bottom-2 -right-2 bg-primary text-black text-[10px] font-bold px-2 py-0.5 rounded-full border border-black">
                                ${vehicle.carToken?.ticker}
                            </div>
                        )}
                    </div>

                    <div className="flex-grow space-y-3 w-full">
                        <div className="flex justify-between items-start gap-4">
                            <div>
                                <h2 className="text-2xl font-bold text-white leading-tight">
                                    {vehicle.nickname || `${vehicle.year} ${vehicle.model}`}
                                </h2>
                                <p className="text-zinc-400 text-sm">
                                    {vehicle.year} {vehicle.make} {vehicle.model}
                                </p>
                            </div>
                            {!vehicle.isUpgraded && onUpgrade && (
                                <Button
                                    size="sm"
                                    variant="outline"
                                    className="border-yellow-500/50 text-yellow-500 hover:bg-yellow-500/10"
                                    onClick={onUpgrade}
                                >
                                    <Zap className="w-4 h-4 mr-1" />
                                    Monetize
                                </Button>
                            )}
                        </div>

                        {(vehicle.owner || vehicle.location || vehicle.registryId) && (
                            <div className="flex flex-wrap items-center gap-3 text-xs text-zinc-400">
                                {vehicle.owner && (
                                    <div className="flex items-center gap-2">
                                        <Avatar className="w-6 h-6 border border-white/10">
                                            <AvatarImage src={vehicle.owner.avatar || ""} />
                                            <AvatarFallback>{vehicle.owner.name[0]}</AvatarFallback>
                                        </Avatar>
                                        <span>Owned by {vehicle.owner.name}</span>
                                    </div>
                                )}
                                {vehicle.location && (
                                    <div className="flex items-center gap-1">
                                        <ShieldCheck className="w-3 h-3 text-primary" />
                                        <span>{vehicle.location}</span>
                                    </div>
                                )}
                                {vehicle.registryId && (
                                    <Badge variant="secondary" className="bg-white/10 text-white border-white/10">
                                        Registry #{vehicle.registryId}
                                    </Badge>
                                )}
                            </div>
                        )}

                        {vehicle.followerCount && (
                            <p className="text-xs text-zinc-500">
                                Followed by {vehicle.followerCount.toLocaleString()} enthusiasts
                            </p>
                        )}
                    </div>
                </div>

                {vehicle.isUpgraded && vehicle.sponsorshipCollection && (
                    <div className="pt-2 border-t border-white/5">
                        <div className="flex items-center justify-between text-xs text-zinc-500 mb-2">
                            <span className="uppercase tracking-wider font-bold text-white">Sponsor grid</span>
                            <span>
                                {vehicle.sponsorshipCollection.mintedCount}/{vehicle.sponsorshipCollection.maxSupply} minted
                            </span>
                        </div>
                        <div className="flex items-center gap-2">
                            <div className="flex -space-x-2">
                                {[...Array(Math.min(3, vehicle.sponsorshipCollection.mintedCount || 0))].map((_, i) => (
                                    <div key={i} className="w-6 h-6 rounded-full bg-zinc-700 border border-black flex items-center justify-center text-[8px] text-white">
                                        S{i + 1}
                                    </div>
                                ))}
                                {(vehicle.sponsorshipCollection.mintedCount || 0) > 3 && (
                                    <div className="w-6 h-6 rounded-full bg-zinc-800 border border-black flex items-center justify-center text-[8px] text-white">
                                        +{(vehicle.sponsorshipCollection.mintedCount || 0) - 3}
                                    </div>
                                )}
                            </div>
                            {remainingSponsors > 0 && (
                                <span className="text-xs text-primary font-semibold">
                                    {remainingSponsors} spots open
                                </span>
                            )}
                        </div>
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

