"use client";

import { useState } from "react";
import { useParams } from "next/navigation";
import { SponsorProfile } from "@/app/components/sponsor/SponsorProfile";
import { ManageSponsorshipModal } from "@/app/components/modals/ManageSponsorshipModal";
import { Button } from "@/app/components/ui/button";
import { ArrowLeft } from "lucide-react";
import Link from "next/link";

export default function SponsorDetailsPage() {
    const params = useParams();
    const id = params.id as string;

    const [showManageModal, setShowManageModal] = useState(false);

    // Mock Data - Fetch based on `id`
    const [sponsor, setSponsor] = useState({
        tokenId: id,
        name: "Red Bull Racing",
        bio: "Gives You Wings. Official partner of the DRVN ecosystem.",
        logo: "https://upload.wikimedia.org/wikipedia/en/thumb/5/51/Red_Bull_Racing_logo.svg/1200px-Red_Bull_Racing_logo.svg.png",
        website: "https://redbull.com",
        twitter: "https://x.com/redbullracing",
        instagram: "https://instagram.com/redbullracing",
        photos: [
            "/Cars/Porsche911.jpg",
            "/Cars/GarageV12.jpg",
            "/Cars/R34Garage.jpg",
        ],
        vehicleId: "1",
        vehicleName: "Black Widow (GT3 RS)",
        tier: "Titanium",
        slotNumber: 3,
        mintedOn: "Mar 18, 2025",
        mintPrice: 150,
        openSeaUrl: "https://opensea.io/assets/base/0xsponsorship/" + id,
        promoLinks: [
            {
                label: "Team Store Drop",
                description: "Limited edition pit crew jacket",
                url: "https://redbullshop.com/drvn",
            },
            {
                label: "Base Onchaincast",
                description: "Weekly race telemetry via Frames",
                url: "https://warpcast.com/redbullracing",
            },
        ],
        metrics: [
            { label: "Impressions", value: "1.2M", hint: "+14% vs last week" },
            { label: "Clicks", value: "38K", hint: "Base + Farcaster" },
            { label: "Engagement", value: "6.4%", hint: "Benchmark +2.1%" },
        ],
    });

    const isOwner = true; // Mock ownership check

    const handleManage = () => {
        setShowManageModal(true);
    };

    const handleBrandingUpdate = (branding: {
        name?: string;
        logo?: string;
        bio?: string;
        website?: string;
        twitter?: string;
        instagram?: string;
        photos?: string[];
    }) => {
        setSponsor((prev) => ({
            ...prev,
            name: branding.name || prev.name,
            logo: branding.logo || prev.logo,
            bio: branding.bio ?? prev.bio,
            website: branding.website || prev.website,
            twitter: branding.twitter || prev.twitter,
            instagram: branding.instagram || prev.instagram,
            photos: branding.photos && branding.photos.length > 0 ? branding.photos : prev.photos,
        }));
    };

    return (
        <div className="min-h-screen bg-gray-950 pb-20">
            {/* Header / Back Button */}
            <div className="sticky top-0 z-10 bg-gray-950/80 backdrop-blur-md border-b border-white/10 p-4">
                <div className="max-w-4xl mx-auto flex items-center gap-4">
                    <Link href={`/vehicles/${sponsor.vehicleId}`}>
                        <Button variant="ghost" size="icon" className="text-white hover:bg-white/10">
                            <ArrowLeft className="w-6 h-6" />
                        </Button>
                    </Link>
                    <h1 className="text-lg font-bold text-white">Sponsor Details</h1>
                </div>
            </div>

            <div className="max-w-4xl mx-auto p-4">
                <SponsorProfile
                    sponsor={sponsor}
                    isOwner={isOwner}
                    onManage={handleManage}
                />
            </div>

            <ManageSponsorshipModal
                isOpen={showManageModal}
                onClose={() => setShowManageModal(false)}
                sponsorshipId={id}
                currentBranding={{
                    name: sponsor.name,
                    logo: sponsor.logo,
                    bio: sponsor.bio,
                    website: sponsor.website,
                    twitter: sponsor.twitter,
                    instagram: sponsor.instagram,
                    photos: sponsor.photos,
                }}
                onBrandingUpdate={handleBrandingUpdate}
            />
        </div>
    );
}

Hardhat/contracts/SponsorshipNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract SponsorshipNFT is ERC721URIStorage, ERC2981, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    uint256 public constant MAX_SUPPLY = 14;
    uint256 public mintPrice;

    address public vehicleTBA;
    address public treasury;
    IERC20 public paymentToken; // USDC or ETH? Assuming ETH/Native for now, or USDC if specified. Let's use Native ETH for simplicity unless specified.
    // Prompt mentioned "TokenWrapper" for wrapping tokens, but for the NFT purchase itself, usually ETH or USDC.
    // Let's support Native ETH for minting to start.

    constructor(
        string memory _name,
        string memory _symbol,
        address _vehicleTBA,
        address _treasury,
        uint256 _mintPrice
    ) ERC721(_name, _symbol) {
        vehicleTBA = _vehicleTBA;
        treasury = _treasury;
        mintPrice = _mintPrice;

        // Set Default Royalty: 50% total (5000 basis points)
        // But EIP2981 only supports one receiver.
        // We need a Splitter contract or just set the TBA as the receiver, 
        // and the TBA logic handles the split? 
        // Or we set the receiver to THIS contract, and `release` function splits it?
        // For simplicity, let's set Royalty Receiver to Vehicle TBA for now.
        // The prompt says "45% to vehicle TBA, 5% to treasury".
        // This implies a split. 
        // I will set the royalty receiver to the Vehicle TBA, and rely on the TBA to handle it, 
        // OR I can implement a custom `royaltyInfo` that returns a payment splitter address.

        // Let's set it to TBA for 45% for now to keep it simple, or 50% and TBA keeps it all.
        _setDefaultRoyalty(_vehicleTBA, 5000); // 50%
    }

    function mint(address to, string memory tokenURI) external payable returns (uint256) {
        require(_tokenIds.current() < MAX_SUPPLY, "Max supply reached");
        require(msg.value >= mintPrice, "Insufficient payment");

        // Primary Sale Split: 95% to Vehicle TBA, 5% to Treasury
        uint256 treasuryShare = (msg.value * 5) / 100;
        uint256 tbaShare = msg.value - treasuryShare;

        (bool success1, ) = treasury.call{value: treasuryShare}("");
        require(success1, "Treasury transfer failed");

        (bool success2, ) = vehicleTBA.call{value: tbaShare}("");
        require(success2, "TBA transfer failed");

        _tokenIds.increment();
        uint256 newItemId = _tokenIds.current();

        _mint(to, newItemId);
        _setTokenURI(newItemId, tokenURI);

        return newItemId;
    }

    // Override supportsInterface to include ERC2981
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721URIStorage, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    // Allow updating royalties
    function setDefaultRoyalty(address receiver, uint96 feeNumerator) external onlyOwner {
        _setDefaultRoyalty(receiver, feeNumerator);
    }
}

