Frontend/app/api/auth/update-user/route..content:
import { NextRequest, NextResponse } from "next/server";
import dbConnect from "@/lib/mongodb";
import User from "@/lib/models/User";

export async function POST(request: NextRequest) {
  try {
    // Connect to database
    await dbConnect();

    // Get request body
    const body = await request.json();
    const { walletAddress, username, profileImage, firstName, lastName, bio } =
      body;

    console.log("Update user request:", {
      walletAddress,
      username,
      profileImage,
      firstName,
      lastName,
      bio,
    });

    // Validate required fields
    if (!walletAddress || !username) {
      return NextResponse.json(
        { error: "Wallet address and username are required" },
        { status: 400 },
      );
    }

    // Validate username format
    const usernameRegex = /^[a-zA-Z0-9_]+$/;
    if (!usernameRegex.test(username)) {
      return NextResponse.json(
        {
          error: "Username can only contain letters, numbers, and underscores",
        },
        { status: 400 },
      );
    }

    if (username.length < 3 || username.length > 20) {
      return NextResponse.json(
        { error: "Username must be between 3 and 20 characters" },
        { status: 400 },
      );
    }

    // Check if username is already taken
    const existingUserWithUsername = await User.findOne({
      username: username.toLowerCase(),
      walletAddress: { $ne: walletAddress.toLowerCase() },
    });

    if (existingUserWithUsername) {
      return NextResponse.json(
        { error: "Username already taken" },
        { status: 409 },
      );
    }

    // Update user - use updateOne with $set to ensure bio field is saved
    const updateData = {
      username: username.toLowerCase(),
      profileImage: profileImage || "",
      firstName: firstName || "",
      lastName: lastName || "",
      updatedAt: new Date(),
    };

    console.log("Update data being sent to database:", updateData);
    console.log("Bio value specifically:", bio);
    console.log("Bio value after ||:", bio || "");

    // First update the main fields
    const updatedUser = await User.updateOne(
      { walletAddress: walletAddress.toLowerCase() },
      { $set: updateData },
    );

    if (!updatedUser.modifiedCount) {
      console.log("No documents were modified in first update");
      return NextResponse.json(
        { error: "Failed to update user" },
        { status: 500 },
      );
    }

    console.log("First update result:", updatedUser);

    // Now explicitly add/update the bio field
    console.log("Attempting to update bio field specifically...");
    const bioUpdate = await User.updateOne(
      { walletAddress: walletAddress.toLowerCase() },
      { $set: { bio: bio || "" } },
    );

    console.log("Bio update result:", bioUpdate);

    // Fetch the updated user to return
    const finalUser = await User.findOne({
      walletAddress: walletAddress.toLowerCase(),
    }).select(
      "firstName lastName username email xHandle profileImage walletAddress bio createdAt updatedAt",
    );

    if (!finalUser) {
      return NextResponse.json(
        { error: "Failed to fetch updated user" },
        { status: 500 },
      );
    }

    console.log("Final updated user in database:", finalUser);
    console.log("Bio field in final user:", finalUser.bio);
    console.log("Bio field type:", typeof finalUser.bio);
    console.log("All fields in final user:", Object.keys(finalUser.toObject()));

    // Return success response
    return NextResponse.json(
      {
        success: true,
        message: "User updated successfully",
        user: {
          id: finalUser._id,
          firstName: finalUser.firstName,
          lastName: finalUser.lastName,
          username: finalUser.username,
          email: finalUser.email,
          xHandle: finalUser.xHandle,
          profileImage: finalUser.profileImage,
          walletAddress: finalUser.walletAddress,
          bio: finalUser.bio,
          createdAt: finalUser.createdAt,
        },
      },
      { status: 200 },
    );
  } catch (error) {
    console.error("User update error:", error);
    return NextResponse.json(
      { error: "Internal server error" },
      { status: 500 },
    );
  }
}

Frontend/app/components/ui/toast.tsx.content:
"use client";

import { useState, useEffect } from "react";
import { Loader2, CheckCircle, AlertCircle, X, Info } from "lucide-react";
import { cn } from "@/lib/utils";

export interface Toast {
  id: string;
  type: "approve" | "minting" | "success" | "error" | "info";
  title: string;
  message: string;
  contractName?: string;
  quantity?: number;
  hash?: string;
}

interface ToastProps {
  toast: Toast;
  onDismiss: (id: string) => void;
}

export function ToastNotification({ toast, onDismiss }: ToastProps) {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Animate in
    const timer = setTimeout(() => setIsVisible(true), 100);
    return () => clearTimeout(timer);
  }, []);

  const handleDismiss = () => {
    setIsVisible(false);
    setTimeout(() => onDismiss(toast.id), 300);
  };

  const getIcon = () => {
    switch (toast.type) {
      case "approve":
      case "minting":
        return <Loader2 className="w-5 h-5 animate-spin text-green-400" />;
      case "success":
        return <CheckCircle className="w-5 h-5 text-green-400" />;
      case "error":
        return <AlertCircle className="w-5 h-5 text-red-400" />;
      case "info":
        return <Info className="w-5 h-5 text-blue-400" />;
      default:
        return null;
    }
  };

  const getBackgroundColor = () => {
    switch (toast.type) {
      case "approve":
      case "minting":
        return "bg-black";
      case "success":
        return "bg-black";
      case "error":
        return "bg-black";
      default:
        return "bg-black";
    }
  };

  return (
    <div
      className={cn(
        "transition-all duration-300 ease-in-out",
        isVisible ? "translate-y-0 opacity-100" : "-translate-y-2 opacity-0",
      )}
    >
      <div
        className={cn(
          "flex items-center gap-3 px-4 py-3 rounded-lg shadow-xl min-w-[320px] max-w-md",
          getBackgroundColor(),
          "border border-gray-800",
        )}
      >
        {getIcon()}

        <div className="flex-1 min-w-0">
          <div className="text-white font-semibold text-sm mb-1">
            {toast.title}
          </div>
          <div className="text-gray-300 text-xs">{toast.message}</div>
          {toast.hash && (
            <div className="text-green-400 text-xs mt-1 font-mono">
              {toast.hash.slice(0, 6)}...{toast.hash.slice(-4)}
            </div>
          )}
        </div>

        <button
          onClick={handleDismiss}
          className="text-gray-400 hover:text-white transition-colors p-1"
        >
          <X className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
}

export function ToastContainer({
  toasts,
  onDismiss,
}: {
  toasts: Toast[];
  onDismiss: (id: string) => void;
}) {
  return (
    <div className="fixed top-4 left-1/2 transform -translate-x-1/2 z-50 pointer-events-none">
      <div className="space-y-3">
        {toasts.map((toast) => (
          <div key={toast.id} className="pointer-events-auto">
            <ToastNotification toast={toast} onDismiss={onDismiss} />
          </div>
        ))}
      </div>
    </div>
  );
}

