"use client";

import { useState, useEffect } from "react";
import { useReadContract } from "wagmi";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { Trophy } from "lucide-react";
import deployedContracts from "../../../contracts/deployedContracts";

interface ContractData {
  name: string;
  address: string;
  totalMinted: bigint | undefined;
  maxSupply: bigint | undefined;
  isLoading: boolean;
  error: string | null;
}

export default function TotalKeysMinted() {
  const [contractsData, setContractsData] = useState<ContractData[]>([
    {
      name: "Steel Key",
      address: "0x0Efc7EFbbeB794517D62aaD40143f46CF6653f28",
      totalMinted: undefined,
      maxSupply: undefined,
      isLoading: true,
      error: null,
    },
    {
      name: "Carbon Key",
      address: "0x56E75E06F2213A20F35d92E57a958210610F0Aa8",
      totalMinted: undefined,
      maxSupply: undefined,
      isLoading: true,
      error: null,
    },
    {
      name: "Titanium Key",
      address: "0x958468063cA58C641e7b98FC4c7EE6fBd3238145",
      totalMinted: undefined,
      maxSupply: undefined,
      isLoading: true,
      error: null,
    },
  ]);

  // Read totalSupply from each contract
  const steelTotalSupply = useReadContract({
    address: "0x0Efc7EFbbeB794517D62aaD40143f46CF6653f28" as `0x${string}`,
    abi: deployedContracts[8453].SteelBuster.abi,
    functionName: "totalMinted",
  });

  const carbonTotalSupply = useReadContract({
    address: "0x56E75E06F2213A20F35d92E57a958210610F0Aa8" as `0x${string}`,
    abi: deployedContracts[8453].CarbonBuster.abi,
    functionName: "totalMinted",
  });

  const titaniumTotalSupply = useReadContract({
    address: "0x958468063cA58C641e7b98FC4c7EE6fBd3238145" as `0x${string}`,
    abi: deployedContracts[8453].TitaniumBuster.abi,
    functionName: "totalMinted",
  });

  // Read maxSupply from each contract
  const steelMaxSupply = useReadContract({
    address: "0x0Efc7EFbbeB794517D62aaD40143f46CF6653f28" as `0x${string}`,
    abi: deployedContracts[8453].SteelBuster.abi,
    functionName: "maxSupply",
  });

  const carbonMaxSupply = useReadContract({
    address: "0x56E75E06F2213A20F35d92E57a958210610F0Aa8" as `0x${string}`,
    abi: deployedContracts[8453].CarbonBuster.abi,
    functionName: "maxSupply",
  });

  const titaniumMaxSupply = useReadContract({
    address: "0x958468063cA58C641e7b98FC4c7EE6fBd3238145" as `0x${string}`,
    abi: deployedContracts[8453].TitaniumBuster.abi,
    functionName: "maxSupply",
  });

  // Update contracts data when contract reads complete
  useEffect(() => {
    setContractsData([
      {
        name: "Steel Key",
        address: "0x0Efc7EFbbeB794517D62aaD40143f46CF6653f28",
        totalMinted: steelTotalSupply.data as bigint | undefined,
        maxSupply: steelMaxSupply.data as bigint | undefined,
        isLoading: steelTotalSupply.isLoading || steelMaxSupply.isLoading,
        error:
          steelTotalSupply.error?.message ||
          steelMaxSupply.error?.message ||
          null,
      },
      {
        name: "Carbon Key",
        address: "0x56E75E06F2213A20F35d92E57a958210610F0Aa8",
        totalMinted: carbonTotalSupply.data as bigint | undefined,
        maxSupply: carbonMaxSupply.data as bigint | undefined,
        isLoading: carbonTotalSupply.isLoading || carbonMaxSupply.isLoading,
        error:
          carbonTotalSupply.error?.message ||
          carbonMaxSupply.error?.message ||
          null,
      },
      {
        name: "Titanium Key",
        address: "0x958468063cA58C641e7b98FC4c7EE6fBd3238145",
        totalMinted: titaniumTotalSupply.data as bigint | undefined,
        maxSupply: titaniumMaxSupply.data as bigint | undefined,
        isLoading: titaniumTotalSupply.isLoading || titaniumMaxSupply.isLoading,
        error:
          titaniumTotalSupply.error?.message ||
          titaniumMaxSupply.error?.message ||
          null,
      },
    ]);
  }, [
    steelTotalSupply.data,
    steelTotalSupply.isLoading,
    steelTotalSupply.error,
    steelMaxSupply.data,
    steelMaxSupply.isLoading,
    steelMaxSupply.error,
    carbonTotalSupply.data,
    carbonTotalSupply.isLoading,
    carbonTotalSupply.error,
    carbonMaxSupply.data,
    carbonMaxSupply.isLoading,
    carbonMaxSupply.error,
    titaniumTotalSupply.data,
    titaniumTotalSupply.isLoading,
    titaniumTotalSupply.error,
    titaniumMaxSupply.data,
    titaniumMaxSupply.isLoading,
    titaniumMaxSupply.error,
  ]);

  // Calculate totals
  const totalMinted = contractsData.reduce((sum, contract) => {
    return sum + (contract.totalMinted ? Number(contract.totalMinted) : 0);
  }, 0);

  const totalMaxSupply = contractsData.reduce((sum, contract) => {
    return sum + (contract.maxSupply ? Number(contract.maxSupply) : 0);
  }, 0);

  const totalPercentage =
    totalMaxSupply > 0 ? (totalMinted / totalMaxSupply) * 100 : 0;

  // Check if any contracts are still loading
  const isLoading = contractsData.some((contract) => contract.isLoading);

  return (
    <div className="space-y-3">
      {/* Compact Stats Table */}
      <Card className="bg-gradient-to-br from-gray-900 to-black border border-gray-700 max-w-6xl mx-auto">
        <CardHeader className="pb-2">
          <CardTitle className="flex items-center gap-2 text-white text-sm">
            <Trophy className="w-4 h-4 text-[#00daa2]" />
            Live Founder Key Specs
          </CardTitle>
        </CardHeader>
        <CardContent className="p-3">
          {/* Summary Row */}
          <div className="grid grid-cols-3 gap-2 mb-3">
            <div className="bg-gray-800/30 rounded-md p-2 text-center">
              <div className="text-xs text-gray-400 mb-1">Total Minted</div>
              <div className="text-lg font-bold text-[#00daa2]">
                {isLoading ? "..." : totalMinted.toLocaleString()}
              </div>
            </div>
            <div className="bg-gray-800/30 rounded-md p-2 text-center">
              <div className="text-xs text-gray-400 mb-1">Max Supply</div>
              <div className="text-lg font-bold text-white">
                {isLoading ? "..." : totalMaxSupply.toLocaleString()}
              </div>
            </div>
            <div className="bg-gray-800/30 rounded-md p-2 text-center">
              <div className="text-xs text-gray-400 mb-1">Progress</div>
              <div className="text-lg font-bold text-white">
                {isLoading ? "..." : `${totalPercentage.toFixed(1)}%`}
              </div>
            </div>
          </div>

          {/* Progress Bar */}
          <div className="mb-3">
            <div className="flex justify-between text-xs text-gray-400 mb-1">
              <span>Overall Progress</span>
              <span>
                {totalMinted.toLocaleString()} /{" "}
                {totalMaxSupply.toLocaleString()}
              </span>
            </div>
            <div className="w-full relative">
              <div
                className="w-full bg-gray-700 rounded-full h-1.5"
                style={{ position: "relative", overflow: "hidden" }}
              >
                <div
                  className="absolute top-0 left-0 h-full bg-gradient-to-r from-[#00daa2] to-green-400 rounded-full transition-all duration-500"
                  style={{ width: `${totalPercentage}%` }}
                />
              </div>
            </div>
          </div>

          {/* Contract Details Table */}
          <div className="overflow-hidden rounded-md border border-gray-600">
            <table className="w-full text-xs">
              <thead>
                <tr className="bg-gray-800/50 border-b border-gray-600">
                  <th className="text-left p-2 text-gray-300 font-medium">
                    Contract
                  </th>
                  <th className="text-center p-2 text-gray-300 font-medium">
                    Minted
                  </th>
                  <th className="text-center p-2 text-gray-300 font-medium">
                    Max
                  </th>
                  <th className="text-center p-2 text-gray-300 font-medium">
                    Progress
                  </th>
                </tr>
              </thead>
              <tbody>
                {contractsData.map((contract, index) => (
                  <tr
                    key={index}
                    className="border-b border-gray-700/50 last:border-b-0 hover:bg-gray-800/30 transition-colors"
                  >
                    <td className="p-2 text-white font-medium">
                      <div className="flex items-center gap-2">
                        <div
                          className={`w-2 h-2 rounded-full ${
                            index === 0
                              ? "bg-blue-400"
                              : index === 1
                                ? "bg-green-400"
                                : "bg-purple-400"
                          }`}
                        />
                        {contract.name}
                      </div>
                    </td>
                    <td className="p-2 text-center">
                      {contract.isLoading ? (
                        <div className="text-gray-400">...</div>
                      ) : contract.error ? (
                        <div className="text-red-400 text-xs">Error</div>
                      ) : (
                        <span className="text-[#00daa2] font-semibold">
                          {contract.totalMinted?.toString() || "0"}
                        </span>
                      )}
                    </td>
                    <td className="p-2 text-center text-gray-300">
                      {contract.isLoading
                        ? "..."
                        : contract.maxSupply?.toString() || "0"}
                    </td>
                    <td className="p-2 text-center">
                      {contract.isLoading ? (
                        <div className="text-gray-400">...</div>
                      ) : contract.error ? (
                        <div className="text-red-400 text-xs">-</div>
                      ) : (
                        <div className="flex items-center gap-2">
                          <div className="flex-1 relative">
                            <div
                              className="w-full bg-gray-700 rounded-full h-1.5"
                              style={{
                                position: "relative",
                                overflow: "hidden",
                              }}
                            >
                              <div
                                className="absolute top-0 left-0 h-full bg-[#00daa2] rounded-full transition-all duration-500"
                                style={{
                                  width:
                                    contract.maxSupply && contract.totalMinted
                                      ? `${(Number(contract.totalMinted) / Number(contract.maxSupply)) * 100}%`
                                      : "0%",
                                }}
                              />
                            </div>
                          </div>
                          <span className="text-gray-400 text-xs min-w-[2rem]">
                            {contract.maxSupply && contract.totalMinted
                              ? `${((Number(contract.totalMinted) / Number(contract.maxSupply)) * 100).toFixed(1)}%`
                              : "0%"}
                          </span>
                        </div>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

Frontend/app/components/modals/signup-mo.content:
"use client";

import { useState } from "react";
import { Button } from "../ui/button";
import { Input } from "../ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card";
import { X, Upload, Loader2 } from "lucide-react";
import { useAccount } from "wagmi";
import Image from "next/image";
import {} from // Wallet,
// WalletDropdown,
// WalletDropdownDisconnect,
// ConnectWallet,
"@coinbase/onchainkit/wallet";
import {} from // Name,
// Avatar,
// Address,
// EthBalance,
"@coinbase/onchainkit/identity";
import { uploadImageToIpfs } from "../../../utils/ipfs";
import { ConnectButton } from "../web3/ConnectButton";

interface SignupModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSwitchToSignin: () => void;
  onSuccess: () => void;
}

export function SignupModal({
  isOpen,
  onClose,
  onSwitchToSignin,
  onSuccess,
}: SignupModalProps) {
  const { address } = useAccount();
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    xHandle: "",
    profileImage: "",
    bio: "", // Add bio field
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string>("");

  // Check if all required fields are filled
  const isFormValid =
    address &&
    formData.firstName.trim() !== "" &&
    formData.lastName.trim() !== "" &&
    formData.username.trim() !== "" &&
    formData.email.trim() !== "";

  const handleInputChange = (field: string, value: string) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));
  };

  const handleFileSelect = (file: File) => {
    setSelectedFile(file);

    // Create preview URL
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviewUrl(e.target?.result as string);
    };
    reader.readAsDataURL(file);
  };

  const handleUploadToIpfs = async (): Promise<string> => {
    if (!selectedFile) {
      throw new Error("No file selected");
    }

    setIsUploading(true);
    setUploadProgress(0);

    try {
      const ipfsUrl = await uploadImageToIpfs(selectedFile, (progress) => {
        setUploadProgress(progress);
      });

      setIsUploading(false);
      setUploadProgress(0);
      return ipfsUrl;
    } catch (error) {
      setIsUploading(false);
      setUploadProgress(0);
      throw error;
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!address) {
      setError("Please connect your wallet first");
      return;
    }

    setIsLoading(true);
    setError("");

    try {
      // If user selected a custom image, upload it to IPFS first
      let finalProfileImage = formData.profileImage;

      if (
        selectedFile &&
        !formData.profileImage.startsWith(
          "https://app.drvnlabo.mypinata.cloud/ipfs/",
        )
      ) {
        try {
          finalProfileImage = await handleUploadToIpfs();
          console.log("Image uploaded to IPFS:", finalProfileImage);
        } catch (uploadError) {
          console.error("Failed to upload image to IPFS:", uploadError);
          setError(
            "Failed to upload profile image. Please try again or use the default image.",
          );
          setIsLoading(false);
          return;
        }
      }

      // Debug: Log what's being sent
      console.log("Form data being sent:", {
        ...formData,
        profileImage: finalProfileImage,
        walletAddress: address,
      });

      const response = await fetch("/api/auth/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          ...formData,
          profileImage: finalProfileImage,
          walletAddress: address,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Signup failed");
      }

      setSuccess(true);
      setFormData({
        firstName: "",
        lastName: "",
        username: "",
        email: "",
        xHandle: "",
        profileImage: "",
        bio: "",
      });
      setSelectedFile(null);
      setPreviewUrl("");

      // Call onSuccess callback and close modal after a short delay
      setTimeout(() => {
        onSuccess();
        onClose();
        setSuccess(false);
      }, 2000);
    } catch (error) {
      setError(error instanceof Error ? error.message : "Signup failed");
    } finally {
      setIsLoading(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-md bg-gray-950 border-gray-800 max-h-[90vh] overflow-y-auto">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4 sticky top-0 bg-gray-900 z-10">
          <CardTitle className="text-white text-xl font-mono">
            Sign Up
          </CardTitle>
          <Button
            variant="outline"
            size="icon"
            onClick={onClose}
            className="text-red-500 border border-red-500 rounded-full size-6"
          >
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>
        <CardContent className="space-y-4">
          {success ? (
            <div className="text-center py-8">
              <div className="text-[#00daa2] text-lg font-mono mb-2">
                Success!
              </div>
              <div className="text-gray-300 text-sm font-mono">
                Your account has been created successfully.
              </div>
            </div>
          ) : (
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label className="text-sm font-medium text-gray-300 font-mono">
                    First Name
                  </label>
                  <Input
                    type="text"
                    placeholder="Enter your first name"
                    value={formData.firstName}
                    onChange={(e) =>
                      handleInputChange("firstName", e.target.value)
                    }
                    className="bg-gray-950 border-gray-700 text-white placeholder:text-gray-400 font-mono"
                    required
                  />
                </div>
                <div className="space-y-2">
                  <label className="text-sm font-medium text-gray-300 font-mono">
                    Last Name
                  </label>
                  <Input
                    type="text"
                    placeholder="Enter your last name"
                    value={formData.lastName}
                    onChange={(e) =>
                      handleInputChange("lastName", e.target.value)
                    }
                    className="bg-gray-950 border-gray-700 text-white placeholder:text-gray-400 font-mono"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300 font-mono">
                  Username
                </label>
                <Input
                  type="text"
                  placeholder="Choose a unique username"
                  value={formData.username}
                  onChange={(e) =>
                    handleInputChange("username", e.target.value)
                  }
                  className="bg-gray-950 border-gray-700 text-white placeholder:text-gray-400 font-mono"
                  required
                />
                <div className="text-xs text-gray-400 font-mono">
                  3-20 characters, letters, numbers, and underscores only
                </div>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300 font-mono">
                  Email
                </label>
                <Input
                  type="email"
                  placeholder="Enter your email"
                  value={formData.email}
                  onChange={(e) => handleInputChange("email", e.target.value)}
                  className="bg-gray-950 border-gray-700 text-white placeholder:text-gray-400 font-mono"
                  required
                />
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300 font-mono">
                  X/Twitter Handle (Optional)
                </label>
                <div className="relative">
                  <span className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400">
                    @
                  </span>
                  <Input
                    type="text"
                    placeholder="@yourhandle"
                    value={formData.xHandle}
                    onChange={(e) =>
                      handleInputChange("xHandle", e.target.value)
                    }
                    className="bg-gray-950 border-gray-700 text-white placeholder:text-gray-400 font-mono pl-8"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300 font-mono">
                  Bio (Optional)
                </label>
                <textarea
                  placeholder="Tell us about yourself..."
                  value={formData.bio}
                  onChange={(e) => handleInputChange("bio", e.target.value)}
                  className="w-full bg-gray-950 border border-gray-700 text-white placeholder:text-gray-400 font-mono p-3 rounded-md resize-none"
                  rows={3}
                />
                <div className="text-xs text-gray-400 font-mono">
                  Tell us a bit about yourself (max 500 characters)
                </div>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300 font-mono">
                  Profile Picture
                </label>
                <div className="flex items-center gap-4">
                  {previewUrl ? (
                    <div className="w-16 h-16 rounded-full overflow-hidden border-2 border-[#00daa2] relative">
                      <Image
                        src={previewUrl}
                        alt="Profile Preview"
                        width={64}
                        height={64}
                        className="object-cover"
                      />
                      {isUploading && (
                        <div className="absolute inset-0 bg-black/50 flex items-center justify-center">
                          <Loader2 className="h-4 w-4 animate-spin text-[#00daa2]" />
                        </div>
                      )}
                    </div>
                  ) : (
                    <div className="w-16 h-16 rounded-full border-2 border-dashed border-gray-600 flex items-center justify-center">
                      <div className="text-gray-400 text-xs text-center">
                        No image
                      </div>
                    </div>
                  )}
                  <div className="flex-1 space-y-2">
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (file) {
                          handleFileSelect(file);
                          // Set a temporary profile image URL for form validation
                          handleInputChange("profileImage", "temp-upload");
                        }
                      }}
                      className="hidden"
                      id="profile-image-upload"
                      disabled={isUploading}
                    />
                    <label
                      htmlFor="profile-image-upload"
                      className={`cursor-pointer bg-gray-800 hover:bg-gray-700 text-white px-4 py-2 rounded-md text-sm font-mono transition-colors flex items-center gap-2 ${
                        isUploading ? "opacity-50 cursor-not-allowed" : ""
                      }`}
                    >
                      <Upload className="h-4 w-4" />
                      {isUploading
                        ? `Uploading... ${uploadProgress}%`
                        : "Upload Image"}
                    </label>
                    <button
                      type="button"
                      onClick={() => {
                        setSelectedFile(null);
                        setPreviewUrl("");
                        handleInputChange(
                          "profileImage",
                          "/Cars/UserImage.png",
                        );
                      }}
                      className="block w-full text-center bg-gray-700 hover:bg-gray-600 text-white px-4 py-2 rounded-md text-sm font-mono transition-colors"
                      disabled={isUploading}
                    >
                      Use Default
                    </button>
                  </div>
                </div>
                <div className="text-xs text-gray-400 font-mono">
                  Upload a profile picture or use the default. Images will be
                  stored on IPFS.
                </div>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-300 font-mono">
                  Connect Wallet
                </label>
                <div className="p-4">
                  {address ? (
                    <div className="flex items-center gap-3">
                      <div className="w-3 h-3 bg-[#00daa2] rounded-full"></div>
                      <div className="flex-1">
                        <div className="text-[#00daa2] text-sm font-mono font-medium">
                          Connected
                        </div>
                        <div className="text-gray-400 text-xs font-mono">
                          {address.slice(0, 6)}...{address.slice(-4)}
                        </div>
                      </div>
                    </div>
                  ) : (
                    <ConnectButton />
                  )}
                </div>
              </div>

              {error && (
                <div className="text-red-400 text-sm font-mono">{error}</div>
              )}

              <Button
                type="submit"
                disabled={!isFormValid || isLoading || isUploading}
                className="w-full bg-[#00daa2] text-black hover:bg-[#00daa2] font-medium font-mono disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isLoading ? "Creating Account..." : "Sign Up"}
              </Button>
            </form>
          )}

          <div className="text-center">
            <span className="text-gray-400 text-sm font-mono">
              Already have an account?{" "}
            </span>
            <button
              onClick={onSwitchToSignin}
              className="text-green-400 hover:text-green-300 text-sm font-medium font-mono"
            >
              Sign In
            </button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

Frontend/app/components/vehicle/TokenDet.content:
"use client";

import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/app/components/ui/card";
import { Button } from "@/app/components/ui/button";
import { Coins, TrendingUp, TrendingDown, ExternalLink, Share2, Copy, Check } from "lucide-react";

interface TokenDetailsProps {
    token: {
        address: string;
        ticker: string;
        price?: number;
        mcap?: number;
        change24h?: number;
    };
    vehicleName?: string;
    onSwapClick?: () => void;
}

export function TokenDetails({ token, vehicleName, onSwapClick }: TokenDetailsProps) {
    const [copied, setCopied] = useState(false);
    const isPositive = (token.change24h || 0) >= 0;

    const handleCopyAddress = async () => {
        await navigator.clipboard.writeText(token.address);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    };

    const handleShare = () => {
        const text = vehicleName 
            ? `Check out ${vehicleName} on @DRVN_VHCLS! Trading as $${token.ticker} ðŸš—\n\nSwap now: https://drvn.io/swap/${token.address}`
            : `Trade $${token.ticker} on @DRVN_VHCLS!\n\nhttps://drvn.io/swap/${token.address}`;

        if (navigator.share) {
            navigator.share({ text });
        } else {
            window.open(
                `https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}`,
                '_blank'
            );
        }
    };

    const handleDexScreener = () => {
        window.open(`https://dexscreener.com/base/${token.address}`, '_blank');
    };

    return (
        <Card className="bg-black/40 border-white/10 backdrop-blur-md">
            <CardHeader className="pb-2">
                <div className="flex items-center justify-between">
                    <CardTitle className="text-xl font-bold text-white flex items-center gap-2">
                        <Coins className="w-5 h-5 text-primary" />
                        ${token.ticker}
                    </CardTitle>
                    <Button
                        variant="ghost"
                        size="sm"
                        onClick={handleShare}
                        className="text-zinc-400 hover:text-white hover:bg-white/10"
                    >
                        <Share2 className="w-4 h-4" />
                    </Button>
                </div>
                <button
                    onClick={handleCopyAddress}
                    className="text-xs text-zinc-500 hover:text-zinc-300 font-mono flex items-center gap-1 mt-1 transition-colors"
                >
                    {token.address.slice(0, 8)}...{token.address.slice(-6)}
                    {copied ? (
                        <Check className="w-3 h-3 text-green-500" />
                    ) : (
                        <Copy className="w-3 h-3" />
                    )}
                </button>
            </CardHeader>
            <CardContent>
                <div className="grid grid-cols-3 gap-3 mb-6">
                    <div className="p-3 bg-zinc-900/50 rounded-lg border border-white/5">
                        <div className="text-xs text-zinc-400 mb-1">Price</div>
                        <div className="text-lg font-bold text-white">
                            ${token.price?.toFixed(6) || "0.000000"}
                        </div>
                    </div>
                    <div className="p-3 bg-zinc-900/50 rounded-lg border border-white/5">
                        <div className="text-xs text-zinc-400 mb-1">24h</div>
                        <div className={`text-lg font-bold flex items-center gap-1 ${isPositive ? "text-green-500" : "text-red-500"}`}>
                            {isPositive ? <TrendingUp className="w-4 h-4" /> : <TrendingDown className="w-4 h-4" />}
                            {Math.abs(token.change24h || 0).toFixed(1)}%
                        </div>
                    </div>
                    <div className="p-3 bg-zinc-900/50 rounded-lg border border-white/5">
                        <div className="text-xs text-zinc-400 mb-1">MCap</div>
                        <div className="text-lg font-bold text-white">
                            ${(token.mcap || 0) >= 1000000 
                                ? `${(token.mcap! / 1000000).toFixed(1)}M`
                                : (token.mcap || 0) >= 1000
                                    ? `${(token.mcap! / 1000).toFixed(1)}K`
                                    : token.mcap?.toLocaleString() || "0"
                            }
                        </div>
                    </div>
                </div>

                <div className="flex gap-3">
                    <Button 
                        onClick={onSwapClick}
                        className="flex-1 bg-primary hover:bg-primary/90 text-black font-bold"
                    >
                        <Coins className="w-4 h-4 mr-2" />
                        Swap ${token.ticker}
                    </Button>
                    <Button 
                        variant="outline" 
                        onClick={handleDexScreener}
                        className="border-white/10 hover:bg-white/5"
                    >
                        <ExternalLink className="w-4 h-4" />
                    </Button>
                </div>
            </CardContent>
        </Card>
    );
}

Frontend/hooks/use-mobile.ts.content:
"use client";

import * as React from "react";

const MOBILE_BREAKPOINT = 768;

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(
    undefined,
  );

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`);
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    };
    mql.addEventListener("change", onChange);
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    return () => mql.removeEventListener("change", onChange);
  }, []);

  return !!isMobile;
}

