"use client";

import { useState, useEffect } from "react";

interface ETHPriceDisplayProps {
  ethAmount: string;
  className?: string;
}

export default function ETHPriceDisplay({
  ethAmount,
  className = "",
}: ETHPriceDisplayProps) {
  const [usdPrice, setUsdPrice] = useState<number | null>(null);

  useEffect(() => {
    const fetchEthPrice = async () => {
      try {
        const response = await fetch("/api/eth-price");
        const data = await response.json();

        if (data.price) {
          setUsdPrice(data.price * parseFloat(ethAmount));
        } else {
          console.error("Failed to fetch ETH price:", data.error);
        }
      } catch (error) {
        console.error("Failed to fetch ETH price:", error);
      }
    };

    fetchEthPrice();
    // Refresh price every 60 seconds
    const interval = setInterval(fetchEthPrice, 60000);
    return () => clearInterval(interval);
  }, [ethAmount]);

  return (
    <span className={className}>
      {usdPrice
        ? `(â‰ˆ $${usdPrice.toLocaleString("en-US", { minimumFractionDigits: 2, maximumFractionDigits: 2 })} USD)`
        : "Loading..."}
    </span>
  );
}

Frontend/app/api/auth/check-user/route.t.content:
import { NextRequest, NextResponse } from "next/server";
import connectToDatabase from "../../../../lib/mongodb";
import User from "../../../../lib/models/User";

export async function POST(request: NextRequest) {
  try {
    const { walletAddress } = await request.json();

    if (!walletAddress) {
      return NextResponse.json(
        { error: "Wallet address is required" },
        { status: 400 },
      );
    }

    await connectToDatabase();

    // Check if user exists in database
    const existingUser = await User.findOne({
      walletAddress: walletAddress.toLowerCase(),
    });

    return NextResponse.json({
      exists: !!existingUser,
      user: existingUser
        ? {
            _id: existingUser._id,
            username: existingUser.username,
            firstName: existingUser.firstName
              ? existingUser.firstName.substring(0, 2) + "***"
              : "***",
            lastName: existingUser.lastName
              ? existingUser.lastName.substring(0, 2) + "***"
              : "***",
            email: existingUser.email
              ? existingUser.email.substring(0, 2) + "***"
              : "***",
            profileImage: existingUser.profileImage || "/Cars/UserImage.png",
            walletAddress: existingUser.walletAddress,
          }
        : null,
    });
  } catch (error) {
    console.error("Error checking user existence:", error);
    return NextResponse.json(
      { error: "Failed to check user existence" },
      { status: 500 },
    );
  }
}

Hardhat/deploy/00_deploy_your_contracts..content:
import { HardhatRuntimeEnvironment } from "hardhat/types";
import { DeployFunction } from "hardhat-deploy/types";
import { BigNumber } from "ethers";

/**
 * Deploys:
 *  - BSTRVault (multi-minter)
 *  - DRVNSteel (ID 0)
 *  - DRVNCarbon (ID 1)
 *  - DRVNTitanium (ID 2)
 * Wires vault â‡„ mints, sets USDC prices in 6â€‘decimals, and (optionally) reward config.
 */
const func: DeployFunction = async (hre: HardhatRuntimeEnvironment) => {
  const { deployments, getNamedAccounts, ethers, network } = hre;
  const { deploy, log } = deployments;
  const { deployer } = await getNamedAccounts();

  // Get current nonce to ensure we're using the right one
  const currentNonce = await network.provider.send("eth_getTransactionCount", [deployer, "latest"]);
  log(`ðŸ“Š Current nonce for deployer: ${parseInt(currentNonce, 16)}`);

  const toUSDC = (n: string | number): BigNumber => ethers.utils.parseUnits(String(n), 6); // 6â€‘decimals
  const toBSTR = (n: string | number): BigNumber => ethers.utils.parseUnits(String(n), 9); // 9â€‘decimals

  // -----------------------------
  // ðŸ”— Addresses (edit per chain)
  // -----------------------------
  const BSTR = "0xbfc5cd421bbc91a2ca976c4ab1754748634b7d41"; // 9â€‘dec ERC20
  const USDC = "0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913"; // 6â€‘dec ERC20 (same for all 3)

  // -----------------------------
