      websiteUrl: "https://drvn.io",
      socialLinks: { x: "https://x.com/drvn_platform", youtube: "https://youtube.com/@drvn" },
      bio: "DRVN is the next-generation automotive platform tokenizing real-world vehicles on Base.",
