    images: images,
    isUpgraded: vehicleData?.isUpgraded ?? false,
    location: "Los Angeles, CA",
    registryId: vehicleData?.registryId || "DRVN-0000",
    description: description,
    modifications: modifications,
    owner: {
      name: "gearhead_mike",
      username: "gearhead_mike",
      avatar: "/Cars/Avatar2.png",
    },
    followerCount: 1234,
    carToken: vehicleData?.carToken ? {
      address: vehicleData.carToken.address,
      ticker: vehicleData.carToken.ticker,
      price: 0.000045,
      change24h: 5.2,
      mcap: 45000,
    } : undefined,
    sponsorshipCollection: sponsorshipCollection || undefined,
    sponsors: sponsorshipCollection ? [
      { tokenId: "1", name: "BSTR", logo: "/Cars/BSTR-Logo-Official.png", holderAddress: "0xsp1..." },
      { tokenId: "2", name: "Base", logo: "/Cars/DCWhtV4.png", holderAddress: "0xsp2..." },
      { tokenId: "3", name: "Coinbase", logo: "/Cars/DRVNLaboLogoDrk.png", holderAddress: "0xsp3..." },
    ] : [],
    ownerAddress: currentUserAddress || "0x1234...5678",
  };

  const taggedPosts: TaggedPost[] = [
    {
      id: "p1",
      author: { username: "gearhead", avatar: "/Cars/Avatar2.png" },
      content: "Just spotted this beauty at the local meet! ðŸ”¥",
      image: "/Cars/GtrDemo2.png",
      timestamp: "2h ago",
      likes: 45,
    },
    {
      id: "p2",
      author: { username: "jdm_lover", avatar: "/Cars/Avatar2.png" },
      content: "The sound of that RB26 is pure music",
      timestamp: "5h ago",
      likes: 32,
    },
  ];

  const nftImage = vehicle.images.find(img => img.isNftImage)?.url || vehicle.images[0]?.url;
  const availableSlotsCount = vehicle.sponsorshipCollection 
    ? vehicle.sponsorshipCollection.maxSupply - (vehicle.sponsorshipCollection.mintedCount || 0)
    : 0;
  const claimedSlotIds = vehicle.sponsors.map(s => parseInt(s.tokenId));
  const availableSlotIds = vehicle.sponsorshipCollection
    ? Array.from({ length: vehicle.sponsorshipCollection.maxSupply }, (_, i) => i + 1)
        .filter(id => !claimedSlotIds.includes(id))
    : [];
  const vehicleDisplayName = vehicle.nickname || `${vehicle.year} ${vehicle.make} ${vehicle.model}`;

  const handleUpgradeComplete = () => {
    setShowUpgradeModal(false);
  };

  const handleCreateCollection = () => {
    setShowCreateCollectionModal(true);
  };

  const handleCollectionCreated = (collection: {
    contractAddress: string;
    maxSupply: number;
    mintPrice: number;
    stage: number;
  }) => {
    setSponsorshipCollection({
      contractAddress: collection.contractAddress,
      maxSupply: collection.maxSupply,
      mintPrice: collection.mintPrice,
      mintedCount: 0,
    });
    setShowCreateCollectionModal(false);
  };

  const handleShareCollection = () => {
    const text = `Looking for sponsors for my ${vehicleDisplayName}! Mint a sponsorship NFT for $${sponsorshipCollection?.mintPrice} USDC on @DRVN_VHCLS`;
    addToast({
      type: "info",
      title: "Share",
      message: "Opening share options...",
    });
    window.open(
      `https://warpcast.com/~/compose?text=${encodeURIComponent(text)}`,
      '_blank'
    );
  };

  const handleSponsorSlotClick = (slotId?: number) => {
    if (isOwner) {
      setShowSponsorModal(true);
    } else {
      setSelectedSlot(slotId);
      setShowMintModal(true);
    }
  };

  const handleMintSuccess = (result: { tokenId: number; transactionHash: string }) => {
    setShowMintModal(false);
    setShowProfileEditor(true);
    setSelectedSponsor({
      tokenId: String(result.tokenId),
      name: "",
      holderAddress: currentUserAddress || "",
    });
  };

  const handleManageSponsor = (sponsor: Sponsor) => {
    setSelectedSponsor(sponsor);
    setShowProfileEditor(true);
  };

  const handleViewSponsor = (sponsor: Sponsor) => {
    const fullSponsor: FullSponsor = {
      tokenId: sponsor.tokenId,
      name: sponsor.name,
      logo: sponsor.logo,
      holderAddress: sponsor.holderAddress,
      websiteUrl: mockSponsorDetails[sponsor.tokenId]?.websiteUrl,
      promoUrl: mockSponsorDetails[sponsor.tokenId]?.promoUrl,
      socialLinks: mockSponsorDetails[sponsor.tokenId]?.socialLinks,
      bio: mockSponsorDetails[sponsor.tokenId]?.bio,
      gallery: mockSponsorDetails[sponsor.tokenId]?.gallery,
      openSeaUrl: mockSponsorDetails[sponsor.tokenId]?.openSeaUrl,
    };
    setViewingSponsor(fullSponsor);
    setShowSponsorDetailsModal(true);
  };

  const handleSaveProfile = () => {
    setShowProfileEditor(false);
    setSelectedSponsor(null);
  };

  const handleShare = () => {
    const text = vehicle.carToken
      ? `Check out ${vehicleDisplayName} on @DRVN_VHCLS! Trading as $${vehicle.carToken.ticker} ðŸš—`
      : `Check out ${vehicleDisplayName} on @DRVN_VHCLS! ðŸš—`;

    window.open(
      `https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}`,
      '_blank'
    );
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (!files) return;

    Array.from(files).forEach(file => {
      const reader = new FileReader();
      reader.onloadend = () => {
        const url = reader.result as string;
        const newImages = [...images, { url, isNftImage: false }];
        handleImageChange(newImages);
      };
      reader.readAsDataURL(file);
    });
  };

  const handleRemoveImage = (index: number) => {
    if (images[index].isNftImage) return;
    const newImages = images.filter((_, i) => i !== index);
    handleImageChange(newImages);
  };

  const handleSetNftImage = (index: number) => {
    const newImages = images.map((img, i) => ({
      ...img,
      isNftImage: i === index
    }));
    handleImageChange(newImages);
  };

  const handleAddMod = () => {
    if (newMod.trim()) {
      setModifications(prev => [...prev, newMod.trim()]);
      setNewMod("");
    }
  };

  const handleRemoveMod = (index: number) => {
    setModifications(prev => prev.filter((_, i) => i !== index));
  };

  const handleSaveDescription = () => {
    setIsEditingDescription(false);
    onUpdate?.({ description });
  };

  const handleSaveMods = () => {
    setIsEditingMods(false);
    onUpdate?.({ modifications });
  };

  const handleImageChange = (newImages: VehicleImage[]) => {
    setImages(newImages);
    onUpdate?.({ images: newImages });
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <Button
          variant="ghost"
          onClick={onBack}
          className="text-zinc-400 hover:text-white hover:bg-white/10 -ml-2"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back
        </Button>

        <div className="flex items-center gap-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowCreatePostModal(true)}
            className="text-primary hover:text-primary hover:bg-primary/10"
          >
            <PenSquare className="w-4 h-4 mr-2" />
            Post
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleShare}
            className="text-zinc-400 hover:text-white hover:bg-white/10"
          >
            <Share2 className="w-4 h-4 mr-2" />
            Share
          </Button>
        </div>
      </div>

      <SwipeableGallery images={vehicle.images} />

      {isOwner && (
        <Card className="bg-black/40 border-white/10 backdrop-blur-md">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-zinc-400 flex items-center gap-2">
              <ImagePlus className="w-4 h-4" />
              Manage Gallery
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-3">
              {images.map((img, index) => (
                <div key={index} className="relative group">
                  <div className="w-20 h-20 rounded-lg overflow-hidden border border-white/10">
                    <Image
                      src={img.url}
                      alt={`Image ${index + 1}`}
                      width={80}
                      height={80}
                      className="object-cover w-full h-full"
                    />
                  </div>
                  {img.isNftImage && (
                    <div className="absolute -top-1 -left-1 bg-primary text-black text-[8px] font-bold px-1 rounded">
                      NFT
                    </div>
                  )}
                  {!img.isNftImage && (
                    <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-1">
                      <button
                        onClick={() => handleSetNftImage(index)}
                        className="p-1 bg-primary/80 rounded text-black text-[10px] hover:bg-primary"
                        title="Set as NFT image"
                      >
                        NFT
                      </button>
                      <button
                        onClick={() => handleRemoveImage(index)}
                        className="p-1 bg-red-500/80 rounded text-white hover:bg-red-500"
                        title="Remove"
                      >
                        <X className="w-3 h-3" />
                      </button>
                    </div>
                  )}
                </div>
              ))}

              <button
                onClick={() => fileInputRef.current?.click()}
                className="w-20 h-20 rounded-lg border-2 border-dashed border-white/20 flex items-center justify-center text-zinc-500 hover:border-primary/50 hover:text-primary transition-colors"
              >
                <Plus className="w-6 h-6" />
              </button>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                multiple
                className="hidden"
                onChange={handleImageUpload}
              />
            </div>
            <p className="text-xs text-zinc-500 mt-2">
              Click an image to set as NFT image or remove. NFT image updates on-chain metadata.
            </p>
          </CardContent>
        </Card>
      )}

      {/* Vehicle Title Module - Combined Title + Token Details */}
      <VehicleTitleModule
        vehicle={{
          nickname: vehicle.nickname,
          make: vehicle.make,
          model: vehicle.model,
          year: vehicle.year,
          nftImage: nftImage,
          isUpgraded: vehicle.isUpgraded,
          registryId: vehicle.registryId,
          carToken: vehicle.carToken,
          socialLinks: vehicle.socialLinks,
        }}
        owner={vehicle.owner ? {
          name: vehicle.owner.name,
          username: vehicle.owner.username,
          avatar: vehicle.owner.avatar,
          walletAddress: vehicle.ownerAddress,
        } : undefined}
        isOwner={isOwner}
        onOwnerClick={() => {
          window.location.href = `/?page=garage&address=${vehicle.ownerAddress}`;
        }}
        onSwapClick={() => setShowSwapModal(true)}
        onEditSocialLinks={() => {
          addToast({
            type: "info",
            title: "Coming Soon",
            message: "Social links editing will be available soon",
          });
        }}
      />

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="space-y-6">

          <Card className="bg-black/40 border-white/10 backdrop-blur-md">
            <CardHeader className="pb-2">
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg font-bold text-white flex items-center gap-2">
                  <Edit2 className="w-4 h-4 text-primary" />
                  Description
                </CardTitle>
                {isOwner && !isEditingDescription && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setIsEditingDescription(true)}
                    className="text-zinc-400 hover:text-white"
                  >
                    <Edit2 className="w-4 h-4" />
                  </Button>
                )}
              </div>
            </CardHeader>
            <CardContent>
              {isEditingDescription ? (
                <div className="space-y-3">
                  <Textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="bg-zinc-900/50 border-white/10 text-white min-h-[100px]"
                    placeholder="Describe your vehicle..."
                  />
                  <div className="flex gap-2">
                    <Button
                      onClick={handleSaveDescription}
                      size="sm"
                      className="bg-primary hover:bg-primary/90 text-black"
                    >
                      <Save className="w-4 h-4 mr-1" />
                      Save
                    </Button>
                    <Button
                      onClick={() => setIsEditingDescription(false)}
                      variant="ghost"
                      size="sm"
                      className="text-zinc-400"
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              ) : (
                <p className="text-sm text-zinc-300 leading-relaxed">
                  {description || "No description yet."}
                </p>
              )}
            </CardContent>
          </Card>

          <Card className="bg-black/40 border-white/10 backdrop-blur-md">
            <CardHeader className="pb-2">
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg font-bold text-white flex items-center gap-2">
                  <Wrench className="w-4 h-4 text-primary" />
                  Modifications
                </CardTitle>
                {isOwner && !isEditingMods && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setIsEditingMods(true)}
                    className="text-zinc-400 hover:text-white"
                  >
                    <Edit2 className="w-4 h-4" />
                  </Button>
                )}
              </div>
            </CardHeader>
            <CardContent>
              {isEditingMods ? (
                <div className="space-y-3">
                  <div className="flex flex-wrap gap-2">
                    {modifications.map((mod, index) => (
                      <div
                        key={index}
                        className="flex items-center gap-1 bg-zinc-800 px-3 py-1.5 rounded-full text-sm text-white"
                      >
                        <span>{mod}</span>
                        <button
                          onClick={() => handleRemoveMod(index)}
                          className="text-red-400 hover:text-red-300 ml-1"
                        >
                          <X className="w-3 h-3" />
                        </button>
                      </div>
                    ))}
                  </div>
                  <div className="flex gap-2">
                    <Input
                      value={newMod}
                      onChange={(e) => setNewMod(e.target.value)}
                      placeholder="Add modification..."
                      className="bg-zinc-900/50 border-white/10 text-white flex-1"
                      onKeyDown={(e) => e.key === "Enter" && handleAddMod()}
                    />
                    <Button
                      onClick={handleAddMod}
                      size="sm"
                      variant="outline"
                      className="border-white/10"
                    >
                      <Plus className="w-4 h-4" />
                    </Button>
                  </div>
                  <div className="flex gap-2">
                    <Button
                      onClick={handleSaveMods}
                      size="sm"
                      className="bg-primary hover:bg-primary/90 text-black"
                    >
                      <Save className="w-4 h-4 mr-1" />
                      Save
                    </Button>
                    <Button
                      onClick={() => setIsEditingMods(false)}
                      variant="ghost"
                      size="sm"
                      className="text-zinc-400"
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              ) : (
                <div className="flex flex-wrap gap-2">
                  {modifications.length > 0 ? (
                    modifications.map((mod, index) => (
                      <span
                        key={index}
                        className="bg-zinc-800/50 px-3 py-1.5 rounded-full text-sm text-zinc-300 border border-white/5"
                      >
                        {mod}
                      </span>
                    ))
                  ) : (
                    <p className="text-sm text-zinc-500">No modifications listed.</p>
                  )}
                </div>
              )}
            </CardContent>
          </Card>

          {vehicle.isUpgraded && (
            <SponsorshipModule
              vehicleName={vehicle.nickname || vehicle.model}
              vehicleTicker={vehicle.carToken?.ticker}
              isOwner={isOwner}
              isUpgraded={vehicle.isUpgraded}
              connectedAddress={currentUserAddress}
              sponsorshipCollection={vehicle.sponsorshipCollection}
              sponsors={vehicle.sponsors}
              onSponsorClick={handleSponsorSlotClick}
              onCreateCollection={handleCreateCollection}
              onShare={handleShareCollection}
              onManageSponsor={handleManageSponsor}
              onViewSponsor={handleViewSponsor}
            />
          )}

          {!vehicle.isUpgraded && isOwner && (
            <div className="relative overflow-hidden rounded-2xl border border-primary/30 bg-gradient-to-br from-primary/10 via-yellow-500/5 to-orange-500/5">
              <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_top_right,_var(--tw-gradient-stops))] from-primary/10 via-transparent to-transparent" />

              <div className="relative p-6">
                <div className="flex items-start justify-between mb-6">
                  <div className="flex items-center gap-3">
                    <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-primary to-yellow-500 flex items-center justify-center shadow-lg shadow-primary/20">
                      <Zap className="w-6 h-6 text-white" />
                    </div>
                    <div>
                      <h3 className="text-xl font-bold text-white">Upgrade to Monetize</h3>
                      <p className="text-sm text-primary/80">Unlock your vehicle&apos;s earning potential</p>
                    </div>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-3 mb-6">
                  <div className="bg-black/40 backdrop-blur-sm rounded-xl p-4 border border-white/5">
                    <Coins className="w-5 h-5 text-primary mb-2" />
                    <p className="text-sm font-medium text-white">ERC20 Token</p>
                    <p className="text-xs text-zinc-400">Tradeable car token</p>
                  </div>
                  <div className="bg-black/40 backdrop-blur-sm rounded-xl p-4 border border-white/5">
                    <Users className="w-5 h-5 text-yellow-500 mb-2" />
                    <p className="text-sm font-medium text-white">14 Sponsor Slots</p>
                    <p className="text-xs text-zinc-400">NFT sponsorships</p>
                  </div>
                  <div className="bg-black/40 backdrop-blur-sm rounded-xl p-4 border border-white/5">
                    <TrendingUp className="w-5 h-5 text-green-500 mb-2" />
                    <p className="text-sm font-medium text-white">Trading Revenue</p>
                    <p className="text-xs text-zinc-400">Earn from volume</p>
                  </div>
                  <div className="bg-black/40 backdrop-blur-sm rounded-xl p-4 border border-white/5">
                    <Shield className="w-5 h-5 text-blue-500 mb-2" />
                    <p className="text-sm font-medium text-white">10% Reserve</p>
                    <p className="text-xs text-zinc-400">Creator allocation</p>
                  </div>
                </div>

                <div className="flex items-center justify-between p-4 rounded-xl bg-black/30 border border-white/5 mb-6">
                  <div>
                    <p className="text-sm text-zinc-400">Upgrade Cost</p>
                    <div className="flex items-baseline gap-2">
                      <span className="text-3xl font-bold text-white">$5</span>
                      <span className="text-sm text-zinc-400">USDC</span>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-zinc-400">Platform Fee</p>
                    <p className="text-lg font-bold text-yellow-500">5% token supply</p>
                  </div>
                </div>

                <Button 
                  onClick={() => setShowUpgradeModal(true)}
                  className="w-full h-14 bg-gradient-to-r from-primary to-yellow-500 hover:from-primary/90 hover:to-yellow-400 text-black font-bold text-lg shadow-lg shadow-primary/20 transition-all hover:shadow-xl hover:shadow-primary/30"
                >
                  <Zap className="w-5 h-5 mr-2" />
                  Upgrade Vehicle
                </Button>
              </div>
            </div>
          )}
        </div>

        <div className="space-y-6">
          <Card className="bg-black/40 border-white/10 backdrop-blur-md">
            <CardHeader className="pb-2">
              <CardTitle className="text-lg font-bold text-white flex items-center gap-2">
                <Users className="w-5 h-5 text-primary" />
                Tagged Posts
              </CardTitle>
              <p className="text-xs text-zinc-400">
                Posts featuring this vehicle
              </p>
            </CardHeader>
            <CardContent>
              {taggedPosts.length > 0 ? (
                <div className="space-y-4 max-h-[400px] overflow-y-auto pr-2">
                  {taggedPosts.map((post) => (
                    <div key={post.id} className="p-4 bg-white/5 rounded-lg border border-white/10">
                      <div className="flex items-center gap-3 mb-3">
                        <div className="w-8 h-8 rounded-full bg-zinc-700 overflow-hidden relative">
                          <Image
                            src={post.author.avatar}
                            alt={post.author.username}
                            fill
                            className="object-cover"
                          />
                        </div>
                        <div>
                          <p className="text-sm font-medium text-white">@{post.author.username}</p>
                          <p className="text-xs text-zinc-500">{post.timestamp}</p>
                        </div>
                      </div>
                      <p className="text-sm text-zinc-300 mb-3">{post.content}</p>
                      {post.image && (
                        <div className="relative aspect-video rounded-lg overflow-hidden mb-3">
                          <Image
                            src={post.image}
                            alt="Post image"
                            fill
                            className="object-cover"
                          />
                        </div>
                      )}
                      <div className="flex items-center gap-4 text-xs text-zinc-500">
                        <span>â¤ï¸ {post.likes}</span>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <Users className="w-10 h-10 text-zinc-600 mx-auto mb-3" />
                  <p className="text-zinc-400">No posts tagged with this vehicle yet</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>

      <BuySponsorshipModal
        isOpen={showSponsorModal}
        onClose={() => setShowSponsorModal(false)}
        vehicleId={vehicle._id}
        vehicleName={vehicle.nickname || vehicle.model}
        vehicleImage={nftImage || ""}
        mintPrice={vehicle.sponsorshipCollection?.mintPrice || 50}
        availableSlots={availableSlotsCount}
        onPurchaseSuccess={(result) => {
          console.log("Sponsorship purchased:", result);
          setShowSponsorModal(false);
        }}
      />

      <UpgradeVehicleModal
        isOpen={showUpgradeModal}
        onClose={() => setShowUpgradeModal(false)}
        vehicleId={vehicle._id}
        vehicleName={vehicleDisplayName}
        vehicleImage={nftImage}
        onUpgradeComplete={() => handleUpgradeComplete()}
        connectedPlatforms={connectedPlatforms}
      />

      {vehicle.carToken && (
        <TokenSwapModal
          isOpen={showSwapModal}
          onClose={() => setShowSwapModal(false)}
          token={{
            address: vehicle.carToken.address,
            ticker: vehicle.carToken.ticker,
          }}
        />
      )}

      {vehicle.carToken && (
        <CreateSponsorshipCollectionModal
          isOpen={showCreateCollectionModal}
          onClose={() => setShowCreateCollectionModal(false)}
          vehicleId={vehicle._id}
          vehicleName={vehicleDisplayName}
          vehicleTicker={vehicle.carToken.ticker}
          onCreationComplete={handleCollectionCreated}
          connectedPlatforms={connectedPlatforms}
        />
      )}

      {vehicle.sponsorshipCollection && (
        <MintSponsorshipModal
          isOpen={showMintModal}
          onClose={() => {
            setShowMintModal(false);
            setSelectedSlot(undefined);
          }}
          vehicleId={vehicle._id}
          vehicleName={vehicleDisplayName}
          vehicleImage={nftImage}
          mintPrice={vehicle.sponsorshipCollection.mintPrice}
          availableSlots={availableSlotIds}
          maxSupply={vehicle.sponsorshipCollection.maxSupply}
          benefits={[
            "Logo placement on vehicle wrap",
            "Social media mentions",
            "Cross-promotion opportunities",
            "Branded content features"
          ]}
          onMintSuccess={handleMintSuccess}
        />
      )}

      {selectedSponsor && (
        <SponsorProfileEditorModal
          isOpen={showProfileEditor}
          onClose={() => {
            setShowProfileEditor(false);
            setSelectedSponsor(null);
          }}
          sponsor={{
            tokenId: selectedSponsor.tokenId,
            name: selectedSponsor.name || "",
            logo: selectedSponsor.logo,
            holderAddress: selectedSponsor.holderAddress,
          }}
          vehicleName={vehicleDisplayName}
          onSave={handleSaveProfile}
        />
      )}

      <SponsorDetailsModal
        isOpen={showSponsorDetailsModal}
        onClose={() => {
          setShowSponsorDetailsModal(false);
          setViewingSponsor(null);
        }}
        sponsor={viewingSponsor}
        vehicleName={vehicleDisplayName}
      />

      <CreatePostModal
        isOpen={showCreatePostModal}
        onClose={() => setShowCreatePostModal(false)}
        userId={currentUserAddress || ""}
        currentUser={{
          name: vehicle.owner?.name || "User",
          username: vehicle.owner?.username || "user",
          avatar: vehicle.owner?.avatar || "/Cars/Avatar2.png",
        }}
        registeredVehicles={[{
          id: vehicle._id,
          nickname: vehicle.nickname,
          make: vehicle.make,
          model: vehicle.model,
        }]}
        initialTaggedVehicles={[vehicle._id]}
        sponsors={vehicle.sponsors.map(s => ({
          id: s.tokenId,
          name: s.name || `Sponsor #${s.tokenId}`,
          logo: s.logo,
        }))}
        connectedPlatforms={connectedPlatforms as Array<"farcaster" | "base" | "x">}
        initialCrossPostSettings={{
          farcaster: connectedPlatforms.includes("farcaster"),
          base: connectedPlatforms.includes("base"),
          x: connectedPlatforms.includes("x"),
        }}
      />
    </div>
  );
}

Frontend/app/components/modals/VehicleDe.content:
"use client";

import { useState } from "react";
import { Dialog, DialogContent, DialogTitle, DialogDescription } from "@/app/components/ui/dialog";
import { VisuallyHidden } from "@radix-ui/react-visually-hidden";
import { Card, CardContent, CardHeader, CardTitle } from "@/app/components/ui/card";
import { Button } from "@/app/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/app/components/ui/avatar";
import { Badge } from "@/app/components/ui/badge";
import { 
  X, 
  ChevronLeft, 
  ChevronRight,
  Trophy,
  TrendingUp,
  TrendingDown,
  BarChart3,
  Users,
  Plus,
  ExternalLink,
  Share2,
  Scale
} from "lucide-react";
import Image from "next/image";
import { cn } from "@/lib/utils";
import { BuySponsorshipModal } from "./BuySponsorshipModal";
import { SponsorDetailsModal } from "./SponsorDetailsModal";
import { type Vehicle, type Sponsor, formatCurrency } from "@/app/data/vehicleData";

interface TaggedPost {
  id: string;
  author: {
    username: string;
    avatar: string;
  };
  content: string;
  image?: string;
  timestamp: string;
  likes: number;
}

interface VehicleDetailModalProps {
  isOpen: boolean;
  onClose: () => void;
  vehicle: Vehicle | null;
  isOwner?: boolean;
}

export function VehicleDetailModal({ isOpen, onClose, vehicle, isOwner = false }: VehicleDetailModalProps) {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showSponsorModal, setShowSponsorModal] = useState(false);
  const [selectedSponsor, setSelectedSponsor] = useState<Sponsor | null>(null);
  const [showSponsorDetails, setShowSponsorDetails] = useState(false);

  if (!vehicle) return null;

  const handleSponsorClick = (sponsor: Sponsor) => {
    setSelectedSponsor(sponsor);
    setShowSponsorDetails(true);
  };

  // Mock tagged posts - would come from API
  const taggedPosts: TaggedPost[] = [
    {
      id: "p1",
      author: { username: "gearhead", avatar: "/avatars/default.png" },
      content: "Just spotted this beauty at the local meet! ðŸ”¥",
      image: vehicle.images[0]?.url,
      timestamp: "2h ago",
      likes: 45,
    },
    {
      id: "p2",
      author: { username: "jdm_lover", avatar: "/avatars/default.png" },
      content: "The sound of that engine is pure music",
      timestamp: "5h ago",
      likes: 32,
    },
  ];

  const nftImage = vehicle.images.find(img => img.isNftImage)?.url || vehicle.images[0]?.url;
  const mintedCount = vehicle.sponsorshipCollection?.mintedCount || vehicle.sponsors.length;
  const availableSlots = vehicle.sponsorshipCollection 
    ? vehicle.sponsorshipCollection.maxSupply - mintedCount
    : 0;

  // Gallery navigation
  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % vehicle.images.length);
  };
  const prevImage = () => {
    setCurrentImageIndex((prev) => (prev - 1 + vehicle.images.length) % vehicle.images.length);
  };

  // Build sponsor slots array for 7x2 grid
  const sponsorSlots = vehicle.sponsorshipCollection 
    ? Array.from({ length: vehicle.sponsorshipCollection.maxSupply }, (_, i) => {
        const sponsor = vehicle.sponsors.find(s => parseInt(s.tokenId) === i + 1);
        return { id: i + 1, sponsor, isAvailable: !sponsor };
      })
    : [];

  return (
    <>
      <Dialog open={isOpen} onOpenChange={(open) => !open && onClose()}>
        <DialogContent 
          className="max-w-6xl w-[95vw] h-[90vh] p-0 bg-white/[0.03] backdrop-blur-2xl border border-white/10 overflow-hidden shadow-2xl shadow-black/50"
          aria-describedby="vehicle-detail-description"
          hideCloseButton
        >
          {/* Accessibility: Hidden title and description for screen readers */}
          <VisuallyHidden>
            <DialogTitle>
              {vehicle.nickname || `${vehicle.year} ${vehicle.make} ${vehicle.model}`} Details
            </DialogTitle>
            <DialogDescription id="vehicle-detail-description">
              View details, images, sponsorship information, and token data for {vehicle.nickname || `${vehicle.year} ${vehicle.make} ${vehicle.model}`}
            </DialogDescription>
          </VisuallyHidden>

          {/* Header - Glassmorphic */}
          <div className="sticky top-0 z-10 flex items-center justify-between p-4 border-b border-white/5 bg-white/5 backdrop-blur-xl">
            <div className="flex items-center gap-3">
              <h2 className="text-xl font-bold text-white font-mono">
                {vehicle.nickname || `${vehicle.year} ${vehicle.model}`}
              </h2>
              {vehicle.carToken && (
                <Badge className="bg-primary/20 text-primary border-primary/30">
                  ${vehicle.carToken.ticker}
                </Badge>
              )}
            </div>
            <div className="flex items-center gap-2">
              <Button variant="ghost" size="icon" className="text-zinc-400 hover:text-white hover:bg-white/10 rounded-full transition-all">
                <Share2 className="w-5 h-5" />
              </Button>
              <Button variant="ghost" size="icon" onClick={onClose} className="text-zinc-400 hover:text-white hover:bg-white/10 rounded-full transition-all">
                <X className="w-5 h-5" />
              </Button>
            </div>
          </div>

          {/* Scrollable Content */}
          <div className="overflow-y-auto h-[calc(90vh-65px)] p-4 md:p-6 sleek-scrollbar">
            {/* Module 1: Swipeable Gallery */}
            <div className="relative w-full aspect-video md:aspect-[21/9] rounded-2xl overflow-hidden bg-black/40 mb-6 group border border-white/10 shadow-xl">
              {vehicle.images.length > 0 ? (
                <>
                  <Image
                    src={vehicle.images[currentImageIndex].url}
                    alt={`${vehicle.nickname || vehicle.model} - Image ${currentImageIndex + 1}`}
                    fill
                    className="object-cover"
                    priority
                  />

                  {/* NFT Badge */}
                  {vehicle.images[currentImageIndex].isNftImage && (
                    <div className="absolute top-4 right-4 bg-primary/90 backdrop-blur-sm text-black text-xs font-bold px-3 py-1.5 rounded-full shadow-lg">
                      NFT Image
                    </div>
                  )}

                  {/* Navigation Arrows - Sleek glassmorphic */}
                  {vehicle.images.length > 1 && (
                    <>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={prevImage}
                        className="absolute left-3 top-1/2 -translate-y-1/2 bg-white/10 backdrop-blur-md hover:bg-white/20 text-white border border-white/20 rounded-full opacity-0 group-hover:opacity-100 transition-all shadow-lg"
                      >
                        <ChevronLeft className="w-5 h-5" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={nextImage}
                        className="absolute right-3 top-1/2 -translate-y-1/2 bg-white/10 backdrop-blur-md hover:bg-white/20 text-white border border-white/20 rounded-full opacity-0 group-hover:opacity-100 transition-all shadow-lg"
                      >
                        <ChevronRight className="w-5 h-5" />
                      </Button>
                    </>
                  )}

                  {/* Image Counter - Glassmorphic */}
                  <div className="absolute top-4 left-4 bg-black/40 backdrop-blur-md px-3 py-1.5 rounded-full text-xs text-white/90 font-mono border border-white/10">
                    {currentImageIndex + 1} / {vehicle.images.length}
                  </div>

                  {/* Dot Navigation - Sleek */}
                  {vehicle.images.length > 1 && (
                    <div className="absolute bottom-4 left-1/2 -translate-x-1/2 flex gap-2 bg-black/30 backdrop-blur-md px-3 py-2 rounded-full border border-white/10">
                      {vehicle.images.map((_, idx) => (
                        <button
                          key={idx}
                          onClick={() => setCurrentImageIndex(idx)}
                          className={cn(
                            "h-1.5 rounded-full transition-all duration-300",
                            idx === currentImageIndex ? "bg-white w-6" : "bg-white/40 hover:bg-white/70 w-1.5"
                          )}
                        />
                      ))}
                    </div>
                  )}
                </>
              ) : (
                <div className="w-full h-full flex items-center justify-center text-zinc-600">
                  No Images Available
                </div>
              )}
            </div>

            {/* Two Column Layout - Desktop: side by side, Mobile: stacked */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Left Column */}
              <div className="space-y-6">
                {/* Module 2: Car Profile Card - Clean Layout */}
                <Card className="bg-white/5 backdrop-blur-lg border-white/10 rounded-2xl shadow-xl overflow-hidden">
                  <CardContent className="p-0">
                    {/* Vehicle Identity Section */}
                    <div className="p-5 md:p-6">
                      <div className="flex items-start gap-5">
                        {/* Large Vehicle Avatar */}
                        <div className="flex-shrink-0">
                          <Avatar className="w-28 h-28 md:w-32 md:h-32 border-2 border-white/10 rounded-2xl shadow-xl">
                            <AvatarImage src={nftImage} className="object-cover" />
                            <AvatarFallback className="bg-white/5 backdrop-blur rounded-2xl text-lg font-bold text-white/50">
                              {vehicle.year}
                            </AvatarFallback>
                          </Avatar>
                        </div>

                        {/* Vehicle Info - Clean vertical stack */}
                        <div className="flex-grow min-w-0 space-y-3">
                          {/* Name & Token */}
                          <div>
                            <div className="flex items-center gap-2 flex-wrap">
                              <h3 className="text-2xl font-bold text-white truncate">
                                {vehicle.nickname || vehicle.model}
                              </h3>
                              {vehicle.isUpgraded && vehicle.carToken && (
                                <Badge className="bg-primary/20 text-primary border border-primary/30 text-xs">
                                  ${vehicle.carToken.ticker}
                                </Badge>
                              )}
                            </div>
                            <p className="text-sm text-zinc-400 mt-1">
                              {vehicle.year} {vehicle.make} {vehicle.model}
                            </p>
                          </div>

                          {/* Registry ID */}
                          {vehicle.registryId && (
                            <Badge variant="secondary" className="bg-white/5 backdrop-blur-sm text-white/60 text-xs border border-white/10 w-fit">
                              Registry #{vehicle.registryId}
                            </Badge>
                          )}

                          {/* Location */}
                          {vehicle.location && (
                            <p className="text-sm text-zinc-500 flex items-center gap-1.5">
                              <span className="text-base">ðŸ“</span> {vehicle.location}
                            </p>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Owner Section - Separate row */}
                    {vehicle.owner && (
                      <div className="px-5 md:px-6 py-4 border-t border-white/5 bg-white/[0.02]">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-3">
                            <Avatar className="w-10 h-10 border border-white/10">
                              <AvatarImage src={vehicle.owner.avatar} />
                              <AvatarFallback className="bg-white/10 text-sm">{vehicle.owner.name[0]}</AvatarFallback>
                            </Avatar>
                            <div>
                              <p className="text-sm font-medium text-white">{vehicle.owner.name}</p>
                              <p className="text-xs text-zinc-500">@{vehicle.owner.username || vehicle.owner.name}</p>
                            </div>
                          </div>
                          <Button variant="outline" size="sm" className="border-white/10 hover:bg-white/5 text-xs rounded-full">
                            Follow
                          </Button>
                        </div>
                      </div>
                    )}

                    {/* Sponsors Preview Section - Separate row */}
                    {vehicle.isUpgraded && vehicle.sponsorshipCollection && vehicle.sponsors.length > 0 && (
                      <div className="px-5 md:px-6 py-4 border-t border-white/5">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center gap-3">
                            <span className="text-xs text-zinc-500 uppercase tracking-wide">Sponsors</span>
                            <div className="flex -space-x-2">
                              {vehicle.sponsors.slice(0, 4).map((sponsor, i) => (
                                <div
                                  key={sponsor.tokenId}
                                  className="w-7 h-7 rounded-full bg-white/10 border border-white/20 flex items-center justify-center overflow-hidden"
                                  style={{ zIndex: 4 - i }}
                                >
                                  {sponsor.logo ? (
                                    <Image src={sponsor.logo} alt={sponsor.name || ""} width={28} height={28} className="object-cover" />
                                  ) : (
                                    <span className="text-[9px] text-white/70 font-medium">
                                      {sponsor.name?.substring(0, 2) || `S${i + 1}`}
                                    </span>
                                  )}
                                </div>
                              ))}
                              {vehicle.sponsors.length > 4 && (
                                <div className="w-7 h-7 rounded-full bg-white/10 border border-white/20 flex items-center justify-center">
                                  <span className="text-[9px] text-white/70 font-medium">+{vehicle.sponsors.length - 4}</span>
                                </div>
                              )}
                            </div>
                          </div>
                          {availableSlots > 0 && (
                            <span className="text-xs text-primary/80">{availableSlots} slots available</span>
                          )}
                        </div>
                      </div>
                    )}
                  </CardContent>
                </Card>

                {/* Module 3: Sponsorship Module (7x2 Grid) */}
                {vehicle.isUpgraded && vehicle.sponsorshipCollection && (
                  <Card className="bg-white/5 backdrop-blur-lg border-white/10 rounded-2xl shadow-xl">
                    <CardHeader className="pb-2">
                      <div className="flex items-center justify-between">
                        <CardTitle className="text-lg font-bold text-white flex items-center gap-2">
                          <Trophy className="w-5 h-5 text-yellow-500" />
                          {isOwner ? "Sponsorship Slots" : "Sponsors"}
                        </CardTitle>
                        {isOwner ? (
                          <Badge className="bg-yellow-500/20 backdrop-blur-sm text-yellow-400 border border-yellow-500/30 shadow-lg">
                            {availableSlots} Available
                          </Badge>
                        ) : (
                          <Button
                            size="sm"
                            className="bg-yellow-500/90 hover:bg-yellow-500 text-black font-bold rounded-full px-4 shadow-lg shadow-yellow-500/20 transition-all hover:shadow-yellow-500/40"
                            onClick={() => setShowSponsorModal(true)}
                            disabled={availableSlots === 0}
                          >
                            {availableSlots === 0 ? "Sold Out" : `Become a Sponsor`}
                          </Button>
                        )}
                      </div>
                      <div className="flex items-center justify-between text-xs text-zinc-400 mt-2">
                        <span>{mintedCount}/{vehicle.sponsorshipCollection.maxSupply} claimed</span>
                        <span className="text-white font-semibold">{vehicle.sponsorshipCollection.mintPrice} USDC per slot</span>
                      </div>
                      {/* Progress Bar - Sleek */}
                      <div className="w-full h-1 rounded-full bg-white/5 overflow-hidden mt-2">
                        <div
                          className="h-full bg-gradient-to-r from-yellow-500 to-yellow-400 transition-all shadow-sm shadow-yellow-500/50"
                          style={{ width: `${(mintedCount / vehicle.sponsorshipCollection.maxSupply) * 100}%` }}
                        />
                      </div>
                    </CardHeader>
                    <CardContent>
                      {/* 7x2 Grid */}
                      <div className="grid grid-cols-7 gap-2">
                        {sponsorSlots.map((slot) => (
                          <div key={slot.id} className="aspect-square">
                            {slot.isAvailable ? (
                              <button
                                onClick={() => setShowSponsorModal(true)}
                                className="w-full h-full rounded-xl border border-dashed border-white/10 bg-white/5 hover:bg-white/10 hover:border-yellow-500/30 transition-all flex flex-col items-center justify-center group backdrop-blur-sm"
                              >
                                <Plus className="w-3 h-3 md:w-4 md:h-4 text-white/30 group-hover:text-yellow-400" />
                                <span className="text-[8px] text-white/20 group-hover:text-yellow-400/80 hidden md:block">
                                  #{slot.id}
                                </span>
                              </button>
                            ) : (
                              <button
                                onClick={() => slot.sponsor && handleSponsorClick(slot.sponsor)}
                                className="w-full h-full rounded-xl border border-white/10 bg-white/5 overflow-hidden backdrop-blur-sm shadow-lg hover:border-primary/50 hover:scale-105 transition-all cursor-pointer"
                              >
                                {slot.sponsor?.logo ? (
                                  <Image
                                    src={slot.sponsor.logo}
                                    alt={slot.sponsor.name || "Sponsor"}
                                    width={48}
                                    height={48}
                                    className="w-full h-full object-cover"
                                  />
                                ) : (
                                  <div className="w-full h-full flex items-center justify-center bg-white/10 text-[10px] font-bold text-white/60">
                                    {slot.sponsor?.name?.substring(0, 2) || "SP"}
                                  </div>
                                )}
                              </button>
                            )}
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* Module 5: Tagged Posts (moved to left column) */}
                <Card className="bg-white/5 backdrop-blur-lg border-white/10 rounded-2xl shadow-xl">
                  <CardHeader className="pb-2">
                    <CardTitle className="text-lg font-bold text-white flex items-center gap-2">
                      <Users className="w-5 h-5 text-primary" />
                      Tagged Posts
                    </CardTitle>
                    <p className="text-xs text-zinc-500">Posts featuring this vehicle</p>
                  </CardHeader>
                  <CardContent>
                    {taggedPosts.length > 0 ? (
                      <div className="space-y-3 max-h-[300px] overflow-y-auto pr-1 sleek-scrollbar">
                        {taggedPosts.map((post) => (
                          <div key={post.id} className="p-3 bg-white/5 backdrop-blur-sm rounded-xl border border-white/5 hover:bg-white/10 transition-all">
                            <div className="flex items-center gap-2 mb-2">
                              <div className="w-7 h-7 rounded-full bg-zinc-700 overflow-hidden relative">
                                <Image
                                  src={post.author.avatar}
                                  alt={post.author.username}
                                  fill
                                  className="object-cover"
                                />
                              </div>
                              <div>
                                <p className="text-sm font-medium text-white">@{post.author.username}</p>
                                <p className="text-[10px] text-zinc-500">{post.timestamp}</p>
                              </div>
                            </div>
                            <p className="text-sm text-zinc-300 mb-2">{post.content}</p>
                            {post.image && (
                              <div className="relative aspect-video rounded overflow-hidden mb-2">
                                <Image src={post.image} alt="Post" fill className="object-cover" />
                              </div>
                            )}
                            <p className="text-xs text-zinc-500">â¤ï¸ {post.likes}</p>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <div className="text-center py-6">
                        <Users className="w-8 h-8 text-zinc-600 mx-auto mb-2" />
                        <p className="text-sm text-zinc-400">No posts tagged yet</p>
                      </div>
                    )}
                  </CardContent>
                </Card>
              </div>

              {/* Right Column */}
              <div className="space-y-6">
                {/* Module 4a: Upgrade Prompt (for non-upgraded vehicles) */}
                {!vehicle.isUpgraded && isOwner && (
                  <Card className="bg-gradient-to-br from-primary/10 to-yellow-500/5 backdrop-blur-lg border border-primary/20 rounded-2xl shadow-xl overflow-hidden">
                    <CardContent className="p-6 text-center relative">
                      <div className="absolute inset-0 bg-gradient-to-br from-primary/5 to-transparent pointer-events-none" />
                      <div className="relative z-10">
                        <div className="w-14 h-14 rounded-2xl bg-primary/20 backdrop-blur-sm flex items-center justify-center mx-auto mb-4 border border-primary/30 shadow-lg shadow-primary/20">
                          <TrendingUp className="w-7 h-7 text-primary" />
                        </div>
                        <h3 className="text-lg font-bold text-white mb-2">Upgrade Your Vehicle</h3>
                        <p className="text-sm text-zinc-400 mb-5">
                          Launch a token for your car and unlock sponsorship opportunities, 
                          fractional ownership trading, and community engagement.
                        </p>
                        <Button className="bg-primary/90 hover:bg-primary text-black font-bold w-full rounded-xl shadow-lg shadow-primary/30 transition-all hover:shadow-primary/50">
                          Start Upgrade ($150)
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* Module 4b: Valuation Card (AV, MV, Spread) */}
                {vehicle.valuation && (
                  <Card className="bg-white/5 backdrop-blur-lg border-white/10 rounded-2xl shadow-xl">
                    <CardHeader className="pb-2">
                      <CardTitle className="text-lg font-bold text-white flex items-center gap-2">
                        <Scale className="w-5 h-5 text-primary" />
                        Valuation
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        {/* Market Value */}
                        <div className="flex justify-between items-center p-3 bg-white/5 backdrop-blur-sm rounded-xl border border-white/5">
                          <div>
                            <p className="text-xs text-primary font-medium mb-0.5">Market Value (MV)</p>
                            <p className="text-xs text-zinc-500">Current trading price</p>
                          </div>
                          <p className="text-xl font-bold text-white font-mono">
                            {formatCurrency(vehicle.valuation.marketValue)}
                          </p>
                        </div>

                        {/* Appraised Value */}
                        <div className="flex justify-between items-center p-3 bg-white/5 backdrop-blur-sm rounded-xl border border-white/5">
                          <div>
                            <p className="text-xs text-zinc-400 font-medium mb-0.5">Appraised Value (AV)</p>
                            <p className="text-xs text-zinc-500">Certified assessment</p>
                          </div>
                          <p className="text-lg font-bold text-zinc-300 font-mono">
                            {formatCurrency(vehicle.valuation.appraisedValue)}
                          </p>
                        </div>

                        {/* Spread */}
                        <div className={cn(
                          "flex justify-between items-center p-3 rounded-xl border",
                          vehicle.valuation.spread < 0 
                            ? "bg-green-500/10 border-green-500/20" 
                            : vehicle.valuation.spread > 0 
                              ? "bg-red-500/10 border-red-500/20"
                              : "bg-white/5 border-white/5"
                        )}>
                          <div>
                            <p className="text-xs text-zinc-400 font-medium mb-0.5">Spread</p>
                            <p className="text-xs text-zinc-500">
                              {vehicle.valuation.spread < 0 ? "Below appraisal" : vehicle.valuation.spread > 0 ? "Above appraisal" : "At appraisal"}
                            </p>
                          </div>
                          <div className="text-right">
                            <p className={cn(
                              "text-lg font-bold font-mono",
                              vehicle.valuation.spread < 0 ? "text-green-400" : vehicle.valuation.spread > 0 ? "text-red-400" : "text-zinc-400"
                            )}>
                              {vehicle.valuation.spread >= 0 ? '+' : ''}{formatCurrency(vehicle.valuation.spread)}
                            </p>
                            <p className={cn(
                              "text-xs font-mono",
                              vehicle.valuation.spread < 0 ? "text-green-400/70" : vehicle.valuation.spread > 0 ? "text-red-400/70" : "text-zinc-500"
                            )}>
                              {vehicle.valuation.spread >= 0 ? '+' : ''}{vehicle.valuation.spreadPercent.toFixed(1)}%
                            </p>
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* Module 4c: Token Details (for upgraded vehicles) */}
                {vehicle.carToken && (
                  <Card className="bg-white/5 backdrop-blur-lg border-white/10 rounded-2xl shadow-xl">
                    <CardHeader className="pb-2">
                      <CardTitle className="text-lg font-bold text-white flex items-center gap-2">
                        <BarChart3 className="w-5 h-5 text-primary" />
                        ${vehicle.carToken.ticker} Token
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="grid grid-cols-2 gap-3 mb-4">
                        <div className="p-3 bg-white/5 backdrop-blur-sm rounded-xl border border-white/5">
                          <p className="text-xs text-zinc-500 mb-1">Price</p>
                          <p className="text-lg font-bold text-white font-mono">
                            ${vehicle.carToken.price.toFixed(6)}
                          </p>
                        </div>
                        <div className="p-3 bg-white/5 backdrop-blur-sm rounded-xl border border-white/5">
                          <p className="text-xs text-zinc-500 mb-1">24h Change</p>
                          <div className={cn(
                            "text-lg font-bold flex items-center gap-1",
                            vehicle.carToken.change24h >= 0 ? "text-green-400" : "text-red-400"
                          )}>
                            {vehicle.carToken.change24h >= 0 ? (
                              <TrendingUp className="w-4 h-4" />
                            ) : (
                              <TrendingDown className="w-4 h-4" />
                            )}
                            {Math.abs(vehicle.carToken.change24h).toFixed(2)}%
                          </div>
                        </div>
                        <div className="p-3 bg-white/5 backdrop-blur-sm rounded-xl border border-white/5 col-span-2">
                          <p className="text-xs text-zinc-500 mb-1">Market Cap</p>
                          <p className="text-lg font-bold text-white">
                            ${vehicle.carToken.mcap.toLocaleString()}
                          </p>
                        </div>
                      </div>

                      {/* Chart Placeholder - Sleek */}
                      <div className="p-4 bg-white/5 backdrop-blur-sm rounded-xl border border-dashed border-white/5 text-center mb-4">
                        <BarChart3 className="w-6 h-6 text-white/20 mx-auto mb-1" />
                        <p className="text-xs text-white/30">Chart coming soon</p>
                      </div>

                      <div className="flex gap-2">
                        <Button className="flex-1 bg-primary/90 hover:bg-primary text-black font-bold text-sm rounded-xl shadow-lg shadow-primary/20 transition-all">
                          Swap ${vehicle.carToken.ticker}
                        </Button>
                        <Button variant="outline" size="icon" className="border-white/10 hover:bg-white/10 rounded-xl transition-all">
                          <ExternalLink className="w-4 h-4" />
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                )}
              </div>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Nested Sponsor Modal */}
      <BuySponsorshipModal
        isOpen={showSponsorModal}
        onClose={() => setShowSponsorModal(false)}
        vehicleId={vehicle._id}
        vehicleName={vehicle.nickname || vehicle.model}
        vehicleImage={nftImage || ""}
        mintPrice={vehicle.sponsorshipCollection?.mintPrice || 50}
        availableSlots={availableSlots}
        onPurchaseSuccess={(result) => {
          console.log("Sponsorship purchased:", result);
          setShowSponsorModal(false);
        }}
      />

      {/* Sponsor Details Modal */}
      <SponsorDetailsModal
        isOpen={showSponsorDetails}
        onClose={() => {
          setShowSponsorDetails(false);
          setSelectedSponsor(null);
        }}
        sponsor={selectedSponsor}
        vehicleName={vehicle.nickname || `${vehicle.year} ${vehicle.model}`}
      />
    </>
  );
}

Frontend/app/components/modals/SponsorDe.content:
"use client";

import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogTitle, DialogDescription } from "@/app/components/ui/dialog";
import { VisuallyHidden } from "@radix-ui/react-visually-hidden";
import { Button } from "@/app/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/app/components/ui/avatar";
import { 
  X, 
  Globe, 
  Tag,
  ExternalLink,
  ChevronLeft,
  ChevronRight,
  Settings,
  Share2
} from "lucide-react";
import Image from "next/image";
import { cn } from "@/lib/utils";
import { ManageSponsorshipModal } from "./ManageSponsorshipModal";
import { useAccount } from "wagmi";
import { type Sponsor, type SponsorSocialLinks } from "@/app/data/vehicleData";

interface SponsorDetailsModalProps {
  isOpen: boolean;
  onClose: () => void;
  sponsor: Sponsor | null;
  vehicleName?: string;
}

export function SponsorDetailsModal({ 
  isOpen, 
  onClose, 
  sponsor,
  vehicleName 
}: SponsorDetailsModalProps) {
  const { address } = useAccount();
  const [currentGalleryIndex, setCurrentGalleryIndex] = useState(0);
  const [showManageModal, setShowManageModal] = useState(false);

  // Reset gallery index when sponsor changes
  useEffect(() => {
    if (isOpen) {
      setCurrentGalleryIndex(0);
      setShowManageModal(false);
    }
  }, [sponsor?.tokenId, isOpen]);

  if (!sponsor) return null;

  const isHolder = address?.toLowerCase() === sponsor.holderAddress?.toLowerCase();

  const nextImage = () => {
    if (sponsor.gallery && sponsor.gallery.length > 1) {
      setCurrentGalleryIndex((prev) => 
        prev === sponsor.gallery!.length - 1 ? 0 : prev + 1
      );
    }
  };

  const prevImage = () => {
    if (sponsor.gallery && sponsor.gallery.length > 1) {
      setCurrentGalleryIndex((prev) => 
        prev === 0 ? sponsor.gallery!.length - 1 : prev - 1
      );
    }
  };

  const socialIcons: { key: keyof SponsorSocialLinks; label: string; icon: string }[] = [
    { key: "farcaster", label: "Farcaster", icon: "ðŸŸ£" },
    { key: "base", label: "Base", icon: "/Cars/base-logo.png" },
    { key: "x", label: "X", icon: "/Cars/XIcon.png" },
    { key: "instagram", label: "Instagram", icon: "instagram" },
    { key: "facebook", label: "Facebook", icon: "facebook" },
    { key: "youtube", label: "YouTube", icon: "youtube" },
    { key: "tiktok", label: "TikTok", icon: "tiktok" },
    { key: "linkedin", label: "LinkedIn", icon: "linkedin" },
  ];

  const handleShare = async () => {
    const shareText = `Check out ${sponsor.name} - a sponsor on DRVN VHCLS!`;
    const shareUrl = window.location.href;

    if (navigator.share) {
      try {
        await navigator.share({
          title: sponsor.name || "Sponsor",
          text: shareText,
          url: shareUrl,
        });
      } catch (err) {
        console.log("Share cancelled or failed", err);
      }
    } else {
      await navigator.clipboard.writeText(`${shareText} ${shareUrl}`);
      alert("Link copied to clipboard!");
    }
  };

  const handleBrandingUpdate = (branding: {
    name?: string;
    logo?: string;
    bio?: string;
    website?: string;
    twitter?: string;
    instagram?: string;
    photos?: string[];
  }) => {
    console.log("Branding updated:", branding);
  };

  return (
    <>
      <Dialog open={isOpen} onOpenChange={(open) => !open && onClose()}>
        <DialogContent 
          className="max-w-2xl w-[95vw] max-h-[90vh] p-0 bg-white/[0.03] backdrop-blur-2xl border border-white/10 overflow-hidden shadow-2xl shadow-black/50"
          aria-describedby="sponsor-detail-description"
          hideCloseButton
        >
          <VisuallyHidden>
            <DialogTitle>{sponsor.name} - Sponsor Details</DialogTitle>
            <DialogDescription id="sponsor-detail-description">
              View details for {sponsor.name}, a sponsor of {vehicleName}
            </DialogDescription>
          </VisuallyHidden>

          {/* Header */}
          <div className="sticky top-0 z-10 flex items-center justify-between p-4 border-b border-white/5 bg-white/5 backdrop-blur-xl">
            <h2 className="text-lg font-bold text-white font-mono">Sponsor Details</h2>
            <div className="flex items-center gap-2">
              <Button 
                variant="ghost" 
                size="icon" 
                onClick={handleShare} 
                className="text-zinc-400 hover:text-white hover:bg-white/10 rounded-full transition-all"
              >
                <Share2 className="w-5 h-5" />
              </Button>
              <Button 
                variant="ghost" 
                size="icon" 
                onClick={onClose} 
                className="text-zinc-400 hover:text-white hover:bg-white/10 rounded-full transition-all"
              >
                <X className="w-5 h-5" />
              </Button>
            </div>
          </div>

          {/* Content */}
          <div className="overflow-y-auto max-h-[calc(90vh-65px)] p-5 md:p-6 sleek-scrollbar space-y-6">

            {/* Sponsor Logo & Name */}
            <div className="flex items-center gap-5">
              <Avatar className="w-24 h-24 md:w-28 md:h-28 border-2 border-white/10 rounded-2xl shadow-xl">
                <AvatarImage src={sponsor.logo} className="object-cover" />
                <AvatarFallback className="bg-white/5 backdrop-blur rounded-2xl text-2xl font-bold text-white/50">
                  {sponsor.name?.substring(0, 2).toUpperCase()}
                </AvatarFallback>
              </Avatar>
              <div className="space-y-2">
                <h3 className="text-2xl font-bold text-white">{sponsor.name}</h3>
                <p className="text-sm text-zinc-500">
                  Sponsorship #{sponsor.tokenId}
                </p>
              </div>
            </div>

            {/* Links Section */}
            {(sponsor.websiteUrl || sponsor.promoUrl) && (
              <div className="flex flex-wrap gap-3">
                {sponsor.websiteUrl && (
                  <a 
                    href={sponsor.websiteUrl} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="flex items-center gap-2 px-4 py-2 bg-white/5 hover:bg-white/10 border border-white/10 rounded-full text-sm text-white transition-all"
                  >
                    <Globe className="w-4 h-4" />
                    Website
                    <ExternalLink className="w-3 h-3 text-zinc-500" />
                  </a>
                )}
                {sponsor.promoUrl && (
                  <a 
                    href={sponsor.promoUrl} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="flex items-center gap-2 px-4 py-2 bg-primary/10 hover:bg-primary/20 border border-primary/30 rounded-full text-sm text-primary transition-all"
                  >
                    <Tag className="w-4 h-4" />
                    Promo Link
                    <ExternalLink className="w-3 h-3 text-primary/70" />
                  </a>
                )}
              </div>
            )}

            {/* Social Accounts */}
            {sponsor.socialLinks && Object.values(sponsor.socialLinks).some(v => v) && (
              <div className="space-y-3">
                <h4 className="text-sm font-medium text-zinc-400 uppercase tracking-wide">Social Accounts</h4>
                <div className="flex flex-wrap gap-2">
                  {socialIcons.map(({ key, label, icon }) => {
                    const url = sponsor.socialLinks?.[key];
                    if (!url) return null;

                    return (
                      <a
                        key={key}
                        href={url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="flex items-center gap-2 px-3 py-2 bg-white/5 hover:bg-white/10 border border-white/10 rounded-lg text-sm text-white transition-all"
                      >
                        {icon.startsWith("/") ? (
                          <Image src={icon} alt={label} width={16} height={16} className="w-4 h-4" />
                        ) : (
                          <span className="w-4 h-4 flex items-center justify-center text-xs">
                            {label.substring(0, 2)}
                          </span>
                        )}
                        {label}
                      </a>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Bio */}
            {sponsor.bio && (
              <div className="space-y-3">
                <h4 className="text-sm font-medium text-zinc-400 uppercase tracking-wide">About</h4>
                <p className="text-white/80 text-sm leading-relaxed bg-white/5 border border-white/5 rounded-xl p-4">
                  {sponsor.bio}
                </p>
              </div>
            )}

            {/* Photo Gallery */}
            {sponsor.gallery && sponsor.gallery.length > 0 && (
              <div className="space-y-3">
                <h4 className="text-sm font-medium text-zinc-400 uppercase tracking-wide">Gallery</h4>
                <div className="relative aspect-video rounded-xl overflow-hidden bg-black/40 border border-white/10 group">
                  <Image
                    src={sponsor.gallery[currentGalleryIndex]}
                    alt={`${sponsor.name} gallery image ${currentGalleryIndex + 1}`}
                    fill
                    className="object-cover"
                  />

                  {/* Navigation */}
                  {sponsor.gallery.length > 1 && (
                    <>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={prevImage}
                        className="absolute left-2 top-1/2 -translate-y-1/2 bg-white/10 backdrop-blur-md hover:bg-white/20 text-white border border-white/20 rounded-full opacity-0 group-hover:opacity-100 transition-all"
                      >
                        <ChevronLeft className="w-5 h-5" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={nextImage}
                        className="absolute right-2 top-1/2 -translate-y-1/2 bg-white/10 backdrop-blur-md hover:bg-white/20 text-white border border-white/20 rounded-full opacity-0 group-hover:opacity-100 transition-all"
                      >
                        <ChevronRight className="w-5 h-5" />
                      </Button>

                      {/* Dot indicators */}
                      <div className="absolute bottom-3 left-1/2 -translate-x-1/2 flex gap-1.5 bg-black/30 backdrop-blur-md px-2 py-1.5 rounded-full">
                        {sponsor.gallery.map((_, idx) => (
                          <button
                            key={idx}
                            onClick={() => setCurrentGalleryIndex(idx)}
                            className={cn(
                              "h-1.5 rounded-full transition-all duration-300",
                              idx === currentGalleryIndex ? "bg-white w-4" : "bg-white/40 hover:bg-white/70 w-1.5"
                            )}
                          />
                        ))}
                      </div>
                    </>
                  )}

                  {/* Image counter */}
                  <div className="absolute top-3 left-3 bg-black/40 backdrop-blur-md px-2 py-1 rounded-full text-xs text-white/90 font-mono border border-white/10">
                    {currentGalleryIndex + 1} / {sponsor.gallery.length}
                  </div>
                </div>
              </div>
            )}

            {/* OpenSea Link */}
            {sponsor.openSeaUrl && (
              <a
                href={sponsor.openSeaUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center justify-center gap-2 w-full py-3 bg-[#2081E2]/10 hover:bg-[#2081E2]/20 border border-[#2081E2]/30 rounded-xl text-[#2081E2] font-medium transition-all"
              >
                <Image src="/opensea.svg" alt="OpenSea" width={20} height={20} className="w-5 h-5" onError={(e) => { e.currentTarget.style.display = 'none' }} />
                View NFT on OpenSea
                <ExternalLink className="w-4 h-4" />
              </a>
            )}

            {/* Manage Sponsorship Button - Only for NFT Holder */}
            {isHolder && (
              <Button
                onClick={() => setShowManageModal(true)}
                className="w-full py-3 bg-primary hover:bg-primary/90 text-black font-bold rounded-xl"
              >
                <Settings className="w-4 h-4 mr-2" />
                Manage Sponsorship
              </Button>
            )}
          </div>
        </DialogContent>
      </Dialog>

      {/* Manage Sponsorship Modal */}
      <ManageSponsorshipModal
        isOpen={showManageModal}
        onClose={() => setShowManageModal(false)}
        sponsorshipId={sponsor.tokenId}
        currentBranding={{
          name: sponsor.name,
          logo: sponsor.logo,
          bio: sponsor.bio,
          website: sponsor.websiteUrl,
          twitter: sponsor.socialLinks?.x,
          instagram: sponsor.socialLinks?.instagram,
          photos: sponsor.gallery,
        }}
        onBrandingUpdate={handleBrandingUpdate}
      />
    </>
  );
}

