          <div className="flex justify-center">
            <Button
              variant="ghost"
              size="sm"
              onClick={handleSwapDirectionToggle}
              className="bg-zinc-800 hover:bg-zinc-700 rounded-full p-2"
            >
              <ArrowDownUp className="w-4 h-4 text-primary" />
            </Button>
          </div>

          <div className="p-4 bg-zinc-900/50 rounded-lg border border-white/10">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-zinc-400">
                {swapDirection === "buy" ? "You receive" : "You receive"}
              </span>
              <span className="text-xs text-zinc-500">Balance: 0.00</span>
            </div>
            <div className="flex items-center gap-3">
              <Input
                type="number"
                placeholder="0.00"
                value={swapDirection === "buy" ? tokenAmount : ethAmount}
                onChange={(e) => swapDirection === "buy" 
                  ? handleTokenChange(e.target.value) 
                  : handleEthChange(e.target.value)
                }
                className="bg-transparent border-none text-2xl font-bold text-white placeholder:text-zinc-600 focus-visible:ring-0"
              />
              <div className="flex items-center gap-2 bg-zinc-800 px-3 py-2 rounded-lg shrink-0">
                {swapDirection === "buy" ? (
                  <>
                    <Coins className="w-5 h-5 text-primary" />
                    <span className="font-medium">${token.ticker}</span>
                  </>
                ) : (
                  <>
                    <div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center text-xs font-bold">E</div>
                    <span className="font-medium">ETH</span>
                  </>
                )}
              </div>
            </div>
          </div>

          <div className="p-3 bg-zinc-900/30 rounded-lg border border-white/5">
            <div className="flex items-center gap-2 text-xs text-zinc-400">
              <Info className="w-3 h-3" />
              <span>Swap will open in Uniswap</span>
            </div>
          </div>

          <Button
            onClick={handleSwap}
            className="w-full bg-primary hover:bg-primary/90 text-black font-bold py-6"
          >
            <ExternalLink className="w-4 h-4 mr-2" />
            Swap on Uniswap
          </Button>

          <p className="text-xs text-center text-zinc-500">
            Trade ${token.ticker} on Base via Uniswap V3
          </p>
        </div>
      </DialogContent>
    </Dialog>
  );
}

Frontend/app/components/profile/Register.content:
"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/app/components/ui/card";
import { Button } from "@/app/components/ui/button";
import { Plus, Car, Zap, ChevronRight } from "lucide-react";
import Image from "next/image";

export interface RegisteredVehicle {
    _id: string;
    nickname?: string;
    make: string;
    model: string;
    year: number;
    vin: string;
    registryId: string;
    images: { url: string; isNftImage: boolean }[];
    isUpgraded: boolean;
    description?: string;
    modifications?: string[];
    carToken?: {
        ticker: string;
        address: string;
    };
    createdAt: string;
}

interface RegisteredVHCLsSectionProps {
    isOwner: boolean;
    registeredVehicles: RegisteredVehicle[];
    onRegisterClick: () => void;
    onVehicleClick: (vehicle: RegisteredVehicle) => void;
}

export function RegisteredVHCLsSection({
    isOwner,
    registeredVehicles,
    onRegisterClick,
    onVehicleClick,
}: RegisteredVHCLsSectionProps) {
    const getNftImage = (vehicle: RegisteredVehicle) => {
        return vehicle.images.find(img => img.isNftImage)?.url || vehicle.images[0]?.url;
    };

    return (
        <Card className="w-full bg-black/40 border-white/10 backdrop-blur-md">
            <CardHeader className="flex flex-row items-center justify-between pb-2">
                <div className="space-y-1">
                    <CardTitle className="text-xl font-bold text-white flex items-center gap-2">
                        <Car className="w-5 h-5 text-primary" />
                        Registered VHCLS
                    </CardTitle>
                    <p className="text-xs text-zinc-400">
                        Your personally registered vehicles
                    </p>
                </div>
                {isOwner && (
                    <Button
                        onClick={onRegisterClick}
                        size="sm"
                        className="bg-primary hover:bg-primary/90 text-black font-bold"
                    >
                        <Plus className="w-4 h-4 mr-1" />
                        Register
                    </Button>
                )}
            </CardHeader>
            <CardContent>
                {registeredVehicles.length > 0 ? (
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {registeredVehicles.map((vehicle) => (
                            <button
                                key={vehicle._id}
                                onClick={() => onVehicleClick(vehicle)}
                                className="text-left w-full group"
                            >
                                <div className="relative rounded-xl overflow-hidden border border-white/10 bg-zinc-900 transition-all hover:border-primary/50">
                                    <div className="relative aspect-square">
                                        {getNftImage(vehicle) ? (
                                            <Image
                                                src={getNftImage(vehicle) || ""}
                                                alt={`${vehicle.year} ${vehicle.make} ${vehicle.model}`}
                                                fill
                                                className="object-cover transition-transform group-hover:scale-105"
                                            />
                                        ) : (
                                            <div className="w-full h-full flex items-center justify-center bg-zinc-800">
                                                <Car className="w-10 h-10 text-zinc-600" />
                                            </div>
                                        )}

                                        {vehicle.isUpgraded && (
                                            <div className="absolute top-2 right-2 bg-primary/90 backdrop-blur-sm px-2 py-1 rounded-full flex items-center gap-1">
                                                <Zap className="w-3 h-3 text-black" />
                                                {vehicle.carToken && (
                                                    <span className="text-[10px] font-bold text-black">
                                                        ${vehicle.carToken.ticker}
                                                    </span>
                                                )}
                                            </div>
                                        )}

                                        <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/90 to-transparent p-3">
                                            <p className="text-[10px] text-zinc-400 font-mono uppercase">
                                                {vehicle.registryId}
                                            </p>
                                            <h3 className="text-sm font-bold text-white truncate">
                                                {vehicle.nickname || vehicle.model}
                                            </h3>
                                            <p className="text-xs text-zinc-400">
                                                {vehicle.year} {vehicle.make}
                                            </p>
                                        </div>
                                    </div>

                                    <div className="absolute inset-0 bg-primary/0 group-hover:bg-primary/5 transition-colors flex items-center justify-center opacity-0 group-hover:opacity-100">
                                        <ChevronRight className="w-8 h-8 text-primary" />
                                    </div>
                                </div>
                            </button>
                        ))}
                    </div>
                ) : (
                    <div className="text-center py-8 px-4 border border-dashed border-white/10 rounded-lg bg-white/5">
                        <div className="flex justify-center mb-4">
                            <div className="p-3 bg-zinc-900 rounded-full">
                                <Car className="w-8 h-8 text-zinc-500" />
                            </div>
                        </div>
                        <h3 className="text-lg font-medium text-white mb-2">
                            No Registered Vehicles
                        </h3>
                        <p className="text-zinc-400 text-sm max-w-md mx-auto mb-4">
                            Register your first vehicle to mint an NFT with your VIN and unlock monetization features.
                        </p>
                        {isOwner && (
                            <Button
                                onClick={onRegisterClick}
                                className="bg-primary hover:bg-primary/90 text-black font-bold"
                            >
                                <Plus className="w-4 h-4 mr-2" />
                                Register Vehicle ($50)
                            </Button>
                        )}
                    </div>
                )}
            </CardContent>
        </Card>
    );
}

Frontend/app/components/modals/SponsorPr.content:
"use client";

import { useState } from "react";
import { X, Upload, Save, Loader2, Trash2, Plus, Link2, Instagram, Youtube, Settings, ExternalLink } from "lucide-react";
import { Button } from "@/app/components/ui/button";
import { Input } from "@/app/components/ui/input";
import { Textarea } from "@/app/components/ui/textarea";
import Image from "next/image";
import { useToast } from "@/app/components/ui/toast-context";

interface SocialLinks {
  base?: string;
  x?: string;
  instagram?: string;
  youtube?: string;
  tiktok?: string;
  linkedin?: string;
  facebook?: string;
}

interface SponsorProfile {
  tokenId: string;
  name: string;
  logo?: string;
  bio?: string;
  website?: string;
  promoLink?: string;
  promoText?: string;
  socialLinks?: SocialLinks;
  galleryImages?: string[];
  holderAddress: string;
}

interface SponsorProfileEditorModalProps {
  isOpen: boolean;
  onClose: () => void;
  sponsor: SponsorProfile;
  vehicleName: string;
  onSave?: (profile: Partial<SponsorProfile>) => void;
}

export function SponsorProfileEditorModal({
  isOpen,
  onClose,
  sponsor,
  vehicleName,
  onSave
}: SponsorProfileEditorModalProps) {
  const [name, setName] = useState(sponsor.name || "");
  const [bio, setBio] = useState(sponsor.bio || "");
  const [website, setWebsite] = useState(sponsor.website || "");
  const [promoLink, setPromoLink] = useState(sponsor.promoLink || "");
  const [promoText, setPromoText] = useState(sponsor.promoText || "");
  const [logo, setLogo] = useState(sponsor.logo || "");
  const [socialLinks, setSocialLinks] = useState<SocialLinks>(sponsor.socialLinks || {});
  const [galleryImages, setGalleryImages] = useState<string[]>(sponsor.galleryImages || []);
  const [isSaving, setIsSaving] = useState(false);
  const { addToast } = useToast();

  if (!isOpen) return null;

  const handleSocialChange = (platform: keyof SocialLinks, value: string) => {
    setSocialLinks(prev => ({ ...prev, [platform]: value }));
  };

  const handleAddGalleryImage = () => {
    const url = prompt("Enter image URL:");
    if (url) {
      setGalleryImages(prev => [...prev, url]);
    }
  };

  const handleRemoveGalleryImage = (index: number) => {
    setGalleryImages(prev => prev.filter((_, i) => i !== index));
  };

  const handleSave = async () => {
    setIsSaving(true);

    await new Promise(resolve => setTimeout(resolve, 1500));

    const updatedProfile: Partial<SponsorProfile> = {
      tokenId: sponsor.tokenId,
      name,
      bio,
      website,
      promoLink,
      promoText,
      logo,
      socialLinks,
      galleryImages
    };

    onSave?.(updatedProfile);

    addToast({
      type: "success",
      title: "Profile Updated",
      message: "Your sponsor profile has been saved!",
    });

    setIsSaving(false);
    onClose();
  };

  const socialPlatforms: { key: keyof SocialLinks; label: string; icon: React.ReactNode; placeholder: string; color: string }[] = [
    { key: "base", label: "Base", icon: <Image src="/Cars/base-logo.png" alt="Base" width={16} height={16} className="w-4 h-4" />, placeholder: "base.org/username", color: "bg-blue-500" },
    { key: "x", label: "X", icon: <span className="text-sm font-bold">ð•</span>, placeholder: "x.com/username", color: "bg-white/10" },
    { key: "instagram", label: "Instagram", icon: <Instagram className="w-4 h-4" />, placeholder: "instagram.com/username", color: "bg-gradient-to-br from-purple-500 to-pink-500" },
    { key: "facebook", label: "Facebook", icon: <span className="text-sm font-bold">f</span>, placeholder: "facebook.com/page", color: "bg-blue-500" },
    { key: "youtube", label: "YouTube", icon: <Youtube className="w-4 h-4" />, placeholder: "youtube.com/@channel", color: "bg-red-500" },
    { key: "tiktok", label: "TikTok", icon: <span className="text-sm">â™ª</span>, placeholder: "tiktok.com/@username", color: "bg-black" },
    { key: "linkedin", label: "LinkedIn", icon: <span className="text-sm font-bold">in</span>, placeholder: "linkedin.com/in/username", color: "bg-blue-600" },
  ];

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      <div className="absolute inset-0 bg-black/80 backdrop-blur-md" onClick={onClose} />

      <div className="relative w-full max-w-2xl mx-4 bg-gradient-to-b from-zinc-900 to-black border border-white/10 rounded-3xl shadow-2xl overflow-hidden max-h-[90vh] flex flex-col">
        <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-yellow-500/5 via-transparent to-transparent pointer-events-none" />

        <div className="relative sticky top-0 bg-gradient-to-b from-zinc-900 to-zinc-900/95 backdrop-blur-sm border-b border-white/10 p-6 flex items-center justify-between z-10">
          <div className="flex items-center gap-4">
            <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-yellow-500 to-orange-500 flex items-center justify-center shadow-lg shadow-yellow-500/20">
              <Settings className="w-6 h-6 text-white" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-white">Manage Sponsorship</h2>
              <p className="text-sm text-zinc-400">Slot #{sponsor.tokenId} on {vehicleName}</p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="p-2 rounded-full bg-white/5 hover:bg-white/10 transition-colors"
          >
            <X className="w-5 h-5 text-zinc-400" />
          </button>
        </div>

        <div className="relative flex-1 overflow-y-auto p-6 space-y-8">
          <div className="flex flex-col sm:flex-row gap-6">
            <div className="flex-shrink-0">
              <p className="text-sm font-medium text-zinc-400 mb-3">Brand Logo</p>
              <div className="relative w-28 h-28 rounded-2xl bg-black/40 border-2 border-dashed border-white/20 overflow-hidden group">
                {logo ? (
                  <>
                    <Image
                      src={logo}
                      alt="Logo"
                      fill
                      className="object-cover"
                    />
                    <button
                      onClick={() => setLogo("")}
                      className="absolute inset-0 bg-black/70 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center"
                    >
                      <Trash2 className="w-6 h-6 text-red-500" />
                    </button>
                  </>
                ) : (
                  <button
                    onClick={() => {
                      const url = prompt("Enter logo URL:");
                      if (url) setLogo(url);
                    }}
                    className="w-full h-full flex flex-col items-center justify-center text-zinc-500 hover:text-yellow-500 transition-colors"
                  >
                    <Upload className="w-7 h-7 mb-2" />
                    <span className="text-xs font-medium">Upload Logo</span>
                  </button>
                )}
              </div>
            </div>

            <div className="flex-1 space-y-4">
              <div>
                <label className="text-sm font-medium text-zinc-400 mb-2 block">Sponsor Name</label>
                <Input
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder="Your brand name"
                  className="h-12 bg-black/40 border-white/10 text-white placeholder:text-zinc-600 focus:border-yellow-500/50"
                />
              </div>

              <div>
                <label className="text-sm font-medium text-zinc-400 mb-2 block">Bio</label>
                <Textarea
                  value={bio}
                  onChange={(e) => setBio(e.target.value)}
                  placeholder="Tell us about your brand..."
                  className="bg-black/40 border-white/10 text-white placeholder:text-zinc-600 min-h-[100px] focus:border-yellow-500/50"
                />
              </div>
            </div>
          </div>

          <div className="grid sm:grid-cols-2 gap-4">
            <div>
              <label className="text-sm font-medium text-zinc-400 mb-2 flex items-center gap-2">
                <Link2 className="w-4 h-4" />
                Website
              </label>
              <Input
                value={website}
                onChange={(e) => setWebsite(e.target.value)}
                placeholder="https://yourwebsite.com"
                className="h-12 bg-black/40 border-white/10 text-white placeholder:text-zinc-600 focus:border-yellow-500/50"
              />
            </div>
            <div>
              <label className="text-sm font-medium text-zinc-400 mb-2 flex items-center gap-2">
                <Link2 className="w-4 h-4" />
                Promo Link
              </label>
              <Input
                value={promoLink}
                onChange={(e) => setPromoLink(e.target.value)}
                placeholder="https://promo.yoursite.com"
                className="h-12 bg-black/40 border-white/10 text-white placeholder:text-zinc-600 focus:border-yellow-500/50"
              />
            </div>
          </div>

          <div>
            <label className="text-sm font-medium text-zinc-400 mb-2 block">Promo Text</label>
            <Textarea
              value={promoText}
              onChange={(e) => setPromoText(e.target.value)}
              placeholder="Describe your promotional offer or call to action..."
              className="bg-black/40 border-white/10 text-white placeholder:text-zinc-600 min-h-[80px] focus:border-yellow-500/50"
            />
            <p className="text-xs text-zinc-500 mt-1">This text will be displayed alongside your promo link</p>
          </div>

          <div>
            <p className="text-sm font-medium text-zinc-400 mb-4">Social Links</p>
            <div className="grid sm:grid-cols-2 gap-3">
              {socialPlatforms.map((platform) => (
                <div key={platform.key} className="flex items-center gap-3">
                  <div className={`w-10 h-10 rounded-xl ${platform.color} flex items-center justify-center text-white flex-shrink-0`}>
                    {platform.icon}
                  </div>
                  <Input
                    value={socialLinks[platform.key] || ""}
                    onChange={(e) => handleSocialChange(platform.key, e.target.value)}
                    placeholder={platform.placeholder}
                    className="flex-1 h-10 bg-black/40 border-white/10 text-white text-sm placeholder:text-zinc-600 focus:border-yellow-500/50"
                  />
                </div>
              ))}
            </div>
          </div>

          <div>
            <div className="flex items-center justify-between mb-4">
              <p className="text-sm font-medium text-zinc-400">Gallery Images</p>
              <Button
                variant="ghost"
                size="sm"
                onClick={handleAddGalleryImage}
                className="text-yellow-500 hover:text-yellow-400 hover:bg-yellow-500/10"
              >
                <Plus className="w-4 h-4 mr-1" />
                Add Image
              </Button>
            </div>

            {galleryImages.length > 0 ? (
              <div className="grid grid-cols-3 gap-3">
                {galleryImages.map((img, index) => (
                  <div
                    key={index}
                    className="relative aspect-square rounded-xl overflow-hidden bg-black/40 border border-white/10 group"
                  >
                    <Image
                      src={img}
                      alt={`Gallery ${index + 1}`}
                      fill
                      className="object-cover"
                    />
                    <button
                      onClick={() => handleRemoveGalleryImage(index)}
                      className="absolute top-2 right-2 p-1.5 bg-black/60 rounded-full opacity-0 group-hover:opacity-100 transition-opacity hover:bg-red-500/80"
                    >
                      <X className="w-3 h-3 text-white" />
                    </button>
                  </div>
                ))}
              </div>
            ) : (
              <div className="border-2 border-dashed border-white/10 rounded-2xl p-10 text-center">
                <p className="text-sm text-zinc-500">No gallery images yet</p>
                <p className="text-xs text-zinc-600 mt-1">Add photos to showcase your brand</p>
              </div>
            )}
          </div>

          <div className="bg-black/40 backdrop-blur-sm rounded-2xl p-5 border border-white/10">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs text-zinc-500 mb-1">OpenSea NFT</p>
                <p className="text-sm text-white font-medium">DRVN Sponsorship #{sponsor.tokenId}</p>
              </div>
              <a
                href={`https://opensea.io/assets/base/collection/drvn-sponsorship/${sponsor.tokenId}`}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center gap-2 px-4 py-2 rounded-xl bg-white/5 hover:bg-white/10 transition-colors text-sm text-yellow-500"
              >
                View on OpenSea
                <ExternalLink className="w-4 h-4" />
              </a>
            </div>
          </div>
        </div>

        <div className="relative sticky bottom-0 bg-gradient-to-t from-black to-black/95 backdrop-blur-sm border-t border-white/10 p-6 flex gap-3">
          <Button
            variant="ghost"
            onClick={onClose}
            className="flex-1 h-12 border border-white/10 hover:bg-white/5"
            disabled={isSaving}
          >
            Cancel
          </Button>
          <Button
            onClick={handleSave}
            className="flex-1 h-12 bg-gradient-to-r from-yellow-500 to-orange-500 hover:from-yellow-400 hover:to-orange-400 text-black font-bold"
            disabled={isSaving}
          >
            {isSaving ? (
              <>
                <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                Saving...
              </>
            ) : (
              <>
                <Save className="w-5 h-5 mr-2" />
                Save Profile
              </>
            )}
          </Button>
        </div>
      </div>
    </div>
  );
}

Frontend/app/components/ui/checkbox.tsx.content:
"use client";

import * as React from "react";
import { Check } from "lucide-react";
import { cn } from "@/lib/utils";

export interface CheckboxProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  checked?: boolean;
  onCheckedChange?: (checked: boolean) => void;
}

const Checkbox = React.forwardRef<HTMLButtonElement, CheckboxProps>(
  ({ className, checked, onCheckedChange, ...props }, ref) => {
    return (
      <button
        type="button"
        role="checkbox"
        aria-checked={checked}
        ref={ref}
        onClick={() => onCheckedChange?.(!checked)}
        className={cn(
          "peer h-5 w-5 shrink-0 rounded border border-white/30 ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50",
          checked && "bg-yellow-500 border-yellow-500",
          className
        )}
        data-state={checked ? "checked" : "unchecked"}
        {...props}
      >
        {checked && (
          <span className="flex items-center justify-center text-black">
            <Check className="h-4 w-4" />
          </span>
        )}
      </button>
    );
  }
);
Checkbox.displayName = "Checkbox";

export { Checkbox };

