                                        <p className="text-white font-bold font-mono">
                                            ${holding.usdValue.toLocaleString()}
                                        </p>
                                        <p className={`text-xs font-mono ${holding.change24h >= 0 ? 'text-[#00daa2]' : 'text-red-400'}`}>
                                            {holding.change24h >= 0 ? '+' : ''}{holding.change24h}%
