    instagram?: string;
    youtube?: string;
    tiktok?: string;
    farcaster?: string;
    base?: string;
}

interface VehicleTitleModuleProps {
    vehicle: {
        nickname?: string;
        make: string;
        model: string;
        year: number;
        nftImage?: string;
        isUpgraded: boolean;
        registryId?: string;
