                                        })}
                                        {isOwner && (
                                            <button
                                                onClick={onEditSocialLinks}
                                                className="w-8 h-8 rounded-full bg-zinc-800/50 hover:bg-zinc-700 flex items-center justify-center text-zinc-400 hover:text-white transition-colors"
                                                title="Edit social links"
                                            >
                                                <Edit2 className="w-3 h-3" />
                                            </button>
                                        )}
                                    </>
                                ) : isOwner ? (
                                    <button
                                        onClick={onEditSocialLinks}
                                        className="text-xs text-zinc-500 hover:text-primary flex items-center gap-1 transition-colors"
                                    >
                                        <Edit2 className="w-3 h-3" />
                                        Add social links
                                    </button>
                                ) : null}
                            </div>
                        </div>
                    </div>

                    {/* Right Side: Token Details (only if upgraded) */}
                    {vehicle.isUpgraded && vehicle.carToken && (
                        <div className="md:w-64 lg:w-72 space-y-3 pt-3 md:pt-0 border-t md:border-t-0 md:border-l border-white/10 md:pl-6">
                            {/* Token Stats */}
                            <div className="grid grid-cols-3 gap-2">
                                <div className="p-2 bg-zinc-900/50 rounded-lg border border-white/5 text-center">
                                    <div className="text-[10px] text-zinc-500 uppercase">Price</div>
                                    <div className="text-sm font-bold text-white">
                                        ${vehicle.carToken.price?.toFixed(4) || "0.00"}
                                    </div>
                                </div>
                                <div className="p-2 bg-zinc-900/50 rounded-lg border border-white/5 text-center">
                                    <div className="text-[10px] text-zinc-500 uppercase">24h</div>
                                    <div className={`text-sm font-bold flex items-center justify-center gap-0.5 ${isPositive ? "text-green-500" : "text-red-500"}`}>
                                        {isPositive ? <TrendingUp className="w-3 h-3" /> : <TrendingDown className="w-3 h-3" />}
                                        {Math.abs(vehicle.carToken.change24h || 0).toFixed(1)}%
                                    </div>
                                </div>
                                <div className="p-2 bg-zinc-900/50 rounded-lg border border-white/5 text-center">
                                    <div className="text-[10px] text-zinc-500 uppercase">MCap</div>
                                    <div className="text-sm font-bold text-white">
                                        ${(vehicle.carToken.mcap || 0) >= 1000000 
                                            ? `${((vehicle.carToken.mcap || 0) / 1000000).toFixed(1)}M`
                                            : (vehicle.carToken.mcap || 0) >= 1000
                                                ? `${((vehicle.carToken.mcap || 0) / 1000).toFixed(1)}K`
                                                : (vehicle.carToken.mcap || 0).toLocaleString()
                                        }
                                    </div>
                                </div>
                            </div>

                            {/* Action Buttons */}
                            <div className="flex gap-2">
                                <Button 
                                    onClick={onSwapClick}
                                    size="sm"
                                    className="flex-1 bg-primary hover:bg-primary/90 text-black font-bold"
                                >
                                    <Coins className="w-3 h-3 mr-1" />
                                    Swap
                                </Button>
                                <Button 
                                    variant="outline" 
                                    size="sm"
                                    onClick={handleDexScreener}
                                    className="border-white/10 hover:bg-white/5"
                                >
                                    <ExternalLink className="w-3 h-3" />
                                </Button>
                            </div>
                        </div>
                    )}

                    {/* Right Side: Upgrade prompt (if not upgraded and is owner) */}
                    {!vehicle.isUpgraded && isOwner && (
                        <div className="md:w-64 lg:w-72 pt-3 md:pt-0 border-t md:border-t-0 md:border-l border-white/10 md:pl-6">
                            <div className="p-4 bg-gradient-to-br from-yellow-500/10 to-orange-500/10 rounded-xl border border-yellow-500/20">
                                <div className="flex items-center gap-2 mb-2">
                                    <Zap className="w-5 h-5 text-yellow-500" />
                                    <span className="font-bold text-white">Monetize Vehicle</span>
                                </div>
                                <p className="text-xs text-zinc-400 mb-3">
                                    Launch a token for your vehicle and unlock sponsorship features.
                                </p>
                                <Button
                                    size="sm"
                                    className="w-full bg-gradient-to-r from-yellow-500 to-orange-500 hover:from-yellow-400 hover:to-orange-400 text-black font-bold"
                                >
                                    <Zap className="w-3 h-3 mr-1" />
                                    Upgrade ($5)
                                </Button>
                            </div>
                        </div>
                    )}
                </div>
            </CardContent>
        </Card>
    );
}

Frontend/app/components/garage/GarageHer.content:
"use client";

import React, { useState, useCallback, useEffect } from "react";
import { ChevronLeft, ChevronRight, Settings } from "lucide-react";

interface GarageBackground {
  id: string;
  src: string;
  name: string;
  focusY?: number;
  isNft?: boolean;
  nftContract?: string;
  nftTokenId?: string;
}

interface CarOverlay {
  id: string;
  src: string;
  name: string;
  collection: string;
  ticker?: string;
  location: string;
  sharesOwned: number;
  totalShares: number;
  usdValue: number;
  change24h: number;
  offsetX?: number;
}

interface GarageHeroProps {
  backgrounds: GarageBackground[];
  cars: CarOverlay[];
  activeBackgroundIndex?: number;
  activeCarIndex?: number;
  onBackgroundChange?: (index: number) => void;
  onCarChange?: (index: number) => void;
  onCustomize?: () => void;
  isOwner?: boolean;
}

export function GarageHero({
  backgrounds,
  cars,
  activeBackgroundIndex = 0,
  activeCarIndex = 0,
  onBackgroundChange,
  onCarChange,
  onCustomize,
  isOwner = false,
}: GarageHeroProps) {
  const [localCarIndex, setLocalCarIndex] = useState(activeCarIndex);
  const [localBgIndex, _setLocalBgIndex] = useState(activeBackgroundIndex);
  void _setLocalBgIndex;
  const [touchStartX, setTouchStartX] = useState<number | null>(null);

  // Two-phase animation state
  const [displayedCarIndex, setDisplayedCarIndex] = useState(activeCarIndex);
  const [animationPhase, setAnimationPhase] = useState<'idle' | 'exit' | 'enter'>('idle');
  const [slideDirection, setSlideDirection] = useState<'left' | 'right'>('left');
  const [pendingCarIndex, setPendingCarIndex] = useState<number | null>(null);

  const currentCarIdx = onCarChange ? activeCarIndex : localCarIndex;
  const currentBgIdx = onBackgroundChange ? activeBackgroundIndex : localBgIndex;

  // Use displayedCarIndex for rendering during animations
  const activeCar = cars.length > 0 ? cars[displayedCarIndex % cars.length] : null;
  const activeBackground = backgrounds.length > 0 
    ? backgrounds[currentBgIdx % backgrounds.length] 
    : null;

  // Handle animation phases
  useEffect(() => {
    if (animationPhase === 'exit' && pendingCarIndex !== null) {
      // After exit animation completes, switch to new car and start enter animation
      const exitTimer = setTimeout(() => {
        setDisplayedCarIndex(pendingCarIndex);
        setAnimationPhase('enter');
      }, 300); // Exit animation duration
      return () => clearTimeout(exitTimer);
    }

    if (animationPhase === 'enter') {
      // After enter animation completes, reset to idle
      const enterTimer = setTimeout(() => {
        setAnimationPhase('idle');
        setPendingCarIndex(null);
      }, 400); // Enter animation duration
      return () => clearTimeout(enterTimer);
    }
  }, [animationPhase, pendingCarIndex]);

  // Sync displayedCarIndex with external changes when not animating
  useEffect(() => {
    if (animationPhase === 'idle') {
      setDisplayedCarIndex(currentCarIdx);
    }
  }, [currentCarIdx, animationPhase]);

  const showNextCar = useCallback(() => {
    if (animationPhase !== 'idle') return; // Prevent overlapping animations
    const newIndex = (displayedCarIndex + 1) % cars.length;
    setSlideDirection('left'); // Car exits left, new car enters from right
    setPendingCarIndex(newIndex);
    setAnimationPhase('exit');

    // Also update the external/local state
    if (onCarChange) {
      onCarChange(newIndex);
    } else {
      setLocalCarIndex(newIndex);
    }
  }, [displayedCarIndex, cars.length, onCarChange, animationPhase]);

  const showPreviousCar = useCallback(() => {
    if (animationPhase !== 'idle') return; // Prevent overlapping animations
    const newIndex = (displayedCarIndex - 1 + cars.length) % cars.length;
    setSlideDirection('right'); // Car exits right, new car enters from left
    setPendingCarIndex(newIndex);
    setAnimationPhase('exit');

    // Also update the external/local state
    if (onCarChange) {
      onCarChange(newIndex);
    } else {
      setLocalCarIndex(newIndex);
    }
  }, [displayedCarIndex, cars.length, onCarChange, animationPhase]);

  const handleSwipeStart = (x: number) => {
    setTouchStartX(x);
  };

  const handleSwipeEnd = (endX: number) => {
    if (touchStartX === null) return;
    const distance = endX - touchStartX;
    if (distance > 50) {
      showPreviousCar();
    } else if (distance < -50) {
      showNextCar();
    }
    setTouchStartX(null);
  };

  const formatUsd = (value: number) =>
    value.toLocaleString("en-US", {
      style: "currency",
      currency: "USD",
      maximumFractionDigits: 0,
    });

  if (!activeCar || !activeBackground) {
    return (
      <div className="bg-gradient-to-br from-gray-900 via-black to-gray-900 border border-white/5 rounded-3xl p-6">
        <div className="aspect-[16/9] rounded-2xl bg-black/40 flex items-center justify-center">
          <p className="text-white/50">No vehicles in your garage yet</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-gradient-to-br from-gray-900 via-black to-gray-900 border border-white/5 rounded-3xl p-3 md:p-6 w-full max-w-full overflow-hidden box-border">
      {/* Main Hero Container */}
      <div
        className="hero-container relative w-full aspect-[3/2] rounded-2xl overflow-hidden"
        onTouchStart={(e) => handleSwipeStart(e.touches[0].clientX)}
        onTouchEnd={(e) => handleSwipeEnd(e.changedTouches[0].clientX)}
      >
        {/* Layer 1: Background with rounded corners and clipping */}
        <div className="absolute inset-0 z-[1] rounded-2xl overflow-hidden bg-gray-950">
          <img
            src={activeBackground.src}
            alt={activeBackground.name}
            className="w-full h-full object-cover"
            style={{
              objectPosition: "center 85%",
            }}
          />
        </div>

        {/* Layer 2: Glow Effect Behind Car */}
        <div className="absolute inset-0 z-[2] flex items-end justify-center pointer-events-none overflow-hidden rounded-2xl">
          <div 
            className="absolute bottom-0 w-[80%] h-[50%]"
            style={{
              background: "radial-gradient(ellipse at center bottom, rgba(0, 255, 255, 0.5) 0%, rgba(59, 130, 246, 0.3) 30%, transparent 70%)",
              filter: "blur(40px)",
              transform: "translateY(10%)",
            }}
          />
        </div>

        {/* Layer 3: Gradient Overlay for Text Readability */}
        <div className="absolute inset-0 z-[3] rounded-2xl bg-gradient-to-t from-black via-black/40 to-transparent pointer-events-none" />

        {/* Layer 4: Content Overlay - Top Info Only */}
        <div className="absolute inset-0 z-[4] p-4 md:p-6 flex flex-col justify-start pointer-events-none">
          <div className="space-y-1">
            <p className="text-sm uppercase tracking-wide text-white/70 font-mono">
              {activeCar.collection}
            </p>
            <div className="flex items-center gap-3 flex-wrap">
              <h2 className="text-2xl md:text-4xl font-bold text-white font-mono">
                {activeCar.name}
              </h2>
              {activeCar.ticker && (
                <span className="text-sm font-bold text-primary bg-primary/10 px-3 py-1 rounded-full border border-primary/20">
                  ${activeCar.ticker}
                </span>
              )}
            </div>
            <p className="text-white/70 text-sm font-sans">
              {activeCar.location}
            </p>
          </div>
        </div>

        {/* Navigation Arrows - Left & Right sides */}
        <button
          onClick={showPreviousCar}
          className="absolute left-4 top-1/2 -translate-y-1/2 z-[6] p-3 bg-black/30 hover:bg-black/50 border border-[#00daa2]/50 hover:border-[#00daa2] rounded-lg transition-all group"
        >
          <ChevronLeft className="w-6 h-6 text-[#00daa2] group-hover:scale-110 transition-transform" />
        </button>
        <button
          onClick={showNextCar}
          className="absolute right-4 top-1/2 -translate-y-1/2 z-[6] p-3 bg-black/30 hover:bg-black/50 border border-[#00daa2]/50 hover:border-[#00daa2] rounded-lg transition-all group"
        >
          <ChevronRight className="w-6 h-6 text-[#00daa2] group-hover:scale-110 transition-transform" />
        </button>

        {/* Customize Button (Owner only) */}
        {isOwner && onCustomize && (
          <button
            onClick={onCustomize}
            className="absolute top-4 right-4 z-[6] p-2 bg-black/50 hover:bg-black/70 rounded-lg border border-white/20 transition-colors"
            title="Customize Garage"
          >
            <Settings className="w-5 h-5 text-white/80" />
          </button>
        )}

        {/* Layer 5: Car Overlay - Positioned in lower portion of hero with cyan glow and two-phase slide animation */}
        <div 
          className="absolute inset-0 z-[5] flex items-end justify-center pointer-events-none overflow-hidden"
          style={{ paddingBottom: '8%' }}
        >
          <img
            key={`${activeCar.id}-${animationPhase}`}
            src={activeCar.src}
            alt={activeCar.name}
            className="max-h-[45%] w-auto max-w-[50%] object-contain pointer-events-auto cursor-pointer hover:scale-105"
            style={{
              filter: "drop-shadow(0 0 35px rgba(0, 255, 255, 1)) drop-shadow(0 0 60px rgba(0, 255, 255, 0.5)) drop-shadow(0 20px 50px rgba(0, 0, 0, 0.8))",
              animation: animationPhase === 'exit'
                ? slideDirection === 'left'
                  ? 'slideOutLeft 0.3s ease-in forwards'
                  : 'slideOutRight 0.3s ease-in forwards'
                : animationPhase === 'enter'
                  ? slideDirection === 'left'
                    ? 'slideFromRight 0.4s ease-out forwards'
                    : 'slideFromLeft 0.4s ease-out forwards'
                  : 'none',
            }}
          />
        </div>

      </div>


      {/* Stats Row - Below Hero */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-4 px-2">
        <div>
          <p className="text-xs uppercase text-white/60 font-mono">
            Shares Owned
          </p>
          <p className="text-lg md:text-xl text-white font-semibold font-mono">
            {activeCar.sharesOwned.toLocaleString()}
          </p>
        </div>
        <div>
          <p className="text-xs uppercase text-white/60 font-mono">
            Ownership
          </p>
          <p className="text-lg md:text-xl text-white font-semibold font-mono">
            {((activeCar.sharesOwned / activeCar.totalShares) * 100).toFixed(2)}%
          </p>
        </div>
        <div>
          <p className="text-xs uppercase text-white/60 font-mono">
            USD Value
          </p>
          <p className="text-lg md:text-xl text-white font-semibold font-mono">
            {formatUsd(activeCar.usdValue)}
          </p>
        </div>
        <div>
          <p className="text-xs uppercase text-white/60 font-mono">
            24h Move
          </p>
          <p
            className={`text-lg md:text-xl font-semibold font-mono ${
              activeCar.change24h >= 0 ? "text-[#00daa2]" : "text-red-400"
            }`}
          >
            {activeCar.change24h >= 0 ? "+" : ""}
            {activeCar.change24h}%
          </p>
        </div>
      </div>

      {/* Progress Dots Only */}
      <div className="flex items-center justify-end mt-4 px-2">
        <div className="flex items-center gap-2">
          {cars.map((car, index) => (
            <button
              key={car.id}
              aria-label={`View ${car.name}`}
              className={`h-2 rounded-full transition-all ${
                index === displayedCarIndex
                  ? "bg-[#00daa2] w-8"
                  : "bg-white/30 w-4 hover:bg-white/50"
              }`}
              onClick={() => {
                if (animationPhase !== 'idle' || index === displayedCarIndex) return;
                // Determine direction based on index difference
                const direction = index > displayedCarIndex ? 'left' : 'right';
                setSlideDirection(direction);
                setPendingCarIndex(index);
                setAnimationPhase('exit');

                if (onCarChange) {
                  onCarChange(index);
                } else {
                  setLocalCarIndex(index);
                }
              }}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

Frontend/app/components/modals/GarageCus.content:
"use client";

import { useState } from "react";
import { X, Check, Image as ImageIcon, Car } from "lucide-react";
import { Button } from "../ui/button";

interface GarageBackground {
  id: string;
  src: string;
  name: string;
  focusY?: number;
  isNft?: boolean;
  nftContract?: string;
  nftTokenId?: string;
}

interface CarOverlay {
  id: string;
  src: string;
  name: string;
}

interface GarageCustomizeModalProps {
  isOpen: boolean;
  onClose: () => void;
  backgrounds: GarageBackground[];
  cars: CarOverlay[];
  selectedBackgroundId: string;
  selectedCarId: string;
  onBackgroundSelect: (id: string) => void;
  onCarSelect: (id: string) => void;
}

export function GarageCustomizeModal({
  isOpen,
  onClose,
  backgrounds,
  cars,
  selectedBackgroundId,
  selectedCarId,
  onBackgroundSelect,
  onCarSelect,
}: GarageCustomizeModalProps) {
  const [activeTab, setActiveTab] = useState<"backgrounds" | "cars">("backgrounds");

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
      {/* Backdrop */}
      <div
        className="absolute inset-0 bg-black/80 backdrop-blur-sm"
        onClick={onClose}
      />

      {/* Modal */}
      <div className="relative w-full max-w-2xl max-h-[80vh] bg-zinc-900 border border-white/10 rounded-2xl overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-white/10">
          <h2 className="text-xl font-bold text-white">Customize Your Garage</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-white/10 rounded-lg transition-colors"
          >
            <X className="w-5 h-5 text-white/70" />
          </button>
        </div>

        {/* Tabs */}
        <div className="flex border-b border-white/10">
          <button
            onClick={() => setActiveTab("backgrounds")}
            className={`flex-1 flex items-center justify-center gap-2 p-4 transition-colors ${
              activeTab === "backgrounds"
                ? "bg-primary/10 text-primary border-b-2 border-primary"
                : "text-white/60 hover:text-white hover:bg-white/5"
            }`}
          >
            <ImageIcon className="w-5 h-5" />
            <span className="font-medium">Backgrounds</span>
          </button>
          <button
            onClick={() => setActiveTab("cars")}
            className={`flex-1 flex items-center justify-center gap-2 p-4 transition-colors ${
              activeTab === "cars"
                ? "bg-primary/10 text-primary border-b-2 border-primary"
                : "text-white/60 hover:text-white hover:bg-white/5"
            }`}
          >
            <Car className="w-5 h-5" />
            <span className="font-medium">Featured Car</span>
          </button>
        </div>

        {/* Content */}
        <div className="p-4 overflow-y-auto max-h-[50vh]">
          {activeTab === "backgrounds" && (
            <div className="space-y-4">
              <p className="text-sm text-white/60">
                Select a garage background. NFT backgrounds will be available soon!
              </p>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {backgrounds.map((bg) => (
                  <button
                    key={bg.id}
                    onClick={() => onBackgroundSelect(bg.id)}
                    className={`relative aspect-square rounded-xl overflow-hidden border-2 transition-all ${
                      selectedBackgroundId === bg.id
                        ? "border-primary ring-2 ring-primary/30"
                        : "border-white/10 hover:border-white/30"
                    }`}
                  >
                    <img
                      src={bg.src}
                      alt={bg.name}
                      className="w-full h-full object-cover"
                    />
                    {selectedBackgroundId === bg.id && (
                      <div className="absolute top-2 right-2 w-6 h-6 bg-primary rounded-full flex items-center justify-center">
                        <Check className="w-4 h-4 text-black" />
                      </div>
                    )}
                    <div className="absolute inset-x-0 bottom-0 bg-gradient-to-t from-black/80 to-transparent p-2">
                      <p className="text-xs text-white font-medium truncate">
                        {bg.name}
                      </p>
                      {bg.isNft && (
                        <span className="text-[10px] text-primary">NFT</span>
                      )}
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}

          {activeTab === "cars" && (
            <div className="space-y-4">
              <p className="text-sm text-white/60">
                Select which car to feature in your garage display.
              </p>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {cars.map((car) => (
                  <button
                    key={car.id}
                    onClick={() => onCarSelect(car.id)}
                    className={`relative aspect-[4/3] rounded-xl overflow-hidden border-2 transition-all bg-zinc-800 ${
                      selectedCarId === car.id
                        ? "border-primary ring-2 ring-primary/30"
                        : "border-white/10 hover:border-white/30"
                    }`}
                  >
                    <img
                      src={car.src}
                      alt={car.name}
                      className="w-full h-full object-contain p-2"
                    />
                    {selectedCarId === car.id && (
                      <div className="absolute top-2 right-2 w-6 h-6 bg-primary rounded-full flex items-center justify-center">
                        <Check className="w-4 h-4 text-black" />
                      </div>
                    )}
                    <div className="absolute inset-x-0 bottom-0 bg-gradient-to-t from-black/80 to-transparent p-2">
                      <p className="text-xs text-white font-medium truncate">
                        {car.name}
                      </p>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex justify-end gap-3 p-4 border-t border-white/10">
          <Button
            variant="outline"
            onClick={onClose}
            className="border-white/20 text-white hover:bg-white/10"
          >
            Cancel
          </Button>
          <Button
            onClick={onClose}
            className="bg-primary hover:bg-primary/90 text-black font-semibold"
          >
            Save Changes
          </Button>
        </div>
      </div>
    </div>
  );
}
